const float pi = acos(-1.);

//https://gist.github.com/companje/29408948f1e8be54dd5733a74ca49bb9
float map_range(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

//https://gist.github.com/ayamflow/c06bc0c8a64f985dd431bd0ac5b557cd
vec2 rotateUV(vec2 uv, float rotation)
{
    float mid = 0.5;
    return vec2(
        cos(rotation) * (uv.x - mid) + sin(rotation) * (uv.y - mid) + mid,
        cos(rotation) * (uv.y - mid) - sin(rotation) * (uv.x - mid) + mid
    );
}

float map(vec2 uv) {
    int iterations = 2;
    vec2 ouv = uv;

    uv.y += iTime/10.;
    uv = abs(mod(uv, 1.)*4. - 2.);

    for(int i = 0; i < iterations; i ++) {
        float fi = float(i);
        float fit = float(iterations);

        uv = abs(uv - (vec2(0.5 + (fi/fit)*0.6 )));

        uv *= 1.16;

        uv = rotateUV(uv, map_range(ouv.x,-1.,1., 0.2, 5.) );
    }

    return sin(length(uv)*10.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 uv_o = uv;

    //Barrel distortion
    vec2 w_uv = uv * dot(uv,uv);
    uv = mix(uv, w_uv, 0.4);

    //Log polar-tiling -> https://www.osar.fr/notes/logspherical/
    vec2 pos = vec2(log(length(uv)), atan(uv.y, uv.x));
    pos *= 6./pi;
    pos = fract(pos) - 0.5;   
    uv = pos;
    uv.x -= iTime/(2.8) + 5700.;

    //RGB offset
    float offset_range = 0.04;
    float offset = map_range(sin(iTime),-1.,1.,0.2,1.) * offset_range;
    float offset_y = cos(iTime) * offset_range * 0.2;

    float cr = map(uv + vec2(offset, 0.));
    float cg = map(uv + vec2(offset*2., offset_y*2.));
    float cb = map(uv + vec2(offset*3., offset_y*3.));
    vec3 color = vec3(cr, cg, cb)*3.;

    //Fade to black towards center to hide aliasing
    float mask = (1. - pow(length(uv_o),0.96) )*3.;
    color -= mask;

    fragColor = vec4(color,1.0);
}

#define linearstep(edge0, edge1, x) clamp((x - (edge0)) / (edge1 - (edge0)), 0.0, 1.0)

float box(in vec2 _st, in vec2 _size, vec2 aa, vec3 color, inout vec3 pixel){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size, _size+aa, _st);
        uv *= smoothstep(_size, _size+aa, vec2(1.0)-_st);
    float res = uv.x*uv.y;
    if (res > 0.) {
        pixel = color;   
    }
    return res;
}

float stationary(vec2 st, vec3 color, inout vec3 pixel) {
    float res = 0.;
    vec2 aa = vec2(0.01);
    // top
    res = max(res, box(st - vec2(0.0, 0.225), vec2(0.35, 0.150), aa, color, pixel));
    // right
    res = max(res, box(st - vec2(0.17, 0.0), vec2(0.2, 0.60), aa, color, pixel));
    // bottom
    res = max(res, box(st - vec2(0.0, -0.225), vec2(0.35, 0.150), aa, color, pixel));
    // left
    res = max(res, box(st - vec2(-0.17, 0.0), vec2(0.2, 0.60), aa, color, pixel));
    if (res >0.) {
        pixel = color;    
    }

    return res;
}

