        if(d<EPS)break;
        t+=d;
    }
	col=n*0.5+0.5;

	fragColor=vec4(col,1.0);
}

/*
The original shader interactively shows raymarching for a single ray.
This shader simultaneously shows raymarching for ALL rays starting from a single point.
Every red point is the endpoint of some ray for some raymarching iteration.
I think, this is one of the first visualizations that shows the individual raymarching steps all at once.

Wavefront mode:
    - Move raymarching source with your mouse.
    - Uncomment original sdf in scene(vec2) function.

Ray mode (original):
    - Pick raymarching source by pressing mouse.
    - Move raymarchign target.
    - R: toggle original ray visualization.
*/

const int AA = 16;
const int iterations = 20;
const int substep = 1;

#define T (iTime * 1.)
const float _precision = 1e-4;
const float maxSteps = float(iterations);
const float maxDistance = 5.;

const int y_press = 0;
const int y_delta = 1;
const int y_toggle = 2;

const int key_R = 0x52;
bool visualizeRay()
{
    return texelFetch(iChannel3, ivec2(key_R, y_toggle), 0).x > .5;
}

vec2 _p; float _d = 1e10;

float sdBox(vec2 p,vec2 s)
{
    return length(max(p=abs(p)-s,0.))+min(max(p.x,p.y),0.);
}

float sdLine(vec2 p,vec2 a,vec2 b)
{
    return length(-clamp(dot(p-=a,b-=a)/dot(b,b),0.,1.)*b+p);
}

float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, float rb )
{
    sca = normalize(sca);
    scb = normalize(scb);
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    float k = (scb.y*p.x > scb.x*p.y) ? dot(p,scb) : length(p);
    return sqrt(max(0., dot(p,p) + ra*ra - 2.0*ra*k)) - rb;
}

float side(float r, float h)
{
    return sqrt(r*r - h*h);
}

float sdArcTo(in vec2 p, in vec2 a, in vec2 b, float r)
{
    vec2 m = a + b;
    float tb = dot(m, b) / length(m);
    float arc = sdArc(p, vec2(m.y, -m.x), vec2(side(r, tb), tb), r, 0.);
    //return min(min(length(p-a), length(p-b)), min(length(p-m), min(arc, length(p)))) -.01;
    return arc;
}

float sdMoon(vec2 p, float d, float ra, float rb )
{
    p.y = abs(p.y);
    float a = (ra*ra - rb*rb + d*d)/(2.0*d);
    float b = sqrt(max(ra*ra-a*a,0.0));
    if( d*(p.x*b-p.y*a) > d*d*max(b-p.y,0.0) )
          return length(p-vec2(a,b));
    return max( (length(p          )-ra),
               -(length(p-vec2(d,0))-rb));
}

float sdSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float scene(vec2 p)
{
    p.x += .5;
    float ball = length(p - vec2(1, 0)) - .3;
    float lens = length(p - vec2(.8, 0)) - .15;
    float wire = abs(p.y) - .05;
    vec2 q = p; q.y = abs(q.y);
    float eye = max(max(ball,-lens),-wire);
    float bx = (pow(.3,2.)-pow(.15,2.)+pow(.2,2.))/(2.*.2);
    float bh = side(.3, bx);
    float ballArc = sdArcTo(q - vec2(1, 0), vec2(-bx,bh), vec2(side(.3,.05), .05), .3);
    float lensArc = sdArcTo(q - vec2(.8, 0), vec2(.2-bx,bh), vec2(side(.15,.05), .05), .15);
    float wireLine = sdLine(q, vec2(.8+side(.15,.05), .05), vec2(1.+side(.3,.05), .05));
    float eye1 = min(min(ballArc, lensArc), wireLine) * sign(eye);
    float socket = min(
        sdBox(vec2(p.x,abs(p.y))-vec2(1,.6),vec2(.3,.05)),
        sdBox(vec2(p.x,abs(p.y))-vec2(1.25,.55),vec2(.05,.05))
    );
    //if (false)
        return min(eye1, socket); // proper sdf
    return min(eye, socket); // original sdf
}

vec2 getNormal(vec2 p)
{    
	vec2 e = vec2(_precision, 0);
    return normalize(vec2(scene(p+e.xy)-scene(p-e.xy),scene(p+e.yx)-scene(p-e.yx)));
}

void visualize(vec2 ro, vec2 rd, float t, float d, float i)
{
    vec2 n = getNormal(ro+rd*t);

    float x  = clamp(floor(T)-i,0.,1.);
    float f1 = x + (1.-x) * fract(T);
    float f2 = clamp((f1-.75)*16.,0.,1.);
    float f3 = floor(abs(cos(min(f1*8.,1.)*6.283))+.5);
    float a  = mix(atan(-n.y,-n.x),atan(rd.y,rd.x),f2);

    // ray line
    _d = min(_d,sdLine(_p,ro+rd*t,ro+rd*t+vec2(cos(a),sin(a))*d*floor(f3)));

    // step indicator
    _d = min(_d,length(_p-ro-rd*t)-.015);

    if (i == floor(T))
    {
        // circle
        _d = min(_d,abs(length(_p-ro-rd*t)-clamp(d*f3,0.,1e4)));
    }
}

vec4 march(vec2 ro, vec2 rd)
{
    float t = 0., s = float(maxSteps), d;

    for(float i = 0.; i < maxSteps; i++)
    {
        d = scene(ro+rd*t);

        if (d < _precision || t > maxDistance || i > floor(T))
        {
            s = float(i);
            break;
        }

		visualize(ro,rd,t,d,i);

        t += d;
    }

    return vec4(ro+rd*t,d,s);
}

void swap(inout vec2 a, inout vec2 b)
{
    vec2 z = a;
    a = b;
    b = z;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zero = min(0., iTime);

    _p = (2.*fragCoord.xy-iResolution.xy)/iResolution.yy;
	vec2 m = (2.*iMouse.xy-iResolution.xy)/iResolution.yy;
    vec2 c = (2.*abs(iMouse.zw)-iResolution.xy)/iResolution.yy;
    bool demo = iMouse.z == 0.;

    if (demo)
    {
        float a = .3 * iTime;
        c = vec2(.5, 0.) + .5 * vec2(cos(a), sin(a));
        m = c;
    }
    else if (visualizeRay())
    {
        //swap(m, c);
    }
    else
    {
        c = m;
    }

    vec2 ro = c;//vec2(-1,0);
    vec2 rd = normalize(m-ro);
    vec4 h  = march(ro,rd);

    // camera & scene
    _d = min(_d,min(min(abs(length(_p-ro)-.01),sdLine(_p,ro,ro+rd*.05)),scene(_p)));

    // normal arrow
    if (h.z < _precision)
    {
        vec2 n = getNormal(h.xy);
        vec2 t = vec2(-n.y,n.x);

        _d = min(_d,sdLine(_p,h.xy,h.xy+n*.1));
        _d = min(_d,sdLine(_p,h.xy+n*.1,h.xy+n*.1+t*.025));
        _d = min(_d,sdLine(_p,h.xy+n*.1,h.xy+n*.1-t*.025));
        _d = min(_d,sdLine(_p,h.xy+n*.1+t*.025,h.xy+n*.125));
        _d = min(_d,sdLine(_p,h.xy+n*.1-t*.025,h.xy+n*.125));
    }

    _d = abs(_d)-1./iResolution.y;

	fragColor = vec4(vec3(smoothstep(_d,_d+.005,.005)),1.0);

    vec4 col = vec4(0);
    for (int i=1 ; i<2*AA ; i+=2)
    for (int j=1 ; j<2*AA ; j+=2)
    {
        vec2 jitter = (vec2(i, j) / float(AA) - .5) / iResolution.yy;
        vec2 ro1 = ro;// + jitter;
        vec2 _p1 = _p + jitter;
        vec2 rd1 = normalize(_p1 - ro1);
        float r = length(_p1 - ro1);
        float t1 = 0.;
        for (int i=0 ; i<iterations*substep ; ++i)
        {
            t1 += scene(ro1 + rd1 * t1) / float(substep);
            //col += vec4(1,0,0,1) * max(0., 1. - abs(t1 - r) * iResolution.y / 1.);
            col += vec4(1,0,0,1) * step(abs(t1 - r) * iResolution.y, 1.);
        }
    }
    col /= float(AA * AA);
    fragColor = col + fragColor * (1. - col.w);
    fragColor.xyz = pow(fragColor.xyz, vec3(.45));
    //fragColor = vec4(visualizeRay());

    //fragColor = vec4(sin(scene(_p) * iResolution.y));
}

#define MAXD 100.
#define SURF 0.01

mat2 r2d(float a) {
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

// All components are in the range [0â¦1], including hue.
vec3 rgb2hsv(vec3 c)
{
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)), d / (q.x + e), q.x);
}


// All components are in the range [0â¦1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
//Taken from http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl.

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdVerticalCapsule( vec3 p, float h, float r )
{
  p.y -= clamp( p.y, 0.0, h );
  return length( p ) - r;
}
float sdCappedTorus(in vec3 p, in vec2 sc, in float ra, in float rb)
{
  p.x = abs(p.x);
  float k = (sc.y*p.x>sc.x*p.y) ? dot(p.xy,sc) : length(p.xy);
  return sqrt( dot(p,p) + ra*ra - 2.0*ra*k ) - rb;
}

vec4 map(vec3 p) {

    //float d = 
    float pd = 0.1;
    float psz = floor(p.z);
    p.xy *= r2d(psz+psz*09.5);
    vec3 o = p;
    p -= 5.;
    p = (fract(p*pd)-0.5)/pd;
    p = abs(p)-1.0;
    //*/
    for (int i=0;i<4;i++) {
        //
        p.xy = vec2(length(p.xy),atan(p.x,p.y));
        p.xy = abs(p.xy)+0.3;
        p.xy *= r2d(0.3);
        p.xy = vec2(p.x*sin(p.y),p.x*cos(p.y));
        p = abs(p)-sin(o.z*0.06+iTime*0.01)*2.;
        p.xy *= r2d(o.z*0.02);
    }
    //float d = length(p)+0.1;

    float an = 3.14*0.75;
    float d = sdCappedTorus(p,vec2(sin(an),cos(an)),1.,0.)+0.0;
    d = max(d,-(length(o.xy)-0.4));
    return vec4(p.xy,o.z,d);
}

vec2 RM(vec3 ro, vec3 rd,float c) {
    float dO = 0.;
    float ii = 0.;
    int steps = 60-int(c*20.);
    for (int i=0;i<steps;i++) {
        vec3 p = ro+rd*dO;
        float dS = map(p).w;
        dO += dS*0.2;
        ii += 0.1;
        if (dO > MAXD || dS < SURF) {break;}
    }
    return vec2(dO,ii);
}
