#define NUM 20.0
#define PI 3.1415926
vec3 color(float t) {
    // from IQ's color pattern
    return .3 + .7 * cos(6.283 * (vec3(1., .5, .0) * (1. - t) + vec3(.7, .75, .3)));
}
float hash11(float p) {
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.y;
    uv.y += .4;
    vec3 col = vec3(0, 0, 0);
    for(float i = 0.;i < NUM; i ++) {
        float id = i;
        float h0 = hash11(id);
        float h1 = hash11(h0);
        float h2 = hash11(h1);
        float h3 = hash11(h2);
        vec2 pos = uv;
        pos.x += cos(iTime * 3. * h0 + h0 * PI * 2.) * .2 * (.5 + h0 * 2.) * (.2 + pow(pos.y, .8));
        pos.y -= fract(iTime * .3 + h1) * .8 * (.5 + h1);
        vec3 c =  color(h3);
        col += 0.012 * h2 /length(pos) * c;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

