#define SAMPLE_SIZE 30

float t(){
    return abs(cos(iTime));
}

float line(vec2 uv, vec2 p1, vec2 p2)
{
    float wid = 0.005;
    vec2 dir0 = p2 -p1;
    vec2 dir1 = uv - p1;
    float h = clamp(dot(dir0, dir1)/dot(dir0, dir0), 0.0, 1.0);
    return (length(dir1 - dir0 * h) - wid * 0.5);
}

float point(vec2 uv, vec2 p, float r){
    vec2 offset = uv - p;
    return sqrt((offset.x * offset.x) + (offset.y * offset.y)) - r;
}


vec2 line_pos(float t, vec2 b0, vec2 b1, vec2 b2){
    vec2 r0 = mix(b0, b1, t);
    vec2 r1 = mix(b1, b2, t);
    return mix(r0, r1, t);
}

vec4 render(float d){
    float stroke = fwidth(d) * 1.0;
    float anti = fwidth(d) * 0.5;
    vec4 strokeLayer = vec4(vec3(0.05), 1.0-smoothstep(-anti, anti, d - stroke));
    vec4 colorLayer = vec4(vec3(0.1), 1.0-smoothstep(-anti, anti, d));

    if (stroke < 0.000001) {
    	return colorLayer;
    }
    return vec4(mix(strokeLayer.rgb, colorLayer.rgb, colorLayer.a), strokeLayer.a);
}


vec4 draw_line(vec2 uv, vec2 p1, vec2 p2)
{
    float d = line(uv,p1,p2);
    return render(d);
}

vec4 draw_point(vec2 uv, vec2 p, float r )
{
    float d = point(uv,p,r);
    return render(d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord.xy/iResolution.xy - vec2(0.5);
    uv.x *= iResolution.x/iResolution.y;

    // Time varying pixel color
    vec3 col = vec3(0.8);

    vec2 apexes[9];
    vec4 layer;

    apexes[0] = vec2(-0.65 + 0.1*sin(iTime),-0.25 - 0.1*sin(iTime));
    apexes[1] = vec2(-0.1,0.);
    apexes[2] = vec2(0.25 + 0.1*cos(iTime),-0.35+ 0.1*cos(iTime));
    apexes[3] = vec2(-0.4,0.25);
    apexes[4] = vec2(0.15,0.35+0.1*t());
    apexes[5] = vec2(0.5,0.2);
    apexes[6] = vec2(-0.2,0.1+0.1*cos(iTime));
    apexes[7] = vec2(0.25,0.15+0.1*t());
    apexes[8] = vec2(0.7 + 0.1*sin(iTime),0. + 0.1*sin(iTime));

#if 1
    //draw line
    for(int i = 1; i < 9; i+=2){
        int p1 = i-3 < 0 ? 0 : i-3;
        int p2 = 3-i < 0 ? i-1 : i+1;
        int p3 = i+3 < 8 ? i+3 : 8;

        layer = draw_line(uv,apexes[p1],apexes[i]);
        col = mix(col, layer.rgb,layer.a);
        layer = draw_line(uv,apexes[i],apexes[p2]);
        col = mix(col, layer.rgb,layer.a);
        layer = draw_line(uv,apexes[i],apexes[p3]);
        col = mix(col, layer.rgb,layer.a);
    }

    // draw point
    for(int i = 0; i < 9; i++){
        layer = draw_point(uv,apexes[i],0.02);
        col = mix(col, layer.rgb,layer.a);
    }
#endif

    // traverse y axis
    float pm = 1./float(SAMPLE_SIZE);
    for(int i = 0; i<=SAMPLE_SIZE; i+=1){
        vec2 c0 = line_pos(pm*float(i), apexes[0], apexes[3], apexes[6]);
        vec2 c1 = line_pos(pm*float(i), apexes[1], apexes[4], apexes[7]);
        vec2 c2 = line_pos(pm*float(i), apexes[2], apexes[5], apexes[8]);

        // traverse x axis
        for(int j = 0; j<=SAMPLE_SIZE; j+=1){
            vec2 p = line_pos(pm*float(j), c0, c1, c2);
            layer = draw_point(uv,p,0.002);
            col = mix(col, layer.rgb,layer.a);
        }
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     vec2 coord = 6.0 * fragCoord.xy / iResolution.xy;
    for(int i = 1;i < 190;i++){
        float n = float(i);
        coord += vec2(0.7 / n * sin(n * coord.y + iTime  + 0.3) + 0.8, 0.4 / n * sin(n * coord.x + iTime + 0.3 ) + 0.6);
    }
    vec3 color = vec3(0.5 * sin(coord.x) + 0.5, 0.5 * sin(coord.y) + 0.5,sin(coord.x+coord.y));
    fragColor = vec4(color,1.0);
}

