#define PI acos(-1.)
#define TAU (2.*PI)

#define time iTime
#define frt(sp,off) fract((time+off)*sp)
#define flt(sp,off) floor((time+off)*sp)

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
#define crep(p,c,l) p-=(c*clamp(round(p/c),-l,l))
#define mo(p,d) p=abs(p)-d;if(p.y>p.x)p=p.yx

struct obj{
  float d;
  vec3 sha;
  vec3 li;
};

obj minobj(obj a, obj b)
{
  if (a.d<b.d) return a; else return b;
}


float box (vec3 p , vec3 c)
{
    vec3 q = abs(p)-c;
    return min(0.,max(q.x,max(q.y,q.z)))+length(max(q,0.));
}

obj base (vec3 p)
{
    vec3 pp = p;
    float per = 5.;
    vec3 id = round(p/per);
    float off = clamp(length(id*0.52),-3.,3.);
    crep(p,per,2.);
    float d = box(p,vec3(per*0.1+off));
    float b = box(p,vec3(0.6));

    p=pp;
    d = max(-d,box(p-vec3(1.9,-1.9,1.8),vec3(11.)));
    d = max(-box(p+vec3(1.,-3.,1.),vec3(8.)),d);

    return obj(d, vec3(0.0,0.01,0.05),vec3(0.,0.1,0.2));
}

obj octas (vec3 p)
{
    p.y -= 2.;
    mo(p.zx,vec2(5.5));
    vec2 id = round(p.xy/3.);
    crep(p.xy,3.,2.);
    float off = length(id),
    anim = (PI/4.)*(flt(2.,off)+pow(frt(2.,off),8.));
    p.xz *= rot(anim);
    float d = dot(p,normalize(sign(p)))-0.8;

    return obj(d,vec3(0.2,0.,0.),vec3(1.,0.5,0.));
}

float g1=0.;
obj pillars (vec3 p)
{
    p -= vec3(1.9,2.,1.8); 
    mo(p.xz,vec2(11.));
    float d = box(p,vec3(.5,15.,.5));
    g1 += 0.1/(0.1+d*d);

    return obj(d,vec3(0.),vec3(0.1));
}

vec2 id;
obj SDF (vec3 p)
{
    p.yz *= rot(-atan(1./sqrt(2.)));
    p.xz *= rot(PI/4.);

    id = round(p.xz/29.);
    crep(p.xz,29.,1.);
    obj scene = base(p);
    scene = minobj(scene, octas(p));
    scene = minobj(scene, pillars(p));

    return scene;
}

vec3 getnorm (vec3 p)
{
    vec2 eps = vec2(0.001,0.);
    return normalize(SDF(p).d-vec3(SDF(p-eps.xyy).d,SDF(p-eps.yxy).d,SDF(p-eps.yyx).d));
}

float AO (float eps, vec3 p, vec3 n)
{return SDF(p+eps*n).d/eps;}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 ouv = fragCoord.xy/iResolution.xy;

    vec3 ro=vec3(uv*20.,-50.), rd=vec3(0.,0.,1.),p=ro,
    col=vec3(0.), l=normalize(vec3(2.,3.,-2.));

    bool hit = false; obj O;
    for (float i=0.; i<64.;i++)
    {
        O = SDF(p);
        if (O.d<0.001)
        {hit=true;break;}
        p += O.d*rd;
    }

    if (hit)
    {
        vec3 n=getnorm(p);
        float light = max(dot(n,l),0.);
        float ao = AO(0.1,p,n)+AO(0.5,p,n)+AO(0.9,p,n);
        col = mix(O.sha, O.li, light)*(ao/3.);
    }

    col += g1*(sin(length(id)-frt(.3,0.)*TAU)+1.);
    fragColor = vec4(sqrt(col),1.);
}

vec2 tile(vec2 st, float scale, float t){
    st *= scale;

    float a;
    float smoothT = fract(t); 
    smoothT = 3.0*pow(smoothT, 2.0) - 2.0*pow(smoothT, 3.0);
    //smoothT *= smoothT*(3.-2.0*smoothT);
    if (mod(t, 2.0) > 1.0) {
        // Move vertical
        a = mod(st.x, 2.0) > 1.0 ? 1.0 : -1.0;
        st.y += a*smoothT;
    } else {
        // Move horizontal
        a = mod(st.y, 2.0) > 1.0 ? 1.0 : -1.0;
        st.x += a*smoothT;
    }

    return fract(st);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    uv = tile(uv, 15.0, iTime);

    // Time varying pixel color
    float d = length(uv - vec2(0.5, 0.5));
    float pix = 22.0/iResolution.y;
    vec3 col = vec3(smoothstep(0.3, 0.3 + pix, d));

    // Output to screen
    fragColor = vec4(col,1.0);
}

float map(in vec2 p) {

    float depth = p.y+1.5;

    p.y += 6.;
    //p.x += (p.y+12.)*0.2*p.x;
    p.xy += vec2(p.y+12.)*0.2*p.xy;

    p.y += iTime*5.;
    float s = sin(p.y+p.x) + sin(p.x-p.y);



    float w = fwidth(s);
    w *= abs(depth)*1.25;

    s = smoothstep(-w, w, s);

    return  s -abs((depth+3.5)*0.025);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 p = (2. * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y)*12.;

    float s = map(p);
    s = max( map(p+vec2(0.,0.125))*0.25, s );


    fragColor = vec4(vec3(s), 1.);
}

//variant of https://shadertoy.com/view/sdd3R4

float  R0 = 20., R1 = 10.,                                    // large and small radii
        k = 2., // .9                                         // tile multiplier
        S = .4;
  #define T 0.
//#define T iTime
#define hue(v)  ( .6 + .6 * cos( v  + vec4(0,23,21,0)  ) )    // hue
#define rot(a)    mat2( cos(a+vec4(0,11,33,0)) )              // rotation                  
#define SQR(x)  ( (x)*(x) )

#define f(x)  DX * sqrt( 1. + SQR( S*6.*R1/R0*cos(6.*x+T) ) )  \
                 / mix( 1.,sin(6.*x+T), S );

float intX( float a ) {                                       // --- antiderivative of large circumference
    a = mod(a,6.2832);   // if you know a close form ( or good approx ), welcome ! :-)
 // return  2.808*a + .32  *(cos(6.*a) -1.); // fitting for S = .4, N=6  https://www.desmos.com/calculator/uepjhnpyap
 // return  1.85 *a + .129 *(cos(6.*a) -1.); //             S = .3 
 // return  1.40 *a + .057 *(cos(6.*a) -1.); //             S = .2 
 // return  1.143*a + .002 *(cos(6.*a) -1.); //             S = .1 
 // return  2.516*a + .45  *(cos(6.*a) -1.); //             S = .4, N=4 
    float x, s = 0., DX = 0.01;  // indeed, approx above better than DX=.01
    for( x = 0.; x < a; x += DX )
        s += f(x);
    return s += ( a - (x-DX) )/DX * f(x) ;                    // smooth integral
}

float a,b,r1,d; vec3 M;

float map(vec3 q) {                                           // --- shape
    q.yz *= rot( .5+6.*M.y),                                  // rotations
    q.xz *= rot( 2.-6.*M.x),
    a = atan(q.z,q.x),
    b = atan(q.y,d),
    r1 = R1* mix( 1., sin(6.*a+T) , S);
    return min( 9., length(vec2(d=length(q.xz)-R0,q.y)) - r1 ); // abs for inside + outside
}

vec3 normal( vec3 p ) { // --- smart gradient  https://www.iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
    float h = 1e-4; 
    vec2 k = vec2(1,-1);
    return normalize( k.xyy* map( p + k.xyy*h ) + 
                      k.yyx* map( p + k.yyx*h ) + 
                      k.yxy* map( p + k.yxy*h ) + 
                      k.xxx* map( p + k.xxx*h ) );
}

void mainImage(out vec4 O, vec2 U) {    // ===================================
    float t=9.;

    vec3  R = iResolution,
          D = normalize(vec3( U+U, -3.5*R.y ) - R ),          // ray direction
          p = 90./R, q;                                       // marching point along ray 
       // M =  iMouse.xyz/R -.5;
          M = iMouse.z > 0. ? iMouse.xyz/R -.5: vec3(8,4,0)/1e2*cos(iTime+vec3(0,11,0));

    for ( O=vec4(0) ; O.x < 1. && t > .01 ; O+=.01 )
        t = map(p), // also set a,b,r1,d
        p += .5*t*D;                                          // step forward = dist to obj          

    O = O.x > 1. ? vec4(0.) : exp(-3.*O/4.);                  // luminance (depth + pseudo-shading )
    if ( U.x < R.x/2. ) {                                     // left: conformal mapping
        a = intX(a); 
        float// s = S*6.*cos(6.*a+T),
             // r = r1/R0, //*sqrt(1.+s*s), 
                r = R1/R0,
               ir = sqrt(1.-r*r);                   // antiderivative of 1/circonf(b) 
        b = .996*  2./ir* atan( (r-1.)/ir* tan(b/2.) );        
     //                                NB: tan(b/2) = ( -d + sqrt(d*d+q.y*q.y) )/q.y )        
    } 

    if (O.x>0.) {
        O = hue( mod(floor(k*R0*a/6.283),floor(k*R0*intX(-1e-5)/6.283)) 
                + 17.*mod(round(k*R1*b/6.283), floor(k*R1)) ); // colored tiles 
        a = sin(k*R0*a/2.), b = cos(k*R1*b/2.);
        O *= sqrt( min(abs(a)/fwidth(a),1.) * min( abs(b)/fwidth(b),1.) );// tiles borders
        O *= .3 + .7*max(0.,dot(normal(p),vec3(.58))); // shading
    }
    if (int(U)==int(R/2.) ) O++;                              // vertical separator
}

