vec3 hash31(float p)
{
    //from David Hoskin's "Hash without sine"
   vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
   p3 += dot(p3, p3.yzx+33.33);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}


vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);




    vec3 col;  
    float t1 = 6.;

    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    vec3 random1 = hash31(floor((iTime)/10.0+uv.x))*10.;
    //vec3 random2 = hash31(1.+floor((iTime)/10.0+uv.x));
    vec3 random3 = (hash31(2.+floor((iTime)/10.0+uv.x))-vec3(.5))/4.;
    vec3 random4 = (hash31(3.+floor((iTime)/10.0+uv.x))-vec3(.5))/4.;

    float offset = .16+random4.z*.04;
    float scale2 = 1.16+.04*random4.y;
    for(int c=0;c<3;c++){
        float scale = c1.z;
        float scale1 = 1.0;
        for(int i=0;i<3;i++)
        {
            uv = triangle_wave(uv+offset,scale)+triangle_wave(uv.yx,scale);
            uv.x *= -1.0;

            uv = triangle_wave(uv+random1.x,scale+random3.z);
            uv.x /= -1.0;

            uv = triangle_wave(uv+random1.y,scale+random3.y);
            uv.x *= -1.0;

            uv = triangle_wave(uv+random1.z,scale+random3.z);
            uv.x /= -1.0;

            //uv.x *= -1.0;
            //uv = triangle_wave(uv+c1.y,scale);
            scale /= scale2+col.x/2.;
            offset *= scale2/(1.+random4.x);

            //uv = uv.yx;
            //uv = uv.yx;
            scale2 += col.x/4.;
            //random1 = random3+hash31(col.x/32.)/2.;
            //random3 = random4+hash31(col.y/32.)/2.;
            //random4 = random1+hash31(col.z/32.)/2.;
            col[c] = fract((uv.y)-(uv.x));

        }

	}

    fragColor = vec4(vec3(col),1.0);

}

const float movement_speed = 4.7;

const vec2 legs_origin = vec2(0.885, 0.06);
const float legs_radius = 0.068;
const float legs_height_clamp = 0.01;

const vec2 pelvis_origin_initial = vec2(0.7105, 0.637);
const float upper_knee_length = 0.32;
const float lower_knee_length = 0.32;

const float torso_height = 0.35;
const float neck_height = 0.13;
const float shoulder_length = 0.22;
const float forearm_length = 0.21;

float circle(in vec2 uv, in vec2 p, in float rad)
{
    float linear_eps = 1.0 / iResolution.x;
    vec2 puv = uv - p;
    return smoothstep(rad + linear_eps, rad - linear_eps, length(puv));
}

float sd_segment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

float line(in vec2 uv, in vec2 p1, in vec2 p2, in float rad)
{
    float linear_eps = 1.0 / iResolution.x;
    return smoothstep(rad + linear_eps, rad - linear_eps, sd_segment(uv, p1, p2));
}

vec2 rotate(in vec2 v, in float theta)
{
    vec2 sincos = vec2(sin(theta), cos(theta));
    mat2x2 rot = mat2x2(
        sincos.y, sincos.x,
        -sincos.x, sincos.y
    );
    return rot * v;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    vec3 col = vec3(0.5, 0.5, 0.5);

    float l1 = lower_knee_length;
    float l2 = upper_knee_length;

    vec2 k2 = vec2(l1*l1 - l2*l2);

    vec2 sincos = vec2(
        sin(iTime * movement_speed), 
        cos(iTime * movement_speed)
    );
    vec2 sincos2 = vec2(
        sin(iTime * movement_speed * 2.0),
        cos(iTime * movement_speed * 2.0)
    );

    vec2 fwd_v = normalize(vec2(-0.47, -1.0)) * (l1 + l2);


    float shoulder_angle_left = -sincos.x / 7.0;
    float forearm_angle_left = shoulder_angle_left + shoulder_angle_left;
    float shoulder_angle_right = sincos.x / 7.0;
    float forearm_angle_right = shoulder_angle_right + shoulder_angle_right;

    vec2 pelvis_origin = pelvis_origin_initial;

    vec2 v_left = legs_origin + sincos.xy * legs_radius;
    vec2 v_right = legs_origin - sincos.xy * legs_radius;

    v_left = mix(v_left, pelvis_origin + fwd_v, (1.0 - sincos.x) / 2.0);
    v_left.y = max(legs_height_clamp, v_left.y);


    v_right = mix(v_right, pelvis_origin + fwd_v, (sincos.x + 1.0) / 2.0);
    v_right.y = max(legs_height_clamp, v_right.y);

    pelvis_origin += 
        vec2(
            sincos2.x * 0.001, 
            sincos2.y * 0.008
        );

    vec2 dir_left = v_left - pelvis_origin;
    vec2 dir_right = v_right - pelvis_origin;

    vec2 k1 = vec2(
        length(dir_left),
        length(dir_right)
    );

    dir_left = normalize(dir_left);
    dir_right = normalize(dir_right);


    if (k1.x > l1 + l2) {
        pelvis_origin = v_left - dir_left * (l1 + l1);
        k1.x = l1 + l1;
        k1.y = length(v_right - pelvis_origin);
    }

    if (k1.y > l1 + l2) {
        pelvis_origin = v_right - dir_right * (l1 + l1);
        k1.y = l1 + l1;
        k1.x = length(v_left - pelvis_origin);
    }

    vec2 torso = pelvis_origin + vec2(0.0, torso_height);

    vec2 neck = torso 
        + vec2(0.0, neck_height)
        + vec2(
            sincos2.x * 0.001, 
            sincos2.y * 0.001
        );

    vec2 elbow_left = torso +
        rotate(vec2(0.0, -shoulder_length), shoulder_angle_left);
    vec2 palm_left = elbow_left +
        rotate(vec2(0.0, -forearm_length), forearm_angle_left);
    vec2 elbow_right = torso +
        rotate(vec2(0.0, -shoulder_length), shoulder_angle_right);
    vec2 palm_right = elbow_right +
        rotate(vec2(0.0, -forearm_length), forearm_angle_right);

    vec2 d = (k1*k1 - k2) / (2.0 * k1);
    vec2 cos_a = d / l2;
    vec2 a = acos(cos_a);

    mat2x2 rot_left = mat2x2(
        cos_a.x, sin(a.x),
        -sin(a.x), cos_a.x
    );
    mat2x2 rot_right = mat2x2(
        cos_a.y, sin(a.y),
        -sin(a.y), cos_a.y
    );

    vec2 knee_left = (rot_left * dir_left) * l2 + pelvis_origin;
    vec2 knee_right = (rot_right * dir_right) * l2 + pelvis_origin;

    neck /= 2.2;
    torso /= 2.2;
    pelvis_origin /= 2.2;
    knee_left /= 2.2;
    knee_right /= 2.2;
    v_left /= 2.2;
    v_right /= 2.2;
    elbow_left /= 2.2;
    elbow_right /= 2.2;
    palm_left /= 2.2;
    palm_right /= 2.2;

    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, torso, neck, 0.002)
    );

    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, pelvis_origin, torso, 0.002)
    );

    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, pelvis_origin, knee_left, 0.002)
    );
    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, knee_left, v_left, 0.002)
    );

    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, pelvis_origin, knee_right, 0.002)
    );
    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, knee_right, v_right, 0.002)
    );

    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, torso, elbow_left, 0.002)
    );
    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, elbow_left, palm_left, 0.002)
    );
    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, torso, elbow_right, 0.002)
    );
    col = mix(
        col, vec3(0.8, 0.8, 0.8), 
        line(uv, elbow_right, palm_right, 0.002)
    );

    col = mix(col, vec3(1.0, 1.0, 1.0), circle(uv, neck, 0.04));

    col = mix(col, vec3(1.0, 1.0, 1.0), circle(uv, pelvis_origin, 0.01));
    col = mix(col, vec3(1.0, 1.0, 1.0), circle(uv, torso, 0.01));

    col = mix(col, vec3(0.88, 0.4, 0.47), circle(uv, knee_left, 0.005));
    col = mix(col, vec3(0.47, 0.4, 0.88), circle(uv, knee_right, 0.005));

    col = mix(col, vec3(0.88, 0.4, 0.47), circle(uv, elbow_left, 0.005));
    col = mix(col, vec3(0.47, 0.4, 0.88), circle(uv, elbow_right, 0.005));

    col = mix(col, vec3(0.88, 0.4, 0.47), circle(uv, palm_left, 0.005));
    col = mix(col, vec3(0.47, 0.4, 0.88), circle(uv, palm_right, 0.005));

    col = mix(col, vec3(0.88, 0.4, 0.47), circle(uv, v_left, 0.005));
    col = mix(col, vec3(0.47, 0.4, 0.88), circle(uv, v_right, 0.005));


    fragColor = vec4(col,1.0);
}

float N21(vec2 p)
{	// Dave Hoskins - https://www.shadertoy.com/view/4djSRW
	vec3 p3  = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

void mainImage( out vec4 fragColor, vec2 fragCoord )
{
    float
        scale = 15., 
        r = mix(.75, 1.5, sin(iTime*.1)*.5+.5), //radius
        s_freq, //stripe per scale
        s_thic, //stripe thickness
        s_pattern, //stripe pattern 
        d, //testing distance
        Dist,
        mRes = max(iResolution.x, iResolution.y),
        aa,
        PI = 3.14159265359; 

    vec2 
        R = iResolution.xy, 
        id, //local coord
        oUV, //testing UV with offset
        UV = fragCoord / mRes * scale;

    vec3
        blue1 = vec3(36.,126.,246.)/255.,
        blue2 = vec3(26.,88.,234.)/255.,
        bg = vec3(.8),
        s_color;

#define T(x,y)                          \
   oUV = round(UV) + vec2(x, y) / 2.,   \
   d = length(UV - oUV) / r;            \
   d < 1. ? Dist = d, id = oUV : id;    

    T(-1, 1);
    T( 1, 1);
    T( 0, 0);
    T(-1,-1);
    T( 2, 0);
    T( 1,-1);
    T( 3,-1);
    T( 0,-2);
    T( 2,-2);
    T(-1,-3);
    T( 1,-3);
    T( 3,-3);
    T(-2,-4);
    T( 0,-4);
    T( 2,-4); 

    s_freq = r * (5. + sin(id.x / 2. + iTime*.3));
    s_thic = sin(id.y / 3. + iTime * .5) * .4 + .5;
    s_pattern = 2. * abs(mod(((1. - s_thic) / (2. * s_freq)) + 1.01 - Dist, 1. / s_freq) * 2. * s_freq - 1.) - (2. * s_thic);
    s_color = mix(blue1, blue2, N21(id));
    aa = fwidth(s_pattern);
    fragColor = vec4(mix(bg, s_color, smoothstep(-aa, 0., s_pattern)), 1);
}

// different pattern + color, from https://shadertoy.com/view/fdd3D8

  #define SQR(v)     ( (v)*(v) )
  #define fwidth2(v)  sqrt( SQR(dFdx(v)) + SQR(dFdy(v)) ) // for more isotropic
//#define fwidth2(v)  fwidth(v)

void mainImage(out vec4 O, vec2 u ) {    
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y;

    float l = length(U), A = atan(U.y,U.x);
    vec4  a = round(R.y/6.) * ( .00*sin(50.*U.x) + sin(4.*A -2.*l - iTime ) +cos(5.*l-A +iTime) - sin(l*l+ vec4(0,.1,.2,0)) ) , //  pattern
          s = sin( a ),                                   // strip base
          v = U.y > 0.
                ? clamp(.5 + .5 * s/fwidth(s), 0.,1.)     // top: strip pattern with AA borders
                : step( 0., s ),                          // bottom: no AA
      aa = smoothstep(3.14/3., 6.28/3., fwidth2(a));      // detect param going too fast

     if (U.x > 0.) v = mix(  v, vec4(.5), aa );           // right: switch to grey to AA high strip freq

    O =  sqrt( v ) ;                                      // to sRGB

    if ( int(u.x)==int(R.x)/2 ) O = vec4(1,0,0,1);        // vertical separator
    if ( int(u.y)==int(R.y)/2 ) O = vec4(1,0,0,1);        // horizontal separator
}

float iter_max = 32.0;
float bound = 50.0;

vec2 iter(in vec2 z, in vec2 c) {
  return vec2(z.x*z.x-z.y*z.y, 2.0*z.x*z.y) + c;
}

vec2 screenToClip(vec2 coord) {
    vec2 uv = coord/iResolution.xy;
    return (uv - vec2(0.6,0.5)) / (iResolution.yx / 3000.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = screenToClip(fragCoord);
    vec2 z = vec2(0.0) + vec2(cos(iTime*0.3), sin(iTime*.2));

    for (int n = 0; float(n) < iter_max; n++) {
      z = iter(z, c);
      if (length(z) > bound) {
        fragColor = 1.0-vec4(float(n) / float(iter_max));
        return;
      }
    }

    fragColor = vec4(1.0-abs(z), length(z), 1.0);
}

#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S(a, b, t) smoothstep(a,b,t)
#define T iTime(a, b, t)

mat2 Rot(float a) {
    float s=sin(a);
    float c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdGyroid(vec3 p, float scale, float thickness, float bias){        
    p *= scale;
    return abs(dot(sin(p), cos(p.zxy))-bias)/scale-thickness;
}

vec3 Transform(vec3 p){
    p.z -= iTime*.1;
    p.y -+ .3;
    return p;

}

float GetDist(vec3 p) {

    p = Transform(p);

    float box = sdBox(p, vec3(1));

    float g1 = sdGyroid(p, 5.23, .03, 1.3);
    float g2 = sdGyroid(p, 10., .03, .3);
    float g3 = sdGyroid(p, 20., .03, .3);
    float g4 = sdGyroid(p, 40., .03, .3);
    float g5 = sdGyroid(p, 60., .03, .3);
    //float g = min(g1, g2);
    g1 -= g2*.2;
    g1 -= g3*.2;
    g1 += g4*.1;
    g1 += g5*.1;
    float d =  g1*.8;

    return d;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.02, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;
    float t = iTime;
    vec3 col = vec3(0);
    vec3 ro = vec3(0, 0, -.03);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 lookat = vec3(0,0,0);
    vec3 rd = GetRayDir(uv, ro, lookat, .8);


    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        //vec3 r = reflect(rd, n);

        p = Transform (p);
        float dif = n.y*.5+.5;
        col += dif*dif;

        float g2 = sdGyroid(p, 10.76, .03, .3);

        col *= S(-.1, .2, g2);

        float crackw = -.02+S(0.,-.5, n.y)*.04;
        float cracks = S(crackw, -.03, g2);

        float g3 = sdGyroid(p+t*.1, 5.76, .03, .0);

        float g4 = sdGyroid(p-t*.15, 3.76, .03, .05);
        cracks *= g3*g4*20.+.2*S(.2, .0, n.y);
        col += cracks*vec3(1, .4, .1)*3.;
    }



    d = sdGyroid(vec3(uv.x, uv.y, iTime*.1), 20., .01, 0.);

    fragColor = vec4(col,.1);
}

#define S smoothstep

//noise funtion abstract from https://www.shadertoy.com/view/4sc3z2
vec2 hash22(vec2 p)
{
    p = vec2( dot(p,vec2(127.1,311.7)),
			  dot(p,vec2(269.5,183.3)));

    //return normalize(-1.0 + 2.0 * fract(sin(p)*43758.5453123));
    return -1.0 + 2.0 * fract(sin(p)*43758.5453123);
}

float simplex_noise(vec2 p)
{
    const float K1 = 0.366025404; // (sqrt(3)-1)/2;
    const float K2 = 0.211324865; // (3-sqrt(3))/6;

    vec2 i = floor(p + (p.x + p.y) * K1);

    vec2 a = p - (i - (i.x + i.y) * K2);
    vec2 o = (a.x < a.y) ? vec2(0.0, 1.0) : vec2(1.0, 0.0);
    vec2 b = a - (o - K2);
    vec2 c = a - (1.0 - 2.0 * K2);

    vec3 h = max(0.5 - vec3(dot(a, a), dot(b, b), dot(c, c)), 0.0);
    vec3 n = h * h * h * h * vec3(dot(a, hash22(i)), dot(b, hash22(i + o)), dot(c, hash22(i + 1.0)));

    return dot(vec3(70.0, 70.0, 70.0), n);
}

float noise_sum(vec2 p)
{
    float f = 0.0;
    p = p * 4.0;
    f += 1.0000 * simplex_noise(p); p = 2.0 * p;
    f += 0.5000 * simplex_noise(p); p = 2.0 * p;
	f += 0.2500 * simplex_noise(p); p = 2.0 * p;
	f += 0.1250 * simplex_noise(p); p = 2.0 * p;
	f += 0.0625 * simplex_noise(p); p = 2.0 * p;

    return f;
}



vec2 drawMountain(vec2 uv, float f, float d)
{
    float Side = uv.y + noise_sum(vec2(uv.x, mix(uv.y,0.,uv.y))*f)*0.1;
    float detal = noise_sum(vec2(uv.x, uv.y)*8.)*0.005;
    Side += detal;

    float Mountain = S(0.48, 0.49, Side);
    float fog = S(d, noise_sum(vec2(uv.x+iTime*0.06, uv.y)*0.2)*0.2, Side);

    return clamp(vec2(Side+fog, Mountain),0.,1.);
}

float drawSun(vec2 uv)
{
    vec2 u = uv;
    u -= 0.5;
    u.x *= iResolution.x/iResolution.y;  

    float Sun = S(0.09, 0.1, length(vec2(u.x-.5, u.y-.3)));

    float fog = S(0.7,noise_sum(vec2(uv.x+iTime*0.001, uv.y)*2.)*0.05,u.y)*1.4;

    return clamp(Sun+fog,0.,1.);
}

float drawBird(vec2 uv)
{
    uv = (uv-.5)*20.;
    uv.x -= uv.y;

    uv.y = uv.y+.45+(sin((iTime*0.5-abs(uv.x))*3.)-1.)*abs(uv.x)*0.5;

    float S1 = smoothstep(0.45,0.4,length(uv));

    uv.y += .1;
    float S2 = smoothstep(0.5,0.45,length(uv));

    float S = S1-S2;
    return S;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float t = iTime*0.5;

    vec3 c = vec3(1.);


    float Sun = drawSun(uv);
    c = mix(vec3(1.,0.2,0.0), c, Sun);  

    float Bird = drawBird(vec2(uv.x-.15,uv.y-.4));
    c = mix(c, vec3(1.)*.65, Bird);

    uv.y -= .2;
    uv.x += t*0.001;
    vec2 Mountain1 = drawMountain(uv, .4, 1.);
    c = mix(vec3(Mountain1.r), c, Mountain1.g);

    uv.y += .1;
    uv.x += 1.;
    uv.x += t*0.005;
    Mountain1 = drawMountain(uv, .3, .8);
    c = mix(vec3(Mountain1.r), c, Mountain1.g);

    uv.y += .1;
    uv.x += 2.42;
    uv.x += t*0.01;
    Mountain1 = drawMountain(uv, .2, 0.6);
    c = mix(vec3(Mountain1.r), c, Mountain1.g);

    uv.y += .1;
    uv.x += 12.84;
    uv.x += t*0.05;
    Mountain1 = drawMountain(uv, .2, 0.4);
    c = mix(vec3(Mountain1.r)-0.01, c, Mountain1.g);


    vec3 col = vec3(c);

    fragColor = vec4(col,1.0);
}

vec2 Mult(vec2 A,vec2 B)
{
    return vec2(A.x*B.x-A.y*B.y,A.x*B.y+A.y*B.x);
}
vec2 Div(vec2 A, vec2 B)
{
    return vec2(A.x*B.x+A.y*B.y,-A.x*B.y+A.y*B.x)/dot(B,B);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 4.5*(fragCoord-iResolution.xy*0.5)/iResolution.x;
    vec2 Z = vec2(uv);

    float angle1=iTime*0.2;
    float angle2=angle1*0.4;
    vec2 C = vec2(cos(angle2),sin(angle2))*0.30;
    vec2 A=vec2(cos(angle1),sin(angle1));
    vec2 B=-A*0.04;
    fragColor = vec4(1,0,1,0);
    vec2 Der =vec2(1,0);
    for(int i =0;i<20;i++)
    {
        Z=Mult(A,Mult(Z,Z))+Div(B,Mult(Z,Z))+C;
        Der =2.0*Mult(Der,Mult(A,Z)-Div(B,Mult(Z,Mult(Z,Z))));
        float D = Z.x*Z.x+Z.y*Z.y;
        if(D>20.0)
        {
            float e=-2.0+log(log(D)/(log(2.0)*2.0))/log(2.0);
            float a=(float(i)-e)/20.0;
            a=pow(a,0.8);
            fragColor=vec4(a*a,0,a,1);
            return;
        }
    }
    float d = Der.x*Der.x+Der.y*Der.y;
    d=pow(d,0.15);
    fragColor = vec4(1,0,1,0)*d+vec4(0.3,0.2,0.8,0)*(1.0-d);
}

// http://mathworld.wolfram.com/HeartSurface.html

float f(vec3 p) {
    vec3 pp = p * p;
    vec3 ppp = pp * p;
    float a = pp.x + 2.25 * pp.y + pp.z - 1.0;
    return a * a * a - (pp.x + 0.1125 * pp.y) * ppp.z;
}

float f1(vec3 p) {
    p = p - vec3(-1.5, 0.0 , 0.0);
    vec3 pp = p * p;
    vec3 ppp = pp * p;
    float a = pp.x + 2.25 * pp.y + pp.z - 1.0;
    return a * a * a - (pp.x + 0.1125 * pp.y) * ppp.z;
}

float f2(vec3 p) {
    p = p - vec3(1.5, 0.0 , 0.0);
    vec3 pp = p * p;
    vec3 ppp = pp * p;
    float a = pp.x + 2.25 * pp.y + pp.z - 1.0;
    return a * a * a - (pp.x + 0.1125 * pp.y) * ppp.z;
}

float bin(float x, float z){
	float left = 0.0, right = 1.0, mid;
    for(int i = 0; i < 15; ++i){
    	mid = (left + right) * 0.5;
        if(f(vec3(x, mid, z)) <= 0.0)
            left = mid;
        else 
            right = mid;
    }
    return mid;

}

//binary search for intersection
float bin1(float x, float z){
	float left = 0.0, right = 1.0, mid;
    for(int i = 0; i < 15; ++i){
    	mid = (left + right) * 0.5;
        if(f1(vec3(x, mid, z)) <= 0.0)
            left = mid;
        else 
            right = mid;
    }
    return mid;

}

float bin2(float x, float z){
	float left = 0.0, right = 1.0, mid;
    for(int i = 0; i < 15; ++i){
    	mid = (left + right) * 0.5;
        if(f2(vec3(x, mid, z)) <= 0.0)
            left = mid;
        else 
            right = mid;
    }
    return mid;

}

// Analytical gradient
// (-2 x z^3+6 x (-1.+x^2+2.25 y^2+z^2)^2) 
// (-0.225 y z^3+13.5 y (-1.+x^2+2.25 y^2+z^2)^2)
// (z (-3 x^2 z-0.3375 y^2 z+6 (-1.+x^2+2.25 y^2+z^2)^2))
vec3 normal(vec2 p) {
    vec3 v = vec3(p.x, bin(p.x, p.y), p.y);
    vec3 vv = v * v;
    vec3 vvv = vv * v;
    float a = -1.0 + dot(vv, vec3(1, 2.25, 1));
   	a *= a;

  	return normalize(vec3(
        -2.0 * v.x * vvv.z +  6.0 * v.x * a,
      -0.225 * v.y * vvv.z + 13.5 * v.y * a,
      v.z * (-3.0 * vv.x * v.z - 0.3375 * vv.y * v.z + 6.0 * a)));
}

//numeric gradient
vec3 normal1(vec2 p){
	vec3 v = vec3(p.x, bin1(p.x, p.y), p.y);
    float delta = 0.00005;
    float gradx = (f1(vec3(v.x + delta, v.y, v.z)) - f1(vec3(v.x - delta, v.y, v.z))) / (2.0 * delta);
    float grady = (f1(vec3(v.x, v.y + delta, v.z)) - f1(vec3(v.x, v.y - delta, v.z))) / (2.0 * delta);
    float gradz = (f1(vec3(v.x, v.y, v.z + delta)) - f1(vec3(v.x, v.y, v.z - delta))) / (2.0 * delta);
    return normalize(vec3(gradx, grady, gradz));
}

vec3 normal2(vec2 p){
	vec3 v = vec3(p.x, bin2(p.x, p.y), p.y);
    float delta = 0.00005;
    float gradx = (f2(vec3(v.x + delta, v.y, v.z)) - f2(vec3(v.x - delta, v.y, v.z))) / (2.0 * delta);
    float grady = (f2(vec3(v.x, v.y + delta, v.z)) - f2(vec3(v.x, v.y - delta, v.z))) / (2.0 * delta);
    float gradz = (f2(vec3(v.x, v.y, v.z + delta)) - f2(vec3(v.x, v.y, v.z - delta))) / (2.0 * delta);
    return normalize(vec3(gradx, grady, gradz));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
