    float s = sin(iTime * 5.0);
    s *= s;
    s *= s;
    s *= 0.08;
    vec3 tp = p * vec3(1.0 + s, 1.0 - s, 0.0) * 2.0;

    vec3 c;
    if (f1(tp.xzy) <= 0.0) {
        vec3 n = normal1(tp.xy);
        float diffuse = dot(n, normalize(vec3(-1, 1, 1))) * 0.5 + 0.5;
        float specular = pow(max(dot(n, normalize(vec3(-1, 2, 1))), 0.0), 32.0);
        float rim = 1.0 - dot(n, vec3(0.0, 1.0, 0.0));
        c = diffuse * vec3(1.0, 0.0, 0.0) + specular * vec3(0.8) + rim * vec3(0.5);
    }
	else if (f2(tp.xzy) <= 0.0) {
        vec3 n = normal2(tp.xy);
        float diffuse = dot(n, normalize(vec3(-1, 1, 1))) * 0.5 + 0.5;
        float specular = pow(max(dot(n, normalize(vec3(-1, 2, 1))), 0.0), 32.0);
        float rim = 1.0 - dot(n, vec3(0.0, 1.0, 0.0));
        c = diffuse * vec3(1., 0.0, 0.0) + specular * vec3(0.8) + rim * vec3(0.5);
    }
    else    c = vec3(1.0, 0.8, 0.7 - 0.07 * p.y) * (1.0 - 0.15 * length(p));

	fragColor = vec4(c, 1);
}

