// Implicit definition (my own modification of the tanglecube formula here:
// https://mathworld.wolfram.com/Tanglecube.html):
// (x^4 + y^4)a + (x^2 + y^2)b + c
float sdTanglesquare(in vec2 p, in float a, in float b, in float c) {
    p = abs(p); // Quadrant symmetry
    if (p.y > p.x) p = p.yx; // Diagonal symmetry

    // Upper-right critical point in the top right quadrant 
    vec2 crit = p - vec2(sqrt((-b + sqrt(2.0 * b * b - 4.0 * a * c)) / (2.0 * a)), sqrt(-b / (2.0 * a)));
    float d = max(abs(crit.x), abs(crit.y));

    // Lower critical point in the top right quadrant
    if (abs((b * b) / (a * c) - 3.0) < 1.0) {
        crit = p - vec2(sqrt(-b / (2.0 * a)), sqrt((-b - sqrt(2.0 * b * b - 4.0 * a * c)) / (2.0 * a)));
        d = min(d, max(abs(crit.x), abs(crit.y)));
    }

    else {
        crit = p - vec2(sqrt((-b + sqrt(b * b - 4.0 * a * c)) / (2.0 * a)), 0.0); // Rightmost middle critical point
        d = min(d, max(abs(crit.x), abs(crit.y)));

        float h = b * b - 4.0 * a * c;
        if (h > 0.0) {
            float x = (-b - sqrt(h)) / (2.0 * a);
            if (x > 0.0) {
                crit = p - vec2(sqrt(x), 0.0); // Next critical point to the left
                d = min(d, max(abs(crit.x), abs(crit.y)));
            }
        }
    }

    // Diagonal ray intersection
    vec2 p2 = p * p, p4 = p2 * p2;
    float sum1 = p.x + p.y, diff1 = p.x - p.y;
    float sum2 = p2.x + p2.y, sum4 = p4.x + p4.y;

    // (ux^2 + vx + w)^2 + t = 0 ---> ux^2 + vx + w = (+/-)sqrt(-t)
    float u = sqrt(2.0 * a);
    float v = sum1 * u;
    float w = ((3.0 * sum2 - sum1 * sum1) * a + b) / u;
    float t = sqrt(w * w - sum4 * a - sum2 * b - c);

    // ux^2 + vx + w = +sqrt(-t)
    float h = v * v - 4.0 * u * (w - t);
    float k = 2.0 * u;
    if (h > 0.0) {
        h = sqrt(h);
        d = min(d, min(abs(v - h), abs(v + h)) / k);
    }

    // ux^2 + vx + w = -sqrt(-t)
    h = v * v - 4.0 * u * (w + t);
    if (h > 0.0) {
        h = sqrt(h);
        d = min(d, min(abs(v - h), abs(v + h)) / k);
    }

    // Other diagonal ray intersection
    v = diff1 * u;
    w = ((3.0 * sum2 - diff1 * diff1) * a + b) / u;
    t = sqrt(w * w - sum4 * a - sum2 * b - c);

    // ux^2 + vx + w = +sqrt(-t)
    h = v * v - 4.0 * u * (w - t);
    if (h > 0.0) {
        h = sqrt(h);
        d = min(d, min(abs(v - h), abs(v + h)) / k);
    }

    // ux^2 + vx + w = -sqrt(-t)
    h = v * v - 4.0 * u * (w + t);
    if (h > 0.0) {
        h = sqrt(h);
        d = min(d, min(abs(v - h), abs(v + h)) / k);
    }

    return d * sign(sum4 * a + sum2 * b + c);
}

// Critical points visualization SDF
float sdCritical(in vec2 p, in float a, in float b, in float c) {
    p = abs(p);
    if (p.y > p.x) p = p.yx;

    float d = length(p - vec2(sqrt((-b + sqrt(2.0 * b * b - 4.0 * a * c)) / (2.0 * a)), sqrt(-b / (2.0 * a))));
    vec2 crit;

    if (abs((b * b) / (a * c) - 3.0) < 1.0) {
        crit = vec2(sqrt(-b / (2.0 * a)), sqrt((-b - sqrt(2.0 * b * b - 4.0 * a * c)) / (2.0 * a)));
        d = min(d, length(p - crit));
    }

    else {
        crit = vec2(sqrt((-b + sqrt(b * b - 4.0 * a * c)) / (2.0 * a)), 0.0);
        d = min(d, length(p - crit));

        float h = b * b - 4.0 * a * c;
        if (h > 0.0) {
            float x = (-b - sqrt(h)) / (2.0 * a);
            if (x > 0.0) {
                crit = vec2(sqrt(x), 0.0);
                d = min(d, length(p - crit));
            }
        }
    }

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Boilerplate
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 4.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 4.0;
    float unit = 8.0 / iResolution.y;
    float time = iTime * 0.5;

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(0.5, 0.5 * sin(time)) * cos(time) * 4.0;

    float a = 16.0;
    float b = -25.0;
    float c = mix(-1.0, 12.0, 0.5 + 0.5 * cos(iTime));
    float dist = sdTanglesquare(uv, a, b, c);

    // Nice colors from iq
    vec3 color = 1.0 - sign(dist) * vec3(0.1, 0.4, 0.7);
	color *= 1.0 - exp(-2.0 * abs(dist));
	color *= 0.8 + 0.2 * cos(100.0 * dist);
	color = mix(color, vec3(1.0), smoothstep(unit, 0.0, abs(dist * 1.25)));

    // Critical points
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(unit, 0.0, sdCritical(uv, a, b, c) - 0.05));

    // Visualize mouse distance
    float mDist = abs(sdTanglesquare(mouse, a, b, c));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, length(uv - mouse) - 0.025));
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(unit, 0.0, abs(max(abs(uv.x - mouse.x), abs(uv.y - mouse.y)) - mDist) - 0.001));

    fragColor = vec4(color, 1.0);
}

#define radius .3
#define PI 3.14159265358979


float sdfLine(vec2 P, vec2 A, vec2 B, float r) {
    /**
     * /ht Inigo Quilez
     * https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
     **/ 

    vec2 pa = P-A, ba = B-A;
    float h = clamp(
                  dot(pa, ba)/
                  dot(ba, ba),
                  0.0,
                  1.0);
    /**
     * Additional calc determine which side of the line we're on.
     **/
    vec2 norm = vec2( ba.y, -ba.x );
    float d = dot( normalize( norm ), pa );

    /**
     * Multiply by the sign so we can include 2 pieces
     * of information in the return value.
     *   -ve -> "left side" of the line
     *   +ve -> "right side" of the line
     **/
    return (length(pa-ba*h) - r) * sign(d);
}

float glowBomb(vec2 uv, vec2 A, vec2 B) { 
    float strokeWidth = 0.;//24. * 1./iResolution.x; // Scale by N units of the X resolution
    float line = sdfLine(uv, A, B, strokeWidth);
    float lineSide = step(0., line); // Same as: line < 0. ? 0. : 1.;

    return (1. - smoothstep( abs(line)/1.15, 0.0, .075 ));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 * iResolution.xy)/iResolution.y;

    float angle = (iTime + 1.) * .08;

    vec2 A = vec2(
         sin(PI * angle * 7.) * radius,
        -sin(PI * angle * 2.) * radius
    );

    vec2 B = vec2(
        -sin(PI * angle * 3.) * radius,
         sin(PI * angle * 2.) * radius
    );

    vec2 C = vec2(
        -sin(PI * angle * 5.) * radius,
         sin(PI * angle * 2.) * radius
    );

    vec2 Z = vec2(
        cos(PI * angle * 2.) * radius * .3,
        sin(PI * angle * 4.) * radius * .3
    );

    vec3 col = vec3(0.);

    float L = glowBomb(uv, Z, A);
    col += L * vec3(.25, .55, 1.);

    L = glowBomb(uv, B, Z);
    col += L * vec3(.75, 1., .88);

    L = glowBomb(uv, C, Z);
    col += L * vec3(1., .65, .3);

    col *= .8;

    // Output to screen
    fragColor = vec4(col,1.0);
}

float sphere (vec3 point, float r) {
    return length(point)-r;
}

float cube (vec3 point, float side) {
    point.x *= 1.5;
    float _t = -.5 * iTime;
    point.xz = vec2(
        point.x * sin(_t) + point.z * cos(_t),
        point.z * sin(_t) - point.x * cos(_t));
	vec3 q = abs(point) - side;
	return length(max(q, 0.0)) + min(max(q.x, max(q.y, q.z)), 0.0);
}

float torus(vec3 point, float r1, float r2) {
    float _t = iTime;
    point.xz = vec2(
        point.x * sin(_t) + point.z * cos(_t),
        point.z * sin(_t) - point.x * cos(_t));
	return length( vec2(length(point.xy)-r1,point.z) )-r2;
}

float dist (vec3 p) {
    float far = 2.;
    float d0 = torus(p - vec3(0,0,far), .3, .15);
    float d1 = sphere(p - vec3(0,.5 + sin(iTime),far),.15);
    float d2 = sphere(p - vec3(.7 + .1 * sin(iTime),0,far),.3);
    float d3 = cube(p - vec3(-.7,-.2,far), .2);

    float d = min(min(min(d0, d1), d2), d3);

    return d;
}

bool intersect (vec3 p, vec3 d) {
    float d0 = .0, total = .0;
    for(int i = 0; i < 20; i ++) {
        vec3 point = p + total * d;
        d0 = dist(point);
        total += d0;
        if (d0 < .01) return true;
    }
    return false;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2. * fragCoord/iResolution.xy - 1.;
    uv.x *= iResolution.x / iResolution.y;
    vec2 m = iMouse.xy / iResolution.xy - .5;
    m *= vec2(1.6, .6);
    vec3 cam = vec3(m, 0);
    vec3 dir = normalize(vec3(uv,1));
    vec3 light = vec3(0,1,50);
    vec3 col = vec3(.1,.4,1);

    float b = 0., st = 1.0 / length(light-cam);
    vec3 l = normalize(light - cam);
    for(int i = 0; i < 60; i ++) {
        vec3 p = cam + st * float(i) * dir;
        if (length(p) > length(light)) break;
        if (!intersect(p, normalize(light - p))) {
            b += st * pow(dot(l, dir), 4.);
        } else col -= .2 * st;
    }
    b = smoothstep(0., 1.5, b);
    col = clamp(col + b, .0, 1.);

    fragColor = vec4(col,1.0);
}

float hash( float n )
{
    return fract(sin(n)*758.5453)*2.;
}

float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x); 
    f = f*f*(3.0-2.0*f);
    float n = p.x + p.y*57.0 + p.z*800.0;
    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x), mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
            mix(mix( hash(n+800.0), hash(n+801.0),f.x), mix( hash(n+857.0), hash(n+858.0),f.x),f.y),f.z);
    return res;
}

float fbm(vec3 p)
{
    float f = 0.0;
    f += 0.50000*noise( p ); p = p*2.02+0.15;
    f -= 0.25000*noise( p ); p = p*2.03+0.15;
    f += 0.12500*noise( p ); p = p*2.01+0.15;
    f += 0.06250*noise( p ); p = p*2.04+0.15;
    f -= 0.03125*noise( p );
    //return f/0.984375;
    return f;
}

float cloud(vec3 p)
{
    p-=fbm(vec3(p.x,p.y,0.0)*0.5)*0.7;

    float a =0.0;
    a-=fbm(p*3.0)*2.2-1.1;
    if (a<0.0) a=0.0;
    a=a*a;
    return a;
}

mat2 rot( float th ){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }


vec3 hash33(vec3 p)
{
    const float UIF = (1.0/ float(0xffffffffU));
    const uvec3 UI3 = uvec3(1597334673U, 3812015801U, 2798796415U);
    uvec3 q = uvec3(ivec3(p)) * UI3;
	q = (q.x ^ q.y ^ q.z)*UI3;
	return vec3(q) * UIF;
}


// 3D Voronoi- (IQ)
float voronoi(vec3 p){

	vec3 b, r, g = floor(p);
	p = fract(p);
	float d = 1.; 
	for(int j = -1; j <= 1; j++)
    {
	    for(int i = -1; i <= 1; i++)
        {
		    b = vec3(i, j, -1);
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 0.0;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 1.;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
	    }
	}
	return d;
}

// fbm layer
float noiseLayers(in vec3 p) {

    vec3 pp = vec3(0., 0., p.z + iTime*.09);
    float t = 0.;
    float s = 0.;
    float amp = 1.;
    for (int i = 0; i < 5; i++)
    {
        t += voronoi(p + pp) * amp;
        p *= 2.;
        pp *= 1.5;
        s += amp;
        amp *= .5;
    }
    return t/s;
}

vec3 n2 (vec2 fragCoord)
{
	vec2 uv = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
        float dd = length(uv*uv)*.025;

	vec3 rd = vec3(uv.x, uv.y, 1.0);

    float rip = 0.5+sin(length(uv)*20.0+iTime)*0.5;
    rip = pow(rip*.38,4.15);
    rd.z=1.0+rip*1.15;// apply a subtle ripple
    rd = normalize(rd);
    rd.xy *= rot(dd+iTime*.0125);
    rd*=2.0;

	float c = noiseLayers(rd*1.85);
    float oc = c;
    c = max(c + dot(hash33(rd)*2. - 1., vec3(.006)), 0.);
    c = pow(c*1.55,2.5);    
    vec3 col =  vec3(.55,0.85,.25);
    vec3 col2 =  vec3(1.4,1.4,1.4)*5.0;
    float pulse2 = voronoi(vec3((rd.xy*1.5),iTime*.255));
    float pulse = pow(oc*1.35,4.0);
    col = mix(col,col2,pulse*pulse2)*c;
    return col;

}
#define PI 3.14159


float vDrop(vec2 uv,float t)
{
uv.y *= 0.25;
    uv.x = uv.x*128.0;						// H-Count
    float dx = fract(uv.x);
    uv.x = floor(uv.x);
    uv.y *= 0.05;							// stretch
    float o=sin(uv.x*215.4);				// offset
    float s=cos(uv.x*33.1)*.3 +.7;			// speed
    float trail = mix(95.0,35.0,s);			// trail length
    float yv = fract(uv.y + t*s + o) * trail;
    yv = 1.0/yv;
    yv = smoothstep(0.0,1.0,yv*yv);
    yv = sin(yv*PI)*(s*5.0);
    float d2 = sin(dx*PI);
    return yv*(d2*d2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
