	ss+=8.0;
   	vec2 coord = ss*position;

    coord.y *= 1.0+(sin(time*0.04+coord.x*.24)*0.3);

    coord*=rot(ss*0.04+time*0.017);
    coord*=0.25;
    coord+=fbm(sin(vec3(coord*8.0,time*0.001)))*0.05;
    coord+=time*0.0171;
    float q = cloud((vec3(coord*1.0,0.222)));
    coord+=time*0.0171;
    q += cloud((vec3(coord*0.6,0.722)));
    coord+=time*0.0171;
    q += cloud(vec3(coord*0.3,.722));
    coord+=time*0.1171;
    q += cloud((vec3(coord*0.1,0.722)));


	float vv1 = sin(time+ss+coord.x)*0.3;
	float vv2 = sin(time*0.9+ss+coord.y)*0.2;

    vec3 col = vec3(1.7-vv2,1.7,1.7+vv1) + vec3(q*vec3(0.7+vv1,0.5,0.3+vv2*1.15));
	col = pow(col,vec3(2.2))*0.08;

	float dd = length(col*.48)+vv1;

	float nn = 0.5+sin(ss*2.7+position.x*2.41+time*0.9)*0.5;

    vec3 col2 = n2(fragCoord)*0.9;

    vec2 p = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;
    float d = length(p);
	p = vec2(atan(p.x, p.y) / PI, 2.5 / d);
    float t = -time*0.04;
    float drop = vDrop(p,t);
    drop += vDrop(p,t+0.5);
    drop*=d;

        col2+=(col*.965);

	col = mix(col,col2,nn);
    col = mix(col,col*1.075,drop);

    col+=col*((d+dd)*0.28);
    col *= d;


    fragColor = vec4( col, 1.0 );
}

void mainImage(out vec4 O,vec2 I)
{
    //Make sure O = vec4(0).
    O -= O;
    //Iterate through layers.
    for(float i=9.,r; i<2e2; i+=9.)
    {
        //Compute radius with pseudo randomness.
        r = i+tan(i);
        //Layer color and intensity.
        O += (cos(i+vec4(1,2,3,0))+1.)*i/6e2*
        //Circle rings.
        max(0.,2.-abs(length(mod(I+=vec2(r,9)+iTime/.2,r+r)-r)-.8*r));
    }
}

float plane(vec3 position, vec3 planeNormal, float planeDistance) {
    float d = dot(position, planeNormal) + planeDistance;
    return d / dot(planeNormal, sign(planeNormal));
}

float box(vec3 position, vec3 halfSize) {
    position = abs(position) - halfSize;
    return max(max(position.x, position.y), position.z);
}

float sphere(vec3 position, float radius) {
    position = abs(position);
    float d = max(max(position.x - radius, position.y), position.z);
    d = min(d, max(max(position.x, position.y - radius), position.z));
    d = min(d, max(max(position.x, position.y), position.z - radius));
    {
        float b = dot(vec2(1.0), position.yz);
        float c = dot(position.yz, position.yz) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.x));
        }
    }
    {
        float b = dot(vec2(1.0), position.xz);
        float c = dot(position.xz, position.xz) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.y));
       }
    }
    {
        float b = dot(vec2(1.0), position.xy);
        float c = dot(position.xy, position.xy) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.z));
        }
    }
    float b = dot(vec3(1.0), position);
    float c = dot(position, position) - (radius * radius);
    float discriminant = b * b - 3.0 * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / 3.0;
        d = min(d, t);
    }

    return d;
}

float ellipsoid(vec3 position, vec3 radius) {
    position = abs(position);
    float d = max(max(abs(position.x - radius.x), position.y), position.z);
    d = min(d, max(max(position.x, abs(position.y - radius.y)), position.z));
    d = min(d, max(max(position.x, position.y), abs(position.z - radius.z)));
    {
        float a = dot(vec2(1.0), 1.0 / (radius.yz * radius.yz));
        float b = dot(vec2(1.0), position.yz / (radius.yz * radius.yz));
        float c = dot(vec2(1.0), (position.yz * position.yz) / (radius.yz * radius.yz)) - 1.0;
        float discriminant = b * b - a * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / a;
            d = min(d, max(t, position.x));
        }
    }
    {
        float a = dot(vec2(1.0), 1.0 / (radius.xz * radius.xz));
        float b = dot(vec2(1.0), position.xz / (radius.xz * radius.xz));
        float c = dot(vec2(1.0), (position.xz * position.xz) / (radius.xz * radius.xz)) - 1.0;
        float discriminant = b * b - a * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / a;
            d = min(d, max(t, position.y));
       }
    }
    {
        float a = dot(vec2(1.0), 1.0 / (radius.xy * radius.xy));
        float b = dot(vec2(1.0), position.xy / (radius.xy * radius.xy));
        float c = dot(vec2(1.0), (position.xy * position.xy) / (radius.xy * radius.xy)) - 1.0;
        float discriminant = b * b - a * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / a;
            d = min(d, max(t, position.z));
        }
    }
    float a = dot(vec3(1.0), 1.0 / (radius * radius));
    float b = dot(vec3(1.0), position / (radius * radius));
    float c = dot(vec3(1.0), (position * position) / (radius * radius)) - 1.0;
    float discriminant = b * b - a * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / a;
        d = min(d, t);
    }
    return d;
}

float cylinder(vec3 position, float radius, float halfHeight) {
    position = abs(position);
    float d = max(position.x - radius, position.z);
    d = min(d, max(position.x, position.z - radius));
    float b = dot(vec2(1.0), position.xz);
    float c = dot(position.xz, position.xz) - (radius * radius);
    float discriminant = b * b - 2.0 * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / 2.0;
        d = min(d, t);
    }
    d = max(d, position.y - halfHeight);
    return d;
}

float capsule(vec3 position, float radius, float halfHeight) {
    position = abs(position);
    position.y -= halfHeight - radius;
    float d = max(max(position.x - radius, position.y), position.z);
    d = min(d, max(max(position.x, position.y - radius), position.z));
    d = min(d, max(max(position.x, position.y), position.z - radius));
    {
        float b = dot(vec2(1.0), position.yz);
        float c = dot(position.yz, position.yz) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.x));
        }
    }
    {
        float b = dot(vec2(1.0), position.xz);
        float c = dot(position.xz, position.xz) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.y));
       }
    }
    {
        float b = dot(vec2(1.0), position.xy);
        float c = dot(position.xy, position.xy) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.z));
        }
    }
    float b = dot(vec3(1.0), position);
    float c = dot(position, position) - (radius * radius);
    float discriminant = b * b - 3.0 * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / 3.0;
        d = min(d, t);
    }
    return d;
}

mat3 rotationMatrixAxisAngle(vec3 axis, float angle) {
    float s = sin(angle);
    float c = cos(angle);
    vec3 column1 = axis * axis.x * (1.0 - c) + vec3(c, axis.z * s, -axis.y * s);
    vec3 column2 = axis * axis.y * (1.0 - c) + vec3(-axis.z * s, c, axis.x * s);
    vec3 column3 = axis * axis.z * (1.0 - c) + vec3(axis.y * s, -axis.x * s, c);
    return mat3(column1, column2, column3);
}

mat3 lookAtMatrix(vec3 from, vec3 to) {
    vec3 forward = normalize(to - from);
    vec3 right = normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up = cross(right, forward);
    return mat3(right, up, forward);
}

vec4 sdf(vec3 position) {
    float material = 0.0;
    float d = plane(position, vec3(0.0, 1.0, 0.0), 0.0);
    float d1 = box(position - vec3(-4.0, 0.5, 0.0), vec3(0.5, 0.5, 0.5));
    if (d1 < d) {
        material = 1.0;
        d = d1;
    }
    float d2 = cylinder(position - vec3(-2.0, 0.5, 0.0), 0.5, 0.5);
    if (d2 < d) {
        material = 2.0;
        d = d2;
    }
    float d3 = capsule(position - vec3(0.0, 0.6, 0.0), 0.3, 0.6);
    if (d3 < d) {
        material = 3.0;
        d = d3;
    }
    float d4 = sphere(position - vec3(2.0, 0.5,  0.0), 0.5);
    if (d4 < d) {
        material = 4.0;
        d = d4;
    }
    float d5 = ellipsoid(position - vec3(4.0, 0.5, 0.0), vec3(0.75, 0.5, 0.5));
    if (d5 < d) {
        material = 5.0;
        d = d5;
    }
    return vec4(d, material, 0.0, 0.0);
}

vec3 normal(vec3 position) {
    float epsilon = 0.001;
    vec3 gradient = vec3(
        sdf(position + vec3(epsilon, 0, 0)).x - sdf(position + vec3(-epsilon, 0, 0)).x,
        sdf(position + vec3(0, epsilon, 0)).x - sdf(position + vec3(0, -epsilon, 0)).x,
        sdf(position + vec3(0, 0, epsilon)).x - sdf(position + vec3(0, 0, -epsilon).x)
    );
    return normalize(gradient);
}

float shadow(vec3 rayOrigin, vec3 rayDirection) {
    int stepCount = 64;
    float t = 0.03;
    float maximumDistance = 20.0;
    for (int i = 0; i < stepCount; i++) {
        if (t > maximumDistance) {
            break;
        }
        vec3 currentPosition = rayOrigin + rayDirection * t;
        float d = sdf(currentPosition).x;
        if (d < 0.001) {
            return 0.0;
        }
        t += d;
    }
    return 1.0;
}

vec4 raycast(vec3 rayOrigin, vec3 rayDirection) {
    vec4 hitResult = vec4(0.0, 0.0, 0.0, 0.0);
    int stepCount = 256;
    float maximumDistance = 20.0;
    float t = 0.0;
    for (int i = 0; i < stepCount; i++) {
        if (t > maximumDistance) {
            break;
        }
        vec3 currentPosition = rayOrigin + rayDirection * t;
        vec4 result = sdf(currentPosition);
        if (result.x < 0.001) {
            hitResult = vec4(t, result.y, 0.0, 0.0);
            break;
        }
        t += result.x;
    }
    return hitResult;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv = (uv * 2.0) - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    vec2 mousePosition = iMouse.xy / iResolution.xy;

    vec3 targetPosition = vec3(0.0, 0.0, 0.0);
    float angle = sin(iTime * 0.2) * 1.0;
    vec3 rayOrigin = targetPosition + vec3(5.0 * sin(angle + 2.0 * -mousePosition.x), 2.0 - 2.0 * mousePosition.y, 5.0 * cos(angle + 2.0 * -mousePosition.x));
    vec3 rayDirection = normalize(vec3(uv, 2.5));
    mat3 cameraTransform = lookAtMatrix(rayOrigin, targetPosition);
    rayDirection = cameraTransform * rayDirection;

    vec3 color = mix(vec3(1.0), vec3(0.25, 0.5, 1.0) * 1.2, (rayDirection.y + 0.3) * 2.0);

    vec4 hitResult = raycast(rayOrigin, rayDirection);
    float t = hitResult.x;
    float material = hitResult.y;
    if (t > 0.0) {
        vec3 position = rayOrigin + rayDirection * t;
        vec3 lightDirection = normalize(vec3(-0.5, 0.8, 0.2));
        vec3 n = normal(position);
        float shadowAttenuation = shadow(position, lightDirection);

        float diffuseAngle = max(dot(n, lightDirection), 0.0);
        vec3 halfwayDirection = normalize(lightDirection + -rayDirection);
        float specularAngle = pow(max(dot(n, halfwayDirection), 0.0), 16.0);

        vec3 baseColor = vec3(0.2);
        if (material == 0.0) {
            float checkerboard = mod(floor(2.0 * position.z) + floor(2.0 * position.x), 2.0);
            baseColor += vec3(0.1) * checkerboard;
        } else {
            baseColor = 0.2 + 0.2 * cos(6.28318 * (vec3(1.0, 1.0, 1.0) * (material / 6.0) + vec3(0.0, 0.33, 0.67)));
        }

        // diffuse
        color = 2.0 * vec3(1.0, 0.85, 0.55) * diffuseAngle * shadowAttenuation * baseColor;
        // specular
		color += 1.0 * vec3(1.0, 0.85, 0.55) * specularAngle * diffuseAngle * shadowAttenuation * baseColor;
        // ambient
        color += 0.5 * vec3(0.25, 0.5, 1.0) * ((n.y + 1.0) * 0.5) * baseColor; // sky
        color += 0.5 * vec3(0.25, 0.25, 0.25) * clamp(dot(n, normalize(vec3(-lightDirection.x, 0.0, -lightDirection.z))), 0.0, 1.0) * clamp(1.0 - position.y, 0.0, 1.0) * baseColor; // back
        // fog
        color = mix(color, vec3(0.7, 0.7, 0.9), 1.0 - exp(-0.0001 * t * t * t));
    }
    // gamma
    color = pow(color, vec3(0.4545));
    fragColor = vec4(color, 1.0);
}

#define PI 3.14159265359
#define viewAngle (PI*0.6)
#define distToScreen (0.5*max(iResolution.x, iResolution.y)/tan(0.5*viewAngle))
#define maxDist 70.0
#define maxStep 500
#define nEPS 0.0125

#define meanWaterLevel -0.5

#define SKY 0.0
#define WATER 1.0
#define LAND 2.0

// MODE 0 sticks the camera to the ground. Drag the mouse to explore.
// MODE 1 makes the camera fly above the terrain
#define MODE 1


//increase this constant to increase the amount of erosion
#define OCTAVES 5

float fbm(in vec2 uv)
{
    //this function generates the terrain height
    float value = 0.;
    float factor = 5.;
    uv *= factor;
    //uv = uv.yx;
    for (int i = 0; i < OCTAVES; i++)
    {
        uv.x *= factor;
        uv += max(sin(uv*factor)/factor,cos(uv/factor)*factor);
        value = min(value,sin((uv.x-uv.y-value))/factor);
        uv.y /= factor;

        uv= -uv.yx/(factor);
        //factor /= 1.5;
        value /= 1.5;
    }

    return value+.5;
}


struct MarchResult {
    float dist;
    vec3 pos;
    float type;
};

float getElevation(vec2 uv, float d) {
    float factor = 3.0;
    return fbm(uv/factor)*factor;
}

vec3 getNormal(vec3 p, float d) {
    return normalize(vec3(
        getElevation(vec2(p.x-nEPS,p.z), d) - getElevation(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getElevation(vec2(p.x,p.z-nEPS), d) - getElevation(vec2(p.x,p.z+nEPS), d)
    ));
}

float getWaterLevel(vec2 p, float d) {
    if (d<5.0) {
    	float t = iTime*1.0;
    	p*=7.0;
    	float w = 0.00025*smoothstep(0.0, 1.0, 0.5/(d+0.00001));
    	return w*(sin(p.y*7.37+t*2.0) + sin(p.x*2.37+t)) + meanWaterLevel;
    }
	else return meanWaterLevel;
}

vec3 getWaterNormal(vec3 p, float d) {
    return normalize(vec3(
        getWaterLevel(vec2(p.x-nEPS,p.z), d) - getWaterLevel(vec2(p.x+nEPS,p.z), d),
        2.0*nEPS,
        getWaterLevel(vec2(p.x,p.z-nEPS), d) - getWaterLevel(vec2(p.x,p.z+nEPS), d)
    ));
}

vec3 rayToPixel(vec2 pixel) {
    pixel -= 0.5*iResolution.xy;
    return normalize(vec3(pixel.x, pixel.y, distToScreen));
}

float estDistToTrn(vec3 p, float d) {
    return (p.y - getElevation(p.xz, d))*(d*0.015+0.35);
}


// TODO generate procedural textures for rocks and grass on the fly
vec4 rock(vec3 p) {
    return texture(iChannel0, p.xz);
}

vec4 grass(vec3 p) {
    return mix(vec4(0.2, 0.4, 0.15, 1.0), texture(iChannel1, p.xz), 0.1);
}

vec4 snow(vec3 p) {
    return vec4(0.9, 0.9, 0.9, 1.0);
}

vec4 fog(vec3 ray, float d, vec3 sunDir, vec4 material) {
    float fogAmount = 1.0-exp(-d*0.035);
    float sunAmount = pow(max(dot(ray, sunDir), 0.0), 90.0);
    vec4 fogCol = mix(vec4(0.3, 0.7, 0.9, 1.0), vec4(1.0, 0.9, 0.7, 1.0), sunAmount);
    return mix(material, fogCol, fogAmount);
}

vec4 terrain(vec3 p, vec3 sunDir) {
    vec3 normal = getNormal(p, 0.0);
	vec3 abnormal = abs(normal);	    
	vec4 grassRock = mix(grass(p), rock(p), smoothstep(0.0, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 snowRock = mix(snow(p), rock(p), smoothstep(0.75, 1.0, max(abnormal.x, abnormal.z)));
   	vec4 fragC = mix(grassRock, snowRock, smoothstep(0.5, 1.0, p.y));
   	fragC *= max(dot(sunDir, normal), 0.2);
    return fragC;
}

MarchResult march(vec3 p0, vec3 ray, bool withWater) {
    float type = SKY;
    float d = 0.0;
    int stp = 0;
    vec3 p = p0;
    while (type==SKY && d<(withWater?maxDist:maxDist*0.125) && (stp++<(withWater?maxStep:maxStep/3))) {
        p = p0 + d*ray;
        float waterLevel = withWater ? /*getWaterLevel(p.xz, d)*/ meanWaterLevel : -9999.9;
        float stpSize = estDistToTrn(p,d) * (withWater?1.0:2.0);
        // TODO fix this mess
        if (p.y<=waterLevel) {
            type = WATER;
            d = (waterLevel-p0.y)/ray.y;
            p = p0+d*ray;
        }
        else if (stpSize<d*0.001) type = LAND;
        else d+= stpSize;
    }
    d = min(d, maxDist);
    return MarchResult(d, p, type);
}


vec4 water(vec3 p, float d, vec3 ray, vec3 sunDir) {
    vec3 normal = getWaterNormal(p, d);
    vec3 ref = normalize(reflect(-sunDir, normal));
    vec4 wc = vec4(0.2,0.55,0.8,1.0);
    vec4 sc = vec4(0.9,0.9,0.7,1.0);
    wc *= max(0.35, dot(sunDir, normal));

    MarchResult uwr = march(p, normalize(reflect(ray, normal)), false);
    vec4 uwt = terrain(uwr.pos, sunDir);
    wc = mix(wc, uwt, uwr.type*0.25);

    return mix(wc, sc, 0.85*pow(max(dot(ref, -ray),0.0),8.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{	

    float pitch = MODE==0 ? 0.0 : 0.2*sin(iTime*0.2);
    float yaw = 0.0;
    float roll = MODE==0 ? 0.0 : 0.1*sin(iTime*0.5);

    vec3 ray = rayToPixel(fragCoord);

    mat3 tr = mat3(
        cos(roll),  -sin(roll), 0.0,
        sin(roll), cos(roll), 0.0,
        0, 0, 1
    ) 
    * mat3(
        cos(yaw), 0.0, sin(yaw),
        0.0, 1.0, 0.0,
        -sin(yaw), 0.0, cos(yaw)
    )
    * mat3(
        1.0, 0.0, 0.0,
        0.0, cos(pitch), -sin(pitch),
        0.0, sin(pitch), cos(pitch)
    )
    ;
    ray *= tr;


    #if MODE
    vec3 p0 = vec3(17.25, 2.0, 1.0*iTime);
    #else
    vec3 p0 = vec3(60.0*iMouse.x/iResolution.x, -0.25, 60.0*iMouse.y/iResolution.y);
    p0.y = max(getElevation(p0.xz,0.0), getWaterLevel(p0.xz,0.0)) + 0.05;
    #endif

    MarchResult res = march(p0, ray, true);
    vec3 sunDir = normalize(vec3(0.2, 0.1, 0.15));

    fragColor = vec4(1.0);
    if (res.dist<maxDist) {
        if (res.type==WATER) {
            fragColor = water(res.pos, res.dist, ray, sunDir);
        } else if (res.type==LAND) {
    		fragColor = terrain(res.pos, sunDir);
        }
    }

    fragColor = fog(ray, res.dist, sunDir, fragColor);
}

#define radius .3
#define PI 3.14159265358979


float sdfLine(vec2 P, vec2 A, vec2 B, float r) {
    /**
     * /ht Inigo Quilez
     * https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
     **/ 

    vec2 pa = P-A, ba = B-A;
    float h = clamp(
                  dot(pa, ba)/
                  dot(ba, ba),
                  0.0,
                  1.0);
    /**
     * Additional calc determine which side of the line we're on.
     **/
    vec2 norm = vec2( ba.y, -ba.x );
    float d = dot( normalize( norm ), pa );

    /**
     * Multiply by the sign so we can include 2 pieces
     * of information in the return value.
     *   -ve -> "left side" of the line
     *   +ve -> "right side" of the line
     **/
    return (length(pa-ba*h) - r) * sign(d);
}

float glowBomb(vec2 uv, vec2 A, vec2 B) { 
    float strokeWidth = 0.;//24. * 1./iResolution.x; // Scale by N units of the X resolution
    float line = sdfLine(uv, A, B, strokeWidth);
    float lineSide = step(0., line); // Same as: line < 0. ? 0. : 1.;

    return (1. - smoothstep( abs(line)/1.15, 0.0, .075 )) * lineSide;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord -.5 * iResolution.xy)/iResolution.y;

    float angle = (iTime + 1.) * .08;

    vec2 A = vec2(
         sin(PI * angle * 7.) * radius,
        -sin(PI * angle * 2.) * radius
    );

    vec2 B = vec2(
        -sin(PI * angle * 3.) * radius,
         sin(PI * angle * 2.) * radius
    );

    vec2 C = vec2(
        -sin(PI * angle * 5.) * radius,
         sin(PI * angle * 2.) * radius
    );

    vec2 Z = vec2(
        cos(PI * angle * 2.) * radius * .3,
        sin(PI * angle * 4.) * radius * .3
    );

    vec3 col = vec3(0.);

    float L = glowBomb(uv, Z, A);
    col += L * vec3(.25, .55, 1.);

    L = glowBomb(uv, B, Z);
    col += L * vec3(.75, 1., .88);

    L = glowBomb(uv, C, Z);
    col += L * vec3(1., .65, .3);

    col *= .8;

    // Output to screen
    fragColor = vec4(col,1.0);
}

mat2 rotate2D(in float a) {
    float co = cos(a), si = sin(a);
    return mat2(co, -si, si, co);
}

float sdBox(in vec2 p, in vec2 size) {
    p = abs(p) - size;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y)); // + min(0.0, max(p.x,p.y) to corrent
                                                        // inretior distance which would otherwise
                                                        // be zero
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / iResolution.y;
    float aaWidth = 8.0 / iResolution.y; // Pixel width in normalized coordinates (x2 to make it smoother);

    vec3 color = vec3(1.0);

    uv *= rotate2D(iTime);

    float box = sdBox(uv, vec2(0.3, 0.3));
    box = max(box, -sdBox(uv, vec2(0.15, 0.1))); // "carve" out some of the interior 

    // Use smoothstep(aa width, 0.0, distance) to smoothly blend onto the background
    color = mix(color, vec3(0.0, 1.0, 0.0), smoothstep(aaWidth, 0.0, box));
    color = mix(color, vec3(0.0), smoothstep(aaWidth, 0.0, abs(box) - 0.001)); // edges

    fragColor = vec4(color, 1.0);
}

#define MAXSTEPS 100
#define MAXDISTANCE 100.0
#define HITDISTANCE 0.01
#define SURFACEOFFSET 0.1
#define HSHADOWSAMPLES 8
#define SAMPLINGWEIGHT 0.00390625  
//SAMPLINGWEIGHT == 1 / (HSHADOWSAMPLES * 2 * HSHADOWSAMPLES * 2)
#define SHADOWOFFSET 0.015

float GetDistanceToSphere(vec3 point, vec3 center, float radius)
{
    float distance = length(point - center.xyz) - radius;
    return distance;
}

float GetDistance(vec3 point)
{
    vec4 sphere = vec4(0.0, 1.0, 6.0, 1.0);
    float planeDistance = point.y; //Plane is axis-aligned with y = 0

    float sphereDistance = GetDistanceToSphere(point, sphere.xyz, sphere.w);

    float distance = min(sphereDistance, planeDistance);

    return distance;
}

float RayMarch(vec3 rOrigin, vec3 rDirection)
{
    float distanceFromOrigin = 0.0;

    for(int i = 0; i < MAXSTEPS; ++i)
    {
        vec3 p = rOrigin + rDirection * distanceFromOrigin;
        float distanceToScene = GetDistance(p);
        distanceFromOrigin += distanceToScene;

        if (distanceFromOrigin > MAXDISTANCE || distanceToScene < HITDISTANCE) break;
    }

    return distanceFromOrigin;
}

vec3 GetNormal(vec3 point)
{
    float distance = GetDistance(point);
    vec2 offset = vec2(0.01, 0.0);

    vec3 normal = distance - vec3(GetDistance(point - offset.xyy), GetDistance(point - offset.yxy),  GetDistance(point - offset.yyx));

    return  normalize(normal);
}

float GetLight(vec3 point)
{
    vec3 lightPosition = vec3(3.0 * sin(iTime), 4.5, 5.0 + cos(iTime) * 3.0);
    vec3 lightDirection = normalize(lightPosition - point);
    vec3 surfaceNormal = GetNormal(point);

    float diffuse = clamp(dot(surfaceNormal, lightDirection), 0.0, 1.0);

    float shadow = 0.0;

    //Basic Cone Tracing
    for (int x = -HSHADOWSAMPLES; x < HSHADOWSAMPLES; ++x)
    {
        for (int z = -HSHADOWSAMPLES; z < HSHADOWSAMPLES; ++z)
        {
            float shadowRay = RayMarch(point  + (surfaceNormal * SURFACEOFFSET), vec3(lightDirection.x + (float(x) * SHADOWOFFSET), lightDirection.y, lightDirection.z + (float(z) * SHADOWOFFSET)));
            shadow += float(step(shadowRay, length(lightPosition - point)));
        }
    }

    shadow *= SAMPLINGWEIGHT;

    diffuse -= shadow * 0.9;

    return diffuse;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 rOrigin = vec3(0.0, 5.0, 0.0);
    vec3 rDirection = normalize(vec3(uv.x, uv.y - 0.7, 1.0));

    float d = RayMarch(rOrigin, rDirection);

    vec3 point = rOrigin + rDirection * d;

    float diffuseLight = GetLight(point);

    vec3 col = vec3(diffuseLight);

    // Output to screen
    fragColor = vec4(col,1.0);
}

/* 
To compute the maximum norm distance between a point p and a 
primitive we need to find the size of the smallest 
axis-aligned cube (compared to the size of the smallest 
sphere under Euclidian norm) centered at p that touches the 
surface of the primitive. There are usually three cases that 
need to be handled depending on whether the point that 
touches the surface of the primitive lies on a corner, edge 
or face of the cube.
*/

float circle(vec2 position, float radius) {
    position = abs(position);
    float d = max(position.x - radius, position.y);
    d = min(d, max(position.x, position.y - radius));
    float b = dot(vec2(1.0), position);
    float c = dot(position, position) - (radius * radius);
    float discriminant = b * b - 2.0 * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / 2.0;
        d = min(d, t);
    }
    return d;
}

// alternative geometric version
float circle2(vec2 position, float radius) {
    position = abs(position);
    if (position.x < position.y - radius || position.y < position.x - radius) {
        return max(position.x, position.y) - radius;
    }
    float p = dot(position, vec2(0.5));
    float q = dot(position, position) * 0.5 - p * p;
    float h = sqrt(radius * radius * 0.5 - q); 
    return p - h;
}

float sphere(vec3 position, float radius) {
    position = abs(position);

    // closest point lies on cube face
    float d = max(max(position.x - radius, position.y), position.z);
    d = min(d, max(max(position.x, position.y - radius), position.z));
    d = min(d, max(max(position.x, position.y), position.z - radius));

    // closest point lies on cube edge
    {
        float b = dot(vec2(1.0), position.yz);
        float c = dot(position.yz, position.yz) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.x));
        }
    }

    // closest point lies on cube edge
    {
        float b = dot(vec2(1.0), position.xz);
        float c = dot(position.xz, position.xz) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.y));
       }
    }

    // closest point lies on cube edge
    {
        float b = dot(vec2(1.0), position.xy);;
        float c = dot(position.xy, position.xy) - (radius * radius);
        float discriminant = b * b - 2.0 * c;
        if (discriminant >= 0.0) {
            float t = (b - sqrt(discriminant)) / 2.0;
            d = min(d, max(t, position.z));
        }
    }

    // closest point lies on cube corner
    float b = dot(vec3(1.0), position);
    float c = dot(position, position) - (radius * radius);
    float discriminant = b * b - 3.0 * c;
    if (discriminant >= 0.0) {
        float t = (b - sqrt(discriminant)) / 3.0;
        d = min(d, t);
    }

    return d;
}

// alternative geometric version
float sphere2(vec3 position, float radius) {
    position = abs(position);

    // closest point lies on cube face
    if (position.y < position.x - radius && position.z < position.x - radius) {
        return position.x - radius;
    }
    // closest point lies on cube face
    if (position.x < position.y - radius && position.z < position.y - radius) {
        return position.y - radius;
    }
    // closest point lies on cube face
    if (position.x < position.z - radius && position.y < position.z - radius) {
        return position.z - radius;
    }

    // closest point lies on cube edge
    if (position.x < position.y && position.x < position.z) {
        float p = dot(position.yz, vec2(0.5));
        float q = dot(position.yz, position.yz) * 0.5 - p * p;
        float h = sqrt(radius * radius * 0.5 - q); 
        float a = p - h;
        if (position.x < a) {
           return a;
        }
    }
    // closest point lies on cube edge
    if (position.y < position.x && position.y < position.z) {
        float p = dot(position.xz, vec2(0.5));
        float q = dot(position.xz, position.xz) * 0.5 - p * p;
        float h = sqrt(radius * radius * 0.5 - q); 
        float a = p - h;
        if (position.y < a) {
            return a;
        }
    }
    // closest point lies on cube edge
    if (position.z < position.x && position.z < position.y) {
        float p = dot(position.xy, vec2(0.5));
        float q = dot(position.xy, position.xy) * 0.5 - p * p;
        float h = sqrt(radius * radius * 0.5 - q); 
        float a = p - h;
        if (position.z < a) {
           return a;
        } 
    }

    // closest point lies on cube corner
    vec3 rayDirection = vec3(sqrt(1.0 / 3.0));
    float p = dot(position, rayDirection);
    float q = dot(position, position) - p * p;
    float h = sqrt(radius * radius - q); 
    float c = p - h;
    float a = c * sqrt(1.0 / 3.0);
    return a;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = fragCoord / iResolution.xy;
    position = (position * 2.0) - 1.0;
    position.x *= iResolution.x / iResolution.y;

    vec2 mousePosition = iMouse.xy / iResolution.xy;
    mousePosition = (mousePosition * 2.0) - 1.0;
    mousePosition.x *= iResolution.x / iResolution.y;

    float z = (-1.0 + fract(iTime * 0.2) * 2.0) * 0.6;
    float radius = 0.5;
    // float d = circle(position, 0.5);
    float d = sphere(vec3(position, z), radius);

    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0, 0.9, 1.0) + sign(d) * vec3(-0.3, 0.4, 0.3); // base color
    color *= 1.0 - exp(-3.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(180.0 * d); // ripples
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 0.01, abs(d))); // outline
    fragColor = vec4(color, 1.0);
}

