#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S smoothstep

mat2 Rot(float a) {
    float s = sin(a), c = cos(a);
    return mat2(c, -s, s, c);
}

float Hash21(vec2 p) {
    p = fract(p*vec2(123.34,233.53));
    p += dot(p, p+23.234);
    return fract(p.x*p.y);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

float sdBox2d(vec2 p, vec2 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, p.y), 0.);
}

float GetDist(vec3 p) {
    float r1 = 2.9, r2=.4;
    vec2 cp = vec2(length(p.xz)-r1, p.y);
    float a = atan(p.x, p.z); // polar angle between -pi and pi
    cp *= Rot(a*0.5 + iTime*0.5);
    cp = abs(cp.xy)-.8;

    float d = length(cp)-r2;

    cp *= Rot(a*1.25-iTime*1.0);
    cp.xy = abs(cp.xy)-.235;


    float d2 = length(cp)-0.17;
    d = min(d,d2);

    //cp = abs(cp.xy)-.1;

    //float d = length(cp)-r2;

    return d*0.6;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}


vec3 Bg(vec3 rd) {
	float k = rd.y*.5+.5;

    vec3 col = mix(vec3(.2, .1, .1), vec3(.2, .5, 1), k);
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0);

    vec3 ro = vec3(0.01, 10, 0);
    //ro.yz *= Rot(sin(iTime*.1)*3.1415/2.);
    //ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);

    col += Bg(rd);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
    	vec3 p = ro + rd * d;
    	vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float spec = pow(max(0., r.y), 30.);
    	float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
    	col = mix(Bg(r), vec3(dif), .5)+spec;
        //col = vec3(spec);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

// Trying to mimick infinite zoom of https://www.shadertoy.com/view/fl2SDV
// with the procedural base of "shortest maze 5 (65 chars)" https://shadertoy.com/view/lt2cRR

void mainImage(out vec4 O, vec2 u) {

    float s = .33 + .67* exp2( mod(iTime,2.) ), k = 1., z = 0.;
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y / s, I,V;

    for ( O-=O; z < 5.; U*=3., k*=3., z++ )         // fractal levels
       I = floor(abs(U/1.5)+.5),
       max(I.x,I.y) == 1.?                          // draw maze tile
            V = ( U+z+floor(iTime/2.) ) *16. + 4.,  // offset level seed
            O += mod( V [ int( 1e4*length(ceil(V/8.)) ) % 2 ] , 8. ) *s/k *R.y/72. :O;
}               // heart of base golfed maze                         // 1 pixel width

// Author: paperu
// Title: ancient horology

#define P 6.283185307

float t;
mat2 rot(in float a) { return mat2(cos(a),sin(a),-sin(a),cos(a)); }
float c(vec2 p, float s) { return length(p) - s; }
const int NS = 7;
float S[NS];

float mask(vec2 p) { p = abs(p) - .5; return max(p.x,p.y); }

float a0(vec2 p) { p = abs(p) - 0.320; return max(c(p, 0.300),-c(p + -0.296, 0.658)); }
float a1(vec2 p) { return max(c(p, .35),-c(p + -0.104, 0.250)); }
float a2(vec2 p) { p = -p; return max(c(p, .35),-min(c(p + -0.104, 0.250),c(p + 0.120, 0.114)));}
float a3(vec2 p) { p = p.yx; float px = p.x;p.x = abs(p.x);p.y -= sign(px)*-.1; return max(c(p, 0.230),-c(p +vec2(1,0)*0.096, .25)); }
float a4(vec2 p) { vec2 pp = p; float px = p.x;p.x = abs(p.x);p.y -= sign(px)*-0.1; return min(c(pp,0.068), max(c(p, 0.230),-c(p +vec2(1,0)*0.096, .25))); }
float a5(vec2 p) { vec2 pp = -p*1.624 + 0.172; pp *=rot(step(.24,length(pp))*5.504 + t*P/5.);return min(max(c(pp, .35),-c(vec2(abs(pp))-0.200, 0.210))/1.624, max(c(p, .35),-c(p + -0.104, 0.250))); }
float a6(vec2 p) {
    vec2 pp = p.yx*rot(-0.096)*2. + vec2(-0.250,0.010);
    return min(
        max(abs(c(p -  vec2(0.,.045),0.285)) - 0.042, abs(c(p + vec2(0.,.045),0.285)) - 0.042),
        max(abs(c(pp - vec2(0.,.045),0.285)) - 0.042, abs(c(pp + vec2(0.5,0.),0.277)) - 0.042)
    );
}

float shapesGen(vec2 p) {
    const vec2 s = vec2(0.,1.);
    S[0] = a0(p);
    S[1] = a1(p+s*1.);
    S[2] = a2(p+s*2.);
    S[3] = a3(p+s*3.);
    S[4] = a4(p+s*4.);
    S[5] = a5(p+s*5.);
    S[6] = a6(p+s*6.);

    float d = S[0];
    for(int i = 1; i < NS; i++)
        d = min(d, S[i]);

    return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = (fragCoord.xy - iResolution.xy*.5)/iResolution.y;
    float aa = 1./iResolution.x;

    t = P*.515 + iTime;

    float scale1 = 20.;
    vec2 p1 = st*scale1;
    p1 += t*.5;
    p1 = vec2(mod(p1.x, 1.) - .5, mod(floor(p1.x)*2.5 + p1.y, float(NS)) - float(NS) + .5);
    float d_bg = shapesGen(p1);
    d_bg = abs(d_bg) - .005;
    d_bg /= scale1;

    float scale2 = 5.;
    float nb = 7.;
    vec2 ptemp = st;
    float rot_s = -P/20.;
    ptemp *= rot(t*rot_s);
    float a = floor((atan(ptemp.x,ptemp.y)/P + .5)*nb);
    float a_2 = (a/nb)*P + (P/nb)/2.;
    float kk = (cos(-t*rot_s + a*3.5)*.5+.5)*1.;
    ptemp *= scale2;
    vec2 p2 = ptemp + vec2(sin(a_2),cos(a_2)) *(1.785 - kk*kk*kk*kk*kk*kk*kk*5.);
    p2 *= rot(-t*rot_s);

    float d_ft = shapesGen(p2 + vec2(0.,-a));
    d_ft = max(d_ft,mask(p2));
    d_ft = max(d_ft, -c(ptemp, 1.436));

    p2 *= rot(t*rot_s - a_2);
    float lines = max(abs(p2.x) - .01, -p2.y);
    lines = max(-lines, (abs(c(ptemp, 1.300)) - 0.044));
    d_ft = min(d_ft, lines);

    float d_ft2 = (abs(d_ft + .02)) - 0.01;
    d_ft = max(d_ft, -d_ft2);
    d_ft /= scale2;

    vec2 p = st;

    float S = 1.5;
    p *= S;
    float s = 0.408;
    float d_set = a1(p);
    s = 1.6;
    float pdlm = cos(P*t*.25)*.5;
    p = (p - .1)*s*rot(pdlm);
    d_set = min(d_set, a2(p)/s);
    float t_s = 1.72;
    d_set = min(d_set, a5((p + .1)*t_s*rot(-pdlm))/s/t_s);
    t_s = 4.;
    d_set = min(d_set, a6((p - .12)*t_s*rot(-pdlm))/s/t_s);
    d_set /= S;
    d_set = max(d_set, -(abs(d_set + .005) - 0.002));

    float d = -min(d_bg, (d_ft));
    d = max(d, -d_set);

    vec3 c = d == -d_set ? vec3(1.)
        : d == -d_ft ? mix(vec3(0.880,0.584,0.077),vec3(1.000,0.994,0.000),st.y+.5)
        : vec3(0.208,0.171,0.335);
    vec3 color = smoothstep(-aa,aa, d)*c;

    float l = length(st);
    fragColor = vec4(color - l*l*l*.3,1.0);
}

mat2 rot(float a)
{
	float s = sin(a), c = cos(a);
	return mat2(c, -s ,s, c);
}

//https://www.shadertoy.com/view/Xds3zN
float cube (vec3 p, vec3 s)
{
	vec3 d = abs(p) - s;
	return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}

float scene(vec3 p)
{

	float z = p.z;

	//p.z += time;

	p.z = mod(p.z - 2., 4.) -2.;
	p.x = mod(p.x - 3., 6.) -3.;
	p.xy /= 12.;
	p.xy *= rot(iTime + p.x * .3 * sin(iTime + z));
	p.xy *= 12.;
	p.y = abs(p.y);
	//return cube( p, vec3(1));
	return cube(p , vec3(abs(sin(iTime - p.z * 0.44 + z)), 0., 1.)) - .5;
}

mat3 cam(vec3 E, vec3 l)
{
	vec3 ww = normalize(l - E);
	vec3 uu = normalize(cross(ww, vec3(0,1,0)));
	vec3 vv = cross(uu,ww);

	return mat3(uu,vv, ww);
}


//https://www.shadertoy.com/view/Xds3zN
vec3 norm(vec3 value)
{
    vec3 n = vec3(0.0);
    for( int i=0; i<4; i++ )
    {
        vec3 e = 0.5773*(2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e * scene(value+0.0005*e);
    }
    return normalize(n);
}


vec4 rc(vec3 ro, vec3 rd)
{
	float len = 0.;
	float closest = 50.;
	for(int i = 0; i < 255; i ++)
	{
		vec3 pos = ro + rd * len;
		float dist = scene(pos);
				closest = min(closest, dist);
		if(dist < .001)
			return vec4(ro + rd * len, closest);


		len += dist;

		if(len > 50.)
		return vec4(0,0,0, closest);

	}
	return vec4(0,0,0, closest);
}

vec3 color(vec4 pos, vec3 cam, vec2 uv)
{
	vec3 n = norm(pos.xyz);
	vec3 amb = vec3(1.-uv.y) * 0.2 * vec3( .4, .4, .7);
	vec3 dis = normalize(cam - pos.xyz);
	vec3 diff = (dot(n ,dis)) * vec3(0.1, .6, .7);
	vec3 glow =  1./(1.+clamp(pos.w, 0.,1.) * 10.) * 0.6* vec3(.4,.4,.7);
	vec3 ref = 2.*dot(n, dis) * n  - dis;
	vec3 spec = pow(dot(dis, ref), 3.0) * vec3(0.1,.6,.7);
	spec = clamp ( spec , 0.,1.);
	if(dot (pos,pos) == 0.)
		return amb + vec3(1.,1.,1.) ;

	return diff * pos.x + amb + glow +spec;
}


void mainImage( out vec4 fc, in vec2 uv )
{
	vec2 r = iResolution.xy, u = (uv * 2. - r)/r.y;

	vec3 ro = vec3(0.,10.,0.);

	//ro.xz *= rot(time);

	vec3 rd = cam(ro, vec3(1)) * normalize(vec3(u, 2.));

	vec4 pos = rc(ro,rd);

	fc = vec4(color(pos, ro, u), 1.) ;
}

vec2 pointToUV(vec3 p, vec3 n)
{
    vec3 e1 = normalize(cross(n, vec3(1, 0, 0)));
    if(e1 == vec3(0))
    e1 = normalize(cross(n, vec3(0, 0, 1)));

    vec3 e2 = normalize(cross(n, e1));
    return vec2(dot(e1, p), dot(e2, p));
}

float getShadow(vec3 p, vec2 uv, vec3 lp)
{
    vec3 rd = normalize(lp - p);  
    vec2 texel = vec2(1.0) / iResolution.xy * rd.xy;
    float currDepth = texture(iChannel1, uv).r;
    for(float i = 0.0; i < 256.0; ++i)
    {
        float nextDepth = texture(iChannel1, uv + texel * i).r;
        currDepth += rd.z;
        if(nextDepth > currDepth)
        {
            return .5;
        }
    }


    return 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 uvn = (fragCoord-0.5*iResolution.xy) / iResolution.y;

    float d = texture(iChannel1, uv).r;
    vec3 rd = normalize(vec3(uvn, 1.0));
    vec3 ro = vec3(0, 0, 0);
    vec3 p = ro + rd * d;
    vec3 e = vec3(vec2(1.0) / iResolution.xy, 0);
    float nx = (texture(iChannel1, uv + e.xz).r - texture(iChannel1, uv - e.xz).r) / (2.0 * e.x);
    float ny = (texture(iChannel1, uv + e.zy).r - texture(iChannel1, uv - e.zy).r) / (2.0 * e.y);
    vec3 n = normalize(vec3(-nx, -ny, 1.));
    vec3 lp = vec3(cos(iTime) * 1.5, sin(iTime) * 1.5, 0.1);
    vec3 ld = normalize(lp - p);
    vec3 vd = normalize(ro - p);
    float diff = max(dot(ld, n), 0.0)*2.;
    float spec = pow(max(dot(reflect(-ld, n), vd), 0.0), 4.0) * 0.5;
    float s = getShadow(p, uv, lp);
    vec3 l = vec3(diff + spec + 0.5) * s;
    l = pow(l, vec3(0.4545));
    vec3 kd = texture(iChannel0, uv).rgb;
    vec3 col = kd*vec3(l);
    fragColor = vec4(col + 1. /pow(distance(ld.xy, uvn) * 2.0, 16.),1.0);
}

#define MAX_STEPS 50
#define MIN_DIST .001
#define MAX_DIST 30.
#define EPSILON .002

float SphereSDF (vec3 point, vec3 center, float radius) {
    return distance(point, center) - radius;
}

float Dist (vec3 point) {

    return max(
        SphereSDF(point, vec3(0, 0, 1.), 3. ),
        min(
            max(
                max(
                    max(
                        SphereSDF(point, vec3(0, 0, 1.), .5),
                        -(distance(point.xy, vec2(0)) - .3)
                    ),
                    -(distance(point.xz, vec2(0, 1)) - .3)
                ),
                -(distance(point.yz, vec2(0, 1)) - .3)
            ),
            SphereSDF(point, round(point), .02)
        )
    );
}

vec3 estimateNormal (vec3 ray) {
    float dist = Dist(ray);
    return normalize(
        vec3(
            dist - Dist(vec3(ray.x - EPSILON, ray.y, ray.z)),
            dist - Dist(vec3(ray.x, ray.y - EPSILON, ray.z)),
            dist - Dist(vec3(ray.x, ray.y, ray.z - EPSILON))
        )
    );
}

vec3 RayMarch (vec3 ro, vec3 rd) {

    float distTravelled = 0.;
    rd = normalize(rd);

    for (int i = 0; i < MAX_STEPS; i++) {

        vec3 pointAtEndOfMarchedRay = ro + rd * distTravelled;
        float distToObj = Dist(pointAtEndOfMarchedRay);

        if (distToObj <= MIN_DIST) {
            return pointAtEndOfMarchedRay;
        }

        distTravelled += distToObj;

        if (distTravelled >= MAX_DIST) return vec3(-10000000.);
    }

    return vec3(-100000.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = 2. * vec2(
        (fragCoord.x - .5 * iResolution.x) / iResolution.y, 
        fragCoord.y / iResolution.y - .5
        );
    vec3 ro = vec3(0., 0., 0.);
    //ro.x = uv.x;
    //ro.y = uv.y;

    ro = vec3(sin(iTime), cos(iTime), -.5);
    ro = vec3(3. * uv.x / 5. + sin(iTime), 3. * uv.y / 5. + cos(iTime), 0);


    ro = vec3(
        (iMouse.x - .5 * iResolution.x) / iResolution.y, 
        iMouse.y / iResolution.y, -.5
    );


    vec3 rd = vec3(uv.x, uv.y, 1.);

    vec3 closestPoint = RayMarch(ro, rd);

    vec3 n = estimateNormal(closestPoint);

    vec3 lightCoord = vec3(5. * cos(iTime), 3., 1. + 5. * sin(iTime));

    float lighting = dot(n, lightCoord);

    vec3 baseColor = vec3(.4 * cos(iTime) + .6, 2. * abs(fract(iTime / 3.) - .5), .3 * sin(iTime) + .7);

    baseColor = abs(normalize(closestPoint));

    vec3 col = baseColor + max(30. - distance(closestPoint, lightCoord), 0.) * lighting * .005;


    // Output to screen
    fragColor = vec4(col,1.0);
}

const float pi = acos(-1.);

//https://gist.github.com/companje/29408948f1e8be54dd5733a74ca49bb9
float map_range(float value, float min1, float max1, float min2, float max2) {
  return min2 + (value - min1) * (max2 - min2) / (max1 - min1);
}

//https://gist.github.com/ayamflow/c06bc0c8a64f985dd431bd0ac5b557cd
vec2 rotateUV(vec2 uv, float rotation)
{
    float mid = 0.5;
    return vec2(
        cos(rotation) * (uv.x - mid) + sin(rotation) * (uv.y - mid) + mid,
        cos(rotation) * (uv.y - mid) - sin(rotation) * (uv.x - mid) + mid
    );
}

float map(vec2 uv) {
    int iterations = 15;
    vec2 ouv = uv;

    uv.y += iTime/16.;
    uv = abs(mod(uv, 1.)*4. - 2.);

    for(int i = 0; i < iterations; i ++) {
        float fi = float(i);
        float fit = float(iterations);

        uv = abs(uv - (vec2(0.5 + (fi/fit)*0.6 )));

        uv *= 1.16;

        uv = rotateUV(uv, map_range(ouv.x,-1.,1., 0.2, 5.) );
    }

    return sin(length(uv)*10.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 uv_o = uv;

    //Log polar-tiling -> https://www.osar.fr/notes/logspherical/
    vec2 pos = vec2(log(length(uv)), atan(uv.y, uv.x));
    pos *= 1./pi;
    pos = fract(pos) - 0.5;   
    uv = pos;
    uv.x -= iTime/5. + 5700.;

    //RGB offset
    float offset_range = 0.005;
    float offset = map_range(sin(iTime),-1.,1.,0.2,1.) * offset_range;
    float offset_y = cos(iTime) * offset_range * 0.2;

    float cr = map(uv + vec2(offset, 0.));
    float cg = map(uv + vec2(offset*2., offset_y*2.));
    float cb = map(uv + vec2(offset*3., offset_y*3.));
    vec3 color = vec3(cr, cg, cb)*3.;

    //Fade to black towards center to hide aliasing
    float mask = (1. - pow(length(uv_o),0.96) )*3.;
    color -= mask;

    fragColor = vec4(color,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

    fragColor = vec4(abs(uv.y-1.),abs(uv.y-1.),1.,1.);

    float offset = mod(uv.x - iTime, 1.);

    if ((offset <= 0.45 && offset >= 0.4 && uv.y >= 0.2 && uv.y <= 0.3))
        fragColor = vec4(0.34,0.36,190./255.,1.);
    if ((uv.x <= 1. && uv.x >= 0.0 && uv.y >= 0.0 && uv.y <= 0.2))
        fragColor = vec4(0.9,0.5,19/255,1.);
    if ((uv.x <= 1. && uv.x >= 0.0 && uv.y >= 0.15 && uv.y <= 0.2))
        fragColor = vec4(0.0,0.9,19/255,1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord/iResolution.xy;

	float coord = pow(uv.y * uv.y + uv.x, iTime);

	fragColor = vec4(tan(coord), sin(coord), cos(coord), 1);
}

