#define MAX_ITER 150

float DoBrot(vec2 z, vec2 c)
{
    int iters = 0;
    for( int i=0; i < MAX_ITER; ++i)
    {
        vec2 pw = z*z;
        z = vec2( pw.x - pw.y, 2.0*z.x*z.y ) + c;
        if( length(z) > 2.0 )
            break;
        ++iters;
    }

    return float(iters)/float(MAX_ITER);
}

float GetSampleBrot(in vec2 fragCoord, float scaler, vec2 centre, vec2 z, vec2 offset )
{
    vec2 uv2 = ((fragCoord+offset)-iResolution.xy*0.5)/scaler;
    vec2 c2 =  (uv2) + /*((iMouse.xy-iResolution.xy*0.5)/iResolution.xy)*/ + centre;
    return DoBrot(z,c2); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float scaler = (iResolution.x*pow(2.0,iTime*0.25))*0.2;
    vec2 uv = (fragCoord-iResolution.xy*0.5)/scaler;

    // Time varying pixel color
    vec3 col = vec3(0);

    col.xy = smoothstep(-1.9, -2.0, uv.xy);

    vec2 z = uv;
    z = vec2(0);

    vec2 centre = vec2(0.4027,0.303);
    vec2 c =  (uv) + /*((iMouse.xy-iResolution.xy*0.5)/iResolution.xy)*/ + centre;

    float I = DoBrot(z,c) * 2.0; //centre pixel gets double influence, otherwise its just a blur op

    I += GetSampleBrot(fragCoord, scaler, centre, z, vec2(-0.5,-0.5));
    I += GetSampleBrot(fragCoord, scaler, centre, z, vec2( 0.5,-0.5));
    I += GetSampleBrot(fragCoord, scaler, centre, z, vec2(-0.5, 0.5));
    I += GetSampleBrot(fragCoord, scaler, centre, z, vec2( 0.5, 0.5));

    I *= 0.166666; // div by 5 samples

    col.xyz = vec3( pow(I,4.0) );   
    col.x = ( pow(I,1.0) );   
    col.y = ( pow(I,2.0) );   

    // Output to screen
    fragColor = vec4(col,1.0);
}

float pulse(float time) {
	float x = mod(time*0.3, 1.0); // x will gradually increase from 0 to 1 in cycle
    float y = smoothstep(0.0,0.13,x) - x; // pulsing function

    return 3.0+(-3.0*y); // adjustements
}

float hash(vec2 co){
    return sin( 2355.44 * fract(sin(dot(co.xy ,vec2(12.9898,78.233)))  * 43758.5453));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){


    float strength = 0.35;
    float t = iTime/35.0;

    vec3 col = vec3(0);
    vec2 fC = fragCoord*1.5;

    #ifdef AA
    for(int i = -1; i <= 1; i++) {
        for(int j = -1; j <= 1; j++) {

            fC = fragCoord+vec2(i,j)/2.0;

            #endif

            //Normalized pixel coordinates (from 0 to 1)
            vec2 pos = fC/iResolution.xy;

            pos.y /= iResolution.x/iResolution.y;
            pos = 4.0*(vec2(0.5) - pos);

            for(float k = 1.0; k < 6.0; k+=1.0){ 
                pos.x += strength * sin(2.0*t+k*1.5 * pos.y)+t*0.5;
                pos.y += strength * cos(2.0*t+k*1.5 * pos.x);
            }

            //Time varying pixel colour
            col += clamp(0.25 + 0.5*cos(t*0.5+pos.xyx*3.).xxx, 0.1, 0.99);

            #ifdef AA
        }
    }

    col /= 9.0;
    #endif

    //Fragment colour
    fragColor = vec4(col,1.0);
}

