float h=1e-3;
float pi=3.1415926;
vec2 I=vec2(0.,1.);

vec2 cxMult(vec2 a,vec2 b){
    return vec2(a.x*b.x-a.y*b.y,a.x*b.y+a.y*b.x);
}

float atanz(float a){
    if(a!=a) return pi/2.;
    return atan(a);
}

float atan2(float y,float x){
    if(x>0.) return atan(y/x);
    if(y>=0. && x!=0.) return atan(y/x)+pi;
    if(x==0.){
        if(y>0.) return pi/2.;
        return -pi/2.;
    }
    return atan(y/x)-pi;
}



vec2 cxPow(vec2 a,vec2 b){
    float len=length(a);
    float theta=b.x*atan2(a.y,a.x);
    float phi=b.y*log(len);
    return vec2(cos(theta)*cos(phi)-sin(theta)*sin(phi),
                cos(theta)*sin(phi)+sin(theta)*cos(phi))
        		*pow(len,b.x)*exp(-b.y*atan2(a.y,a.x));
}

vec2 cxPow(vec2 a,float b){
    return cxPow(a,vec2(b,0.));
}

vec2 conj(vec2 z){
    return vec2(z.x,-z.y);
}

vec2 cxDiv(vec2 a,vec2 b){
    return cxMult(a,conj(b))/(pow(b.x,2.)+pow(b.y,2.));
}

vec2 cxExp(vec2 a){
    return exp(a.x)*vec2(cos(a.y),sin(a.y));
}

vec2 cxSin(vec2 a){
    return cxDiv(cxExp(cxMult(I,a))-cxExp(cxMult(-I,a)),2.*I);
}



vec2 f(vec2 x){
    float t=.25*iTime;
    return cxPow(x,10.*sin(t))+vec2(1.,0.);
}

vec2 dfdx(vec2 x){
    return cxDiv(f(x+vec2(h))-f(x-vec2(h)),2.*vec2(h));
}


vec2 newtmeth(vec2 x){
    for(int i=0;i<100;i++){
        x-=cxDiv(f(x),dfdx(x));
    }
    return x;
}


vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.x;
    uv-=vec2(.5,.5*iResolution.y/iResolution.x);
    uv*=pow(2.,2.5);

    vec2 c=newtmeth(uv);

    // Time varying pixel color
    //vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    vec3 col=hsb2rgb(vec3(1.*atan2(c.y,c.x)/pi/2.,1.,1.));
    c=uv;
    if(c.x!=c.x || c.y!=c.y) col=vec3(1.);
    fragColor = vec4(col,1.0);
}
