// https://www.shadertoy.com/view/4tjXWt
// Simple Concrete Material, 2021 by jt
// (using iq's raymarching & candycat' noises)

// https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
#define MOD3 vec3(.1031,.11369,.13787)
//#define MOD3 vec3(443.8975,397.2973, 491.1871)
float hash31(vec3 p3)
{
    p3  = fract(p3 * MOD3);
    p3 += dot(p3, p3.yzx + 19.19);
    return -1.0 + 2.0 * fract((p3.x + p3.y) * p3.z);
}

vec3 hash33(vec3 p3)
{
    p3 = fract(p3 * MOD3);
    p3 += dot(p3, p3.yxz+19.19);
    return -1.0 + 2.0 * fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}

// https://www.shadertoy.com/view/4sc3z2
// Noise Lab (3D) by candycat
float perlin_noise(vec3 p)
{
    vec3 pi = floor(p);
    vec3 pf = p - pi;

    vec3 w = pf * pf * (3.0 - 2.0 * pf);

    return  mix(
                mix(
                    mix(dot(pf - vec3(0, 0, 0), hash33(pi + vec3(0, 0, 0))),
                        dot(pf - vec3(1, 0, 0), hash33(pi + vec3(1, 0, 0))),
                        w.x),
                    mix(dot(pf - vec3(0, 0, 1), hash33(pi + vec3(0, 0, 1))),
                        dot(pf - vec3(1, 0, 1), hash33(pi + vec3(1, 0, 1))),
                        w.x),
                    w.z),
                mix(
                    mix(dot(pf - vec3(0, 1, 0), hash33(pi + vec3(0, 1, 0))),
                        dot(pf - vec3(1, 1, 0), hash33(pi + vec3(1, 1, 0))),
                        w.x),
                    mix(dot(pf - vec3(0, 1, 1), hash33(pi + vec3(0, 1, 1))),
                        dot(pf - vec3(1, 1, 1), hash33(pi + vec3(1, 1, 1))),
                        w.x),
                    w.z),
                w.y);
}

// https://www.shadertoy.com/view/4sc3z2
// Noise Lab (3D) by candycat
float simplex_noise(vec3 p)
{
    const float K1 = 0.333333333;
    const float K2 = 0.166666667;

    vec3 i = floor(p + (p.x + p.y + p.z) * K1);
    vec3 d0 = p - (i - (i.x + i.y + i.z) * K2);

    // thx nikita: https://www.shadertoy.com/view/XsX3zB
    vec3 e = step(vec3(0.0), d0 - d0.yzx);
    vec3 i1 = e * (1.0 - e.zxy);
    vec3 i2 = 1.0 - e.zxy * (1.0 - e);

    vec3 d1 = d0 - (i1 - 1.0 * K2);
    vec3 d2 = d0 - (i2 - 2.0 * K2);
    vec3 d3 = d0 - (1.0 - 3.0 * K2);

    vec4 h = max(0.6 - vec4(dot(d0, d0), dot(d1, d1), dot(d2, d2), dot(d3, d3)), 0.0);
    vec4 n = h * h * h * h * vec4(dot(d0, hash33(i)), dot(d1, hash33(i + i1)), dot(d2, hash33(i + i2)), dot(d3, hash33(i + 1.0)));

    return dot(vec4(31.316), n);
}


#define EPSILON 0.001
#define DIST_MAX 50.0

float halfspace(vec3 p)
{
    return p.z + 1.2;
}

float sphere(vec3 p)
{
    return length(p) - 1.0;
}

float box(vec3 p)
{
    vec3 d = abs(p) - 1.0;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float cylinder( vec3 p )
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(1,1);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float map(vec3 p)
{
    return min(min(box(p+vec3(+1.0,0.0,0.0)), cylinder(p+vec3(-1.0,0.0,0.0))), halfspace(p));
    //return min(min(box(p+vec3(+1.0,0.0,0.0)), sphere(p+vec3(-1.0,0.0,0.0))), halfspace(p));
    //return box(p);
}

// https://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm tetrahedron normals
vec3 normal( vec3 p ) 
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ) + 
                      k.yyx*map( p + k.yyx*h ) + 
                      k.yxy*map( p + k.yxy*h ) + 
                      k.xxx*map( p + k.xxx*h ) );
}

float trace(vec3 ro, vec3 rd)
{
    for(float t = 0.0; t < DIST_MAX;)
    {
        float h = map(ro + rd * t);
        if(h < EPSILON)
            return t;
        t += h;
    }
    return DIST_MAX;
}

// https://iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow( in vec3 ro, in vec3 rd, float mint, float maxt )
{
    for( float t=mint; t<maxt; )
    {
        float h = map(ro + rd*t);
        if( h<EPSILON )
            return 0.0;
        t += h;
    }
    return 1.0;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor );
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

vec3 material(vec3 p)
{
    vec3 color = vec3(0.0);
    vec3 color0 = mix(vec3(0.45), vec3(0.55), simplex_noise(p*vec3(20.0, 20.0, 1.0)));
    //vec3 color0 = mix(vec3(0.4), vec3(0.6), perlin_noise(p*vec3(20.0, 20.0, 1.0)));
    vec3 color1 = mix(vec3(0.4), vec3(0.6), simplex_noise(p*vec3(50.0, 50.0, 50.0)));
    //vec3 color1 = mix(vec3(0.4), vec3(0.6), perlin_noise(p*vec3(50.0, 50.0, 50.0)));
    color = mix(color0, color1, 0.75);
    return color;
}

#define pi 3.1415926

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 ndc = 2.0 * uv - 1.0;
    ndc.x *= float(iResolution.x) / float(iResolution.y);
    float mx = 6.28 * float(iMouse.x) / float(iResolution.x);
    float my = pi / 2.0 + pi / 2.0 * float(iMouse.y) / float(iResolution.y);
    mx = (iMouse.x != 0.0) ? mx : 6.28 * fract(iTime * 0.1);
    my = (iMouse.y != 0.0) ? my : pi / 2.0;

    mat2 R = mat2(vec2(cos(mx), sin(mx)), vec2(-sin(mx), cos(mx)));
    vec3 ro = vec3(0.0, 0.0, -5.0 );//vec3(0.0, -10.0 * my, 0.0);
    //mat2 S = mat2(vec2(0.0, 1.0), vec2(-1.0, 0.0));
    mat2 S = mat2(vec2(cos(my), sin(my)), vec2(-sin(my), cos(my)));
    ro.yz=S*ro.yz;
    ro.xy = R * ro.xy;

    vec3 rd = normalize(vec3(0.5 * ndc.xy, 1.0)); // NOTE: omitting normalization results in clipped edges artifact
    rd.yz=S*rd.yz;
    rd.xy = R * rd.xy;

    float dist = trace(ro, rd);
    vec3 dst = ro + rd * dist;
    vec3 n = normal(dst);

    vec3 lightdir = normalize(vec3(1.0, 1.0, 1.0));
    vec3 ambient = vec3(0.4);
    float brightness = max(dot(lightdir, n), 0.0);
    brightness *= shadow(ro+rd*dist,lightdir, 0.01, DIST_MAX ); // XXX artifacts on cylinder XXX
    //vec3 color = (n * 0.5 + 0.5);
    vec3 color = mix(vec3(0.1), vec3(material(dst)), step(-1.0, dst.z));
    color = (ambient * calcAO(dst, n) + brightness) * color;

    fragColor = mix(vec4(color, 1.0), vec4(0.0), step(DIST_MAX, dist));
    fragColor = sqrt(fragColor); // approximate gamma
}

// SDFs
float sdDisk(in vec2 p, in vec2 o, in float r) {
    return length(p - o) - r;
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

// https://www.shadertoy.com/view/tlcBW2
float udTriangleWave(in vec2 p, in float freq, in float amp) {
    float pw = 1.0 / freq, qw = 0.25 * pw;
    vec2 sc = vec2(2.0 * amp, pw);
    float l = length(sc);
    p.x = abs(mod(p.x + qw, pw) - 0.5 * pw) - qw;
    p *= mat2(sc, -sc.y, sc.x) / l;
    return length(vec2(p.x, max(0.0, abs(p.y) - 0.25 * l)));
}

// mx'' + ux' + kx = 0
float springODE(in float t, in float t0, in float x0, in float v0,
                in float m, in float u, in float k) {

    t -= t0;
    float alpha = -u / (2.0 * m);
    float discr = alpha * alpha - k / m;
    float beta = sqrt(abs(discr));

    if (discr < 0.0) { // Normal oscillation
        float w = beta * t;
        return (x0 * cos(w) - (alpha * x0 - v0) / beta * sin(w)) * exp(alpha * t);
    }

    if (abs(discr) < 1e-3) { // No oscillation - edge case
        return (x0 - (alpha * x0 - v0) * t) * exp(alpha * t);
    }

    // No oscillation
    return (((beta - alpha) * x0 + v0) * exp((alpha + beta) * t) +
            ((beta + alpha) * x0 - v0) * exp((alpha - beta) * t)) / (2.0 * beta);
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 8.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 8.0;
    float unit = 16.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Default before user interaction
    if (ivec2(iMouse.xy) == ivec2(0)) mouse = vec2(2.0 * cos(iTime), sin(0.5 * iTime)) * 2.0;

    // Constants: mass (m), springiness (k), resistance/friction (u)
    float m = 1.0;
    float k = 100.0;
    float u = 0.8;

    // Initial position (x0) and initial velocity (v0)
    float x0 = 3.0;
    float v0 = 0.0;

    // Position vs. time graph
    float y = springODE(iTime - uv.x, 0.0, x0, v0, m, u, k) - 2.0;
    float dx = (springODE(iTime - uv.x + 0.001, 0.0, x0, v0, m, u, k) - 2.0 - y) / 0.001;
    if (uv.x > 0.0 && uv.x < iTime) drawSDF(abs(uv.y - y) / sqrt(1.0 + dx * dx), vec3(1.0, 0.0, 0.0));

    // Spring and weight
    float x = springODE(iTime, 0.0, x0, v0, m, u, k) - 2.0;
    if (uv.y < 8.0 && uv.y > x) drawSDF(udTriangleWave(vec2(uv.y - 4.0, uv.x), 12.0 / (4.0 - x), 0.7), vec3(0.0));
    drawSDF(sdDisk(uv, vec2(0.0, x), 0.5), vec3(0.0, 0.0, 1.0));

    // Vignette
    color *= 1.0 - 2.0 * pow(length(fragCoord / iResolution.xy - 0.5), 4.0);

    fragColor = vec4(color, 1.0);
}

