void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U, P = u/R;
    u.x += float(iFrame);
    U = u / R;

    float h =    P.y > .66 ? hash(U)
               : P.y > .33 ? T(iChannel0) 
               :             T(iChannel1);
    O = vec4 ( iMouse.z > 0. ? step(P.x,h)               // raw thresholding
                             : R.x/64.* ( h - P.x) +.5   // progressive appear
             );             // 10.
    if ( int(u.y)*3 % int(R.y) == 0 ) O = vec4(1,0,0,1); // horizontal separator
}

//reference: https://gam0022.net/blog/2017/03/02/raymarching-fold/

#define PI  3.14159265359
#define PI2 PI * 2.0

vec3 foldX(vec3 p) {
    p.x = abs(p.x);
    return p;
}

mat2 rotate(float a)
{
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

// https://www.shadertoy.com/view/Mlf3Wj
vec2 foldRotate(in vec2 p, in float s) 
{
    float a = PI / s - atan(p.x, p.y);
    float n = PI2 / s;
    a = floor(a / n) * n;
    p *= rotate(a);
    return p;
}


float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

float de1(vec3 p, float s)
{

    float scale = s;
    vec3 size = vec3(.3, 1., .3);
        float d = sdBox(p, size);

        for (int i = 0; i < 8; i++){
           vec3 q = foldX(p);
           q.xy -= size.y * 2.;
           q.xy *= rotate(PI * iTime * 0.1);

           d = min(d, sdBox(p, size * scale));
           p = q;
        }

        d = min(d, sdBox(p, size));
        return d;
}


float map(vec3 p)
{
    vec3 p1 = p;
    p1.xy = foldRotate(p.xy, 3.);
    vec3 p2 = p;
    p2.zy = foldRotate(p.zy, 5.);
    vec3 p3 = p;
    p3.zx = foldRotate(p.zx, 10.);

    return min(de1(p1, 0.8), min(de1(p2, sin(iTime * 0.2) + .4), de1(p3, 1.)));
} 

vec3 doColor(vec3 p)
{
    return vec3(0.,.1,.2);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec3 ro=vec3(0,0,-50);
    ro.zx*=rotate(iTime*0.2);
    ro.zy*=rotate(iTime*0.5);
    vec3 rd=normalize(vec3(uv,2));
    vec3 ta=vec3(0);
    vec3 w=normalize(ta-ro);
    vec3 u=normalize(cross(w,vec3(0,1,0)));
    vec3 v=cross(u,w);
    mat3 lookat=mat3(u,v,w);
    rd=lookat*rd;
    float d,i,t=0.0;
    vec3 p=ro;
    vec3 col=vec3(0);
    for(i=1.0;i>0.0;i-=1./50.0)
    {
        t+=d=map(p);
        if(d<0.0001)
        {
            col+=doColor(p);
            col+=pow(i, 1.);
            break;
        }
        p+=rd*d;
    }
    fragColor=vec4(col,1.0);
}

mat2 getRotationMatrix(float angle) {
    float s = sin(angle);
    float c = cos(angle);

    return mat2(c, -s,
                s, c);
}


vec3 flower(
        vec2 position, 
        vec2 center, 
        float amplitude, 
        float size, 
        float frequency, 
        float blur, 
        float rotation, 
        vec2 skew,
        float index) {

    // by default, flower color will be black, and the background white
    vec3 color = vec3(1.0);
    vec2 figure = position - center;

    // draw the vertical line
    float lineSize = 0.005;

    // 0.1 * sin(5.0 * figure.y) makes the line be "wavy"
    // to flip horizontaly -> - 0.1 * sin(5.0 * figure.y)
    float verticalLine = smoothstep(lineSize, lineSize + lineSize / 2.0, abs(figure.x + 0.1 * sin(5.0 * figure.y)));

    // remove the 'top' part of the vertical line 
    color *= 1.0 - (1.0 - verticalLine) * (smoothstep(0.1, 0.0, figure.y));

    // animation
    mat2 mat = getRotationMatrix(iTime * index * 2.5);
    figure = mat * figure;

    // draw the flower
    figure += skew;
    float r = size + amplitude * cos(atan(figure.y, figure.x) * frequency + rotation);
    color *= smoothstep(r, r + blur, length(figure));

    // apply the colors
    return color;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //vec2 uv = fragCoord/iResolution.xy;
    vec2 uv = fragCoord / iResolution.y - vec2(0.5, 0.0);

    // mirror effect
    // vec2 uv = abs(fragCoord.xy / iResolution.xy - 0.5); 

    // duplicates effect
    // uv = mod(uv * 12.0, 1.0); 

    // diagonal symmetry
    // if (uv.x>uv.y) uv = uv.yx;

    // nice rotation
    // uv += .1*sin(5.*uv-iTime);

    // another cool effect :)
    // uv /= ( .5-dot(uv,uv) );

    vec3 color = mix(vec3(0.1647, 0.8078, 0.3255), vec3(1.0), uv.y);

    // draw all flowers and black and white
    color     *= flower(uv, vec2(0.5, 0.5),  0.1,  0.1, 10.0, 0.01, 0.0, vec2(0.0), -0.6);
    color     *= flower(uv, vec2(0.3, 0.9),  0.1,  0.001, 4.0,  0.01, 0.0, vec2(0.0), 0.2);
    color     *= flower(uv, vec2(0.51, 0.83),  0.01, 0.09, 60.0, 0.01, 0.0, vec2(0.0), 0.3);
    color     *= flower(uv, vec2(0.1, 0.2),  0.2,  0.1, 7.0,  0.01, 0.0, vec2(0.0), -0.4);
    color     *= flower(uv, vec2(0.55, 0.1), 0.05, 0.1, 3.0,  0.01, 0.0, vec2(0.0), 0.5);
    color     *= flower(uv, vec2(0.8, 0.3),  0.05, 0.1, 10.0, 0.01, 0.0, vec2(0.0), -0.6);
    color     *= flower(uv, vec2(0.15, 0.7), 0.01, 0.1, 10.0, 0.01, 0.0, vec2(0.0), 0.7);
    color     *= flower(uv, vec2(0.8, 0.8),  0.03, 0.1, 10.0, 0.01, 0.0, vec2(0.0), 0.8);

    // color them
    color = mix(vec3(1.0, 0.9961, 0.8392), vec3(0.1725, 0.6471, 0.5216), color);

    fragColor = vec4(color, 1.0);
}

vec4 osc(vec2 _st, float frequency, float sync, float offset) {
     vec2 st = _st;
     float r = sin((st.x-offset/frequency+iTime*sync)*frequency)*0.5  + 0.5;
     float g = sin((st.x+iTime*sync)*frequency)*0.5 + 0.5;
     float b = sin((st.x+offset/frequency+iTime*sync)*frequency)*0.5  + 0.5;
     return vec4(r, g, b, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = osc(uv,60.,0.1,1.);
}

void mainImage( out vec4 O, in vec2 I )
{
    vec2 R = iResolution.xy;
    vec2 u = (I-.5*R.xy)/R.y+vec2(.55,-.2);
    float d = texture(iChannel1, .1*u+iTime*.1).r * .024;
    u += d;
    float c = sin(iTime+16.*atan(u.x,u.y));
    c = smoothstep(.1,.1+1.5/R.y, c);
    O = vec4(mix(vec3(1,0,0),vec3(1),vec3(c-smoothstep(.85,.85 +1.5/R.y, 1.-length(u))))-d*2.,1.0);
}

