#define s(sm,p,i,j) smoothstep (p+sm,p-sm,abs(i)+j)

vec3 p(vec2 uv, vec2 l, vec2 sh, float sm, out float a)
{
        float ys = fract(sin(dot(floor(uv),vec2(12.9898,78.233)))*43758.5453123); //random y-shift and color for cell
        vec3 c = .6 + .6 * cos(6.3*(ys) + vec3(0.,23.,21.)); //base color HUE
        ys += (sin(iTime*ys*2.+ys*3.)-1.)*.25; //y-shift moving 
        l.x += sh.x;
        ys += sh.y;

        a = (smoothstep(1.,.5,abs(l.x)+(l.y-ys))*.6 + s(sm,.5,l.x,l.y-ys)*.4)*s(sm,.5,l.x,0.); //alpha mask
        vec3 f = l.y < ys ? c*.6 :vec3(0.); //base color
        f = mix (f,vec3(.9,.9,.6), s(sm,.5,l.x,abs(l.y-ys))); //yellow nib
        f = mix (f,c, s(sm,.2,l.x,abs(l.y-ys-.3))); //color nib
        f *= (1.-abs(l.x)) * (smoothstep(sm,-sm,l.x)+1.5)/2.; //light & shadow 
        return f; 
}

void mainImage( out vec4 C, in vec2 g)
{
    vec2 r = iResolution.xy
        ,uv = ((g+g-r)/r.y+vec2(0.,-iTime*0.3))*vec2(4, 1)*vec2(.75)
        ,l = fract(uv); //cell local coords 
    float  sm = 3./r.y //smoothing factor
            ,a; //alpha

    l.x -= .5;
    l.y *= 4.;

    vec3 f = p(uv+vec2(0.,1.),l,vec2(0.,4.5),sm,a); //top neighbor
    f = mix (f,p(uv+vec2(9.,0.),l,vec2(-.5,2.),sm,a),a); //left neighbor
    f = mix(f,p(uv+vec2(8.,0.),l,vec2(.5,2.),sm,a),a); //right neighbor
    f = mix(f,p(uv,l,vec2(0.,.5),sm,a),a); 
    C = vec4(f,1.);
}

#define S(a, b, t) smoothstep(a, b, t)
#define Sat(a) clamp(a, 0., 1.)

vec3 objcol = vec3(0.);
float ffd1 = 1., ffd2 = 1.;

mat2 Rot(float a){
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c);
}

float Octa(vec3 p, float s){
    p = abs(p);
    return (p.x + p.y + p.z - s) * 0.57735027;
}

float GetDist(vec3 p){
    float t = (iTime*.3) + iMouse.x/iResolution.x;

    p = vec3(p.xy * Rot(t), p.z);
    p.yz *= Rot(t*0.5);
    float r =  0.5;

    float l = S(0.2, 0.8, abs(sin(t))) + ffd1 * 0.2;

    vec3 op = p;

    op.zx *= Rot(t);
    op.xy *= Rot(t*3.5);
    op.yz *= Rot(t*2.2);

    p = abs(p) - l - r;



    float d = length(p) - r;
    //d = max(d, Octa(p,0.6) );

    op = abs(op);
    //d = min(d, min((length(op) - l * 0.3), Octa(op, l * r) ));

    d = min(d,Octa(op, l * r));

    d = min(d, max(length(p.xz)-.1,p.y));
    d = min(d, max(length(p.xy)-.1,p.z));
    d = min(d, max(length(p.yz)-.1,p.x));

    //l = S(0.4, 0.8, abs(sin(t*0.2)));


    //.yz *= Rot(t*3.);

    //op.xz *= Rot(op.y * .5);

    d = min(d, length(vec2(length(op.xz)-(l*4.*ffd1+2.), op.y)) - (l*0.01+0.1) );
    d = min(d, Octa(op-r,0.2));
    d = min(d, length(vec2(length(op.xy)-(l*3.*ffd1+2.), op.z) ) - (l*0.01+0.1));
    //d = min(d, length(vec2(length(op.yz)-(l*2.+2.), op.x) ) - (l*0.01+0.1));


    //q.xz *= Rot(0.1 * q.y);

    vec3 q = op;
    q.xz *= Rot(q.y * 0.5);
    q.xy *= Rot(t*0.5);
    q.yz *= Rot(t*2.3);

    d = min(d, length(vec2(length(q.xy)-(l-r+0.8), q.z)) - (l*0.05+0.01));
    d = min(d, length(vec2(length(q.yz)-(l-r+0.8), q.x)) - (l*0.05+0.01));
    d = min(d, length(vec2(length(q.xz)-(l-r+0.8), q.y)) - (l*0.05+0.01));

    objcol += abs(op);
    return d;
}

vec3 GetNormal(vec3 p){
    vec2 e = vec2(0., .001);
    return vec3(normalize(GetDist(p) - vec3(GetDist(p - e.yxx), GetDist(p - e.xyx),GetDist(p - e.xxy)) ));
}

float Hash21(vec2 p){
    p = sin(p * vec2(135.11, 257.49));
    float n = dot(p, p + 1237.56);
    return fract(n * (p.x * p.y));
}

float march(vec3 ro, vec3 rd){
    float d = 0.;
    vec3 p = ro ;
    for(int i=0; i<100; i++){
        float d1 = GetDist(p);
        d += d1;
        p += rd * d1;      
        if(d > 100. || d1 < 0.01) break;
    }
    return d;
}

float refMarch(vec3 ro, vec3 rd){
    float d = 0.;
    vec3 p = ro ;
    for(int i=0; i<32; i++){
        float d1 = GetDist(p);
        d += d1;
        p += rd * d1;      
        if(d > 100. || d1 < 0.01) return d;
    }
    return 999.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-iResolution.xy*.5)/iResolution.y;
    float m = iMouse.x/iResolution.x;
    vec3 col = vec3(0), bg = vec3(0);
    vec3 ro = vec3(vec2(1,1) * Rot(iTime+m),-8), rd = normalize(vec3(uv,1));
    ffd1 = texture(iChannel0, vec2(32. / 512., 0)).x * 0.5;
    ffd2 = texture(iChannel0, vec2(1. / 512., 0)).x;
    float d = march(ro, rd); //first pass

    if(d < 100.){
        ro += d * rd;
        col += objcol * 0.015;

        rd = reflect(rd, GetNormal(ro));
        ro += rd ;
        d = refMarch(ro, rd);  //second pass(reflection)


        if(d < 100.){
            vec3 p = ro + rd * d;
            vec3 refcol = Sat(dot(GetNormal(p), vec3(0., 0., -1.)) ) * objcol ;
            col += refcol * 0.01;
        }
    }
    else{ 
        bg = abs(uv.x) * vec3(0.3, 0.4, 0.2);
        bg += (1.-(fragCoord/iResolution.xy).y) * vec3(0.3, 0.1, 0.4);
        bg *= ffd2;
    }
    col += S(-5., 5., ro.z) * .2 + bg;
    col = pow(col, vec3(2.2));

    fragColor = vec4(col,1.0);
}

