float deformAlongSin(in vec2 uv, in vec2 center){
    vec2 newUV = normalize(uv - center);
    float amp = 25.0 * cos(iTime);

    float res =  sin(atan(newUV.x, newUV.y) * 25.0) * amp;
    return res;
}

float bias(float a, float t)
{
    return pow(t, log(a) / log(0.5));
}


float deformFlower(in vec2 uv, in vec2 center){
    //vec2 newUV = rotatePoint2d(newUV, center, sin(iTime) * 5.0);
    vec2 newUV = normalize(uv - center);
    float amp = 25.0 * cos(iTime);
    float res = sin(atan(newUV.x, newUV.y) * 10.0);

    res = (res + 1.0) / 2.0;
    res = sqrt(res) * 3.0;
    res = easeInExpo(res);
    return res * amp;
}

