    float a = 0.5;
    vec2 shift = vec2(100.0);
    mat2 rot = mat2(cos(0.5),sin(0.5),-sin(0.5),cos(0.5));
    for(int i = 0;i<NUM_OCTAVES;++i)
    {
        v += a * noise(_st);
        _st = rot * _st * 2.0 + shift;
        a *= 0.5;
    }
    return v;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy*3.0;
    vec3 color = vec3(0.0);

    vec2 q = vec2(0.);
    q.x = fbm(st + 0.00*iTime);
    q.y = fbm(st + vec2(1.0));

    vec2 r = vec2(0.);
    r.x = fbm(st + 1.0*q + vec2(1.7,9.2)+0.15*iTime);
    r.y = fbm(st + 1.0*q + vec2(8.3,2.8)+0.126*iTime);

    float f = fbm(st + r);

    color = mix(vec3(0.101961,0.619608,0.666667),
                vec3(0.666667,0.666667,0.498039),
                clamp((f*f)*4.0,0.0,1.0));

    color = mix(color,
                vec3(0,0,0.164706),
                clamp(length(q),0.0,1.0));

    color = mix(color,
                vec3(0.666667,1,1),
                clamp(length(r.x),0.0,1.0));

    fragColor = vec4((f*f*f+.6*f*f+.5*f)*color,1);
}

const int MAX_STEP = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.001;

/* 一个球的SDF */
float sphereSDF(vec3 samplePoint){
    return length(samplePoint) - 1.0;
}

/* 场景的SDF，目前只是一个球 */
float sceneSDF(vec3 samplePoint){
    return sphereSDF(samplePoint);
}

/* 从眼睛朝某一方向，发出光线，直到击中场景表面，之间的距离 */
float shortestDistanceFromEyeToSurface(vec3 eye, vec3 rayDirection, float start, float end){
    float depth = start;
    for(int i = 0; i < MAX_STEP && depth < end;i++){
        float dist = sceneSDF(eye+depth*rayDirection);
        if(dist < EPSILON) 
            return depth;
        else 
            depth += dist;
    }
    return end;
}
/* 根据视场角以及视口的尺寸，生成到每一个像素的光线方向 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/* 根据SDF的梯度来估计法线方向 
   p 点在SDF表面，n = (dp/dx,dp/dy,dp/dz)

*/
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}
/* 计算 Phong 光照 c = c_r(c_a + c_l*max(0.0,dot(N,L)) + c_l*c_p*pow(max(0.0,dot(R,E)),s))
*/
vec3 PhongLight(vec3 c_r, vec3 c_a, vec3 c_p, vec3 c_l,vec3 eye, vec3 p_l, vec3 p, float s){
    vec3 N = estimateNormal(p);
    vec3 L = normalize(p_l - p);
    vec3 E = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotNL = max(0.0,dot(N,L));
    float dotRE = max(0.0,dot(R,E));
    vec3 c = c_r*(c_a + c_l*dotNL + c_l*c_p*pow(dotRE,s));
    return c;
}
/* 计算光照，在这个函数设定光源参数，然后调用 PhongLight 计算光照
*/
vec3 illumination(vec3 c_r, vec3 c_a, vec3 c_p, vec3 eye, vec3 p, float s) {
    vec3 c_l = vec3(1.0);
    vec3 p_l = vec3(4.0 * sin(iTime),
                          1.0,
                          4.0 * cos(iTime));
    vec3 color = PhongLight(c_r,c_a,c_p,c_l,eye,p_l,p,s);    
    return color;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 dir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.0, 5.0);
