float rand1(in float x) {
    return fract(158789.0*sin(x));
}

float worley_3d(vec3 current, vec3 sizes, int nb_x, int nb_y, int nb_z) {    
    float min_dist = sizes.x+sizes.y+sizes.z;
    int current_xi = int(current.x/sizes.x);
    int current_yi = int(current.y/sizes.y);
    int current_zi = int(current.z/sizes.z);

    for (int xi = current_xi-1; xi < current_xi+2; xi++) {
    for (int yi = current_yi-1; yi < current_yi+2; yi++) {
    for (int zi = current_zi-1; zi < current_zi+2; zi++) {
        vec3 point = vec3(
            sizes.x/float(nb_x)*(float(xi)+rand1(float(xi+yi+zi))),
            sizes.y/float(nb_y)*(float(yi)+rand1(0.789*float(xi+yi+zi))),
            sizes.z/float(nb_z)*(float(zi)+rand1(0.456*float(xi+yi+zi)))
        );
        float dist = distance(current, point);
        if (dist < min_dist) {
            min_dist = dist;
        }
    }
    }
    }

    return min_dist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float depth = min(iResolution.x, iResolution.y);
    vec3 current = vec3(fragCoord.x, fragCoord.y, depth*mod(iTime*0.2, 1.0));
    vec3 sizes = vec3(iResolution.x, iResolution.y, depth);
    float w = worley_3d(current, sizes, 5, 5, 5);
    float light = atan(w/depth)/1.58;
    fragColor = vec4(vec3(light), 1.0);
}

