//raymarching loop
float raymarch(vec3 ro, vec3 rd, out bool hit)
{
    const int MAX_STEPS = 70;
    const float epsilon = 0.5;

    hit = false;
    float totalDist = 0.0;
    for(int i = 0; i < MAX_STEPS; i ++){
        float d = sdf(ro + rd * totalDist);
        if(abs(d) < epsilon){
            hit = true;
            break;
        }
        totalDist += d;
    }
    return totalDist;
}

