    hit = false;
    float totalDist = 0.0;
    for(int i = 0; i < MAX_STEPS; i ++){
        float d = tsdf(ro + rd * totalDist);
        if(abs(d) < epsilon){
            hit = true;
            break;
        }
        totalDist += d;
    }
    return totalDist;
}

