mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float rand(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}
float noise(vec3 p)
{
    vec3 fp = floor(p);
	vec3 fs = fract(p);
    vec3 sf = vec3(1.,0.,0.);

    float a = rand(fp);
    float b = rand(fp + sf);
    float c = rand(fp + sf.yxz);
    float d = rand(fp + sf.xxz);
    float e = rand(fp + sf.yyx);
    float f = rand(fp + sf.xyx);
    float g = rand(fp + sf.yxx);
    float h = rand(fp + sf.xxx);

    vec3 u = smoothstep(0.,1.,fs);
    u = pow(fs,vec3(3.));

    return mix(mix(
        		mix(a,b,u.x),
        		mix(c,d,u.x),u.y),
               mix(
                mix(e,f,u.x),
                mix(g,h,u.x),u.y),u.z
            );
}

float fbm(vec3 p)
{
    float v = 0.;
    float a = .5;
    float f = 0.;
    for(int i=0;i<3;i++)
    {
        v+=a*noise(p);
        p*=2.;
        a*=.5;
    }
    return v;
}

vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;
    vec3 op = p;
    //o = length(p) - 3.;
    o = p.y + 4.;
    o -= noise(p/5. + iTime/13.) * 2.1;
    // o = max(length(p)-2.8,o);

    if(o < 1.)
    {
    	o -= fbm(p*2. + iTime)/1.3;
    }
    // float l = 
    // o = min(o,l);
    o*=.4;
    return vec2(o,id);
}

vec2 march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i< 99 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.01)
        {
            return vec2(depth,d.y);
        }
        depth += d.x;
    }
    return vec2(-depth , 0.);

}

vec3 render(vec3 cp , vec3 rd,vec3 l,vec3 bg)
{
    float depth = 0.;
    vec3 col = vec3(0.);
    float trans = 1.;
    float totaldens = 0.;
    float diff = 0.;
    float emit = 0.;
    vec3 p = cp;
    float t = iTime/2.;
    float tt = fbm(iTime * 12. * vec3(1.));
    for(int i = 0 ; i< 30 ; i++)
    {
        vec2 d = map(p);
        p += rd * 0.03;
   		float dens = 1. - smoothstep(0.,.3,d.x);
        totaldens += dens;
        trans *= dens;
        if(totaldens > 1. && diff <= 0.)
        {
            totaldens = 0.;
            //表面から遠いほど内側
            diff = clamp(1.-map(p).x,0.,1.);
            emit = pow( smoothstep(0.1,.0,map(p).x) ,10.);
            col += step(0.7,tt)*clamp(noise(p + t)-.5,0.,1.) * vec3(.0,.3,.7) * 3.;
        }
        depth += d.x;
    }
    trans = (1. - pow(totaldens/30.,10.5));
    trans = smoothstep(0.,1.,trans);
    emit = mix(emit*trans,1.,.2);
    col = col + vec3(.5);
    col = mix(col,bg,pow(trans,1.25) );
    return col;

}

