// Signed distance to a line segment. Tutorial on how to derive
//
// the formula and code: https://www.youtube.com/watch?v=PMltMdi1Wzg

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm


float udSegment( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 ba = b-a;
    vec2 pa = p-a;
    float h =clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length(pa-1.1*h*ba);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;
    p *= 1.4;
    m *= 1.4;

    vec2 v1 = cos( iTime*0.5 + vec2(0.0,1.00) + 0.0 );
	vec2 v2 = cos( iTime*0.5 + vec2(0.0,3.00) + 1.5 );
    //float th = 0.3*(0.5+0.5*cos(iTime*1.1+1.0));
    float th = 0.1;

	float d = udSegment( p, v1, v2 ) - th;

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-3.0*abs(d));
	col *= 0.8 + 0.2*cos(120.0*d);
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.015,abs(d)) );

    if( iMouse.z>0.001 )
    {
    d = udSegment(m, v1, v2) - th;
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, abs(length(p-m)-abs(d))-0.0025));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, 0.005, length(p-m)-0.015));
    }

	fragColor = vec4(col,1.0);
}

#define SC (250.0)

float noise(in vec2 uv)
{
    return sin(uv.x/2.)+cos(uv.y*2.);
}


#define OCTAVES 4
float fbm(in vec2 uv,int octaves)
{
    //this function generates the terrain height
    float value = 0.;
    float amplitude = .75;
    float freq = 1.;
    float n1 = 0.;
    for (int i = 0; i < octaves; i++)
    {
        n1 = (noise(uv * freq-n1)-n1);
        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = abs(value-(n1) * amplitude);

        amplitude *= .37;

        freq *= 2.;

        uv = uv.yx;
        float f1 = .1*noise(uv);
        value *= .9+f1;
        freq += f1;

        //value *= .9;

}

    return value;
}

float fbm(in vec2 uv){
    return fbm(uv,OCTAVES);
}

float f(in vec3 p,int iters)
{   
    float h = fbm(p.xz,iters);
    return h;
}

float f(in vec3 p)
{   
    float h = fbm(p.xz,OCTAVES+4);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos,OCTAVES);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
} 

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(-.8, .15, -.3));

    vec3 camStep = vec3(lightDir.x, 0., lightDir.z) * (iTime)/4.;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;
    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    ro.y += fbm(ro.xz,OCTAVES)-1.5;

    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));

    //if (length(iMouse.xy) > 40.0) {
        rd.yx *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
        rd.xz *= rot(3.14-iMouse.x/iResolution.x*3.14*2.0);
    //}

    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

#define MAX_STEPS 10
#define MAX_DIST 100.
#define SURF_DIST 1.

float myLength(vec3 p) {
    float theta = atan(p.y, p.x);
    float beta = atan(p.y, p.z);
    float x = 0.5 * (1. + cos(beta + 0.3 * iTime));
    float z = 0.5 * (1. + sin(theta + 0.3 * iTime));
    x = 16. * x * x * (1.-x) * (1.-x);
    z = 16. * z * z * (1.-z) * (1.-z);
    return sqrt(pow(p.x + x, 2.) + p.y * p.y + pow(p.z + z, 2.));

}

float sdCapsule(vec3 p, vec3 a, vec3 b, float r) {
    vec3 ab = b - a;
    vec3 ap = p - a;

    float t = dot(ab, ap) / dot(ab, ab);
    t = clamp(t, 0., 1.);
    //t = 0.8 * t + 0.2 * 4. * t * (1.-t);
    vec3 c = a + t * ab;
    float d = myLength(p - c) - r;
    return d;
}

float GetDist(vec3 p) {
    vec4 s = vec4(0, 1.3 + 0.2 * cos(iTime), 6, 1);
    float sphereDist = length(p - s.xyz) - s.w;
    float planeDist = p.y;

    float b = 0.5 * (1. + cos(0.5 * iTime));

    float cd = sdCapsule(p, vec3(-cos(-iTime),1.5,6. + sin(-iTime)), vec3(cos(-iTime),1,6.-sin(-iTime)), .8);
   // float cd2 = sdCapsule(p, vec3(-sin(-iTime),1,6. + cos(-iTime)), vec3(sin(-iTime),1.5,6.-cos(-iTime)), .8);


    float d = min(cd, planeDist);
    return d;
}


float RayMarch(vec3 ro, vec3 rd) {
    float dO = 0.;

    for (int i = 0; i < MAX_STEPS; i++) {
        vec3 p = ro + 1.4 * dO * rd;
        float dS = GetDist(p);
        dO += dS;
        if (dO>MAX_DIST || dO<SURF_DIST) break;
    }
    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p);
    vec2 e = vec2(.01,0);

    vec3 n = d - vec3(
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));

    return normalize(n);
}

float GetLight(vec3 p) {
    vec3 lightPos = vec3(0, 7. + 3. * cos(0.45 * iTime), 3);
    vec3 lightPos2 = lightPos;
    vec2 v = 6. * vec2(cos(0.4 * iTime), sin(0.4 * iTime));
    lightPos.xz += v;
    lightPos2.xz -= v;
    vec3 l = normalize(lightPos - p);
    vec3 l2 = normalize(lightPos2 - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n,l), 0., 1.);
    float dif2 = clamp(dot(n,l2),0.,1.);
    //dif = 16. * dif * dif * (1.-dif) * (1.-dif);
    dif *= dif;
    dif2 *= dif2;
    float d = RayMarch(p + n * SURF_DIST * 2., l);
   // if (d<length(lightPos-p))
     //   dif *= .1;

    return dif + dif2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) /iResolution.y;

    vec3 col = vec3(0);

    vec3 ro = vec3(0,1.,0);
    vec3 rd = normalize(vec3(uv.x, uv.y, 1));

    float d = RayMarch(ro,rd);

    vec3 p = ro - 2. * rd * d;

    float dif = GetLight(p); 
    dif = 16. * dif * dif *(1.-dif) * (1.-dif);
    col = vec3(dif);


    fragColor = vec4(col,1.0);
}

#define MAX_DIST 20.0

const float rad1 = 1.0;
const float rad2 = 0.075;

float squircle_toroid(in vec3 pt, in float radius1, in float radius2, in float squish) {
  vec3 pows = pt * pt;
  pows = pows * pows;
  float radial = sqrt(sqrt(pows.x + pows.z)) - radius1;
  radial = radial * radial;
  radial = radial * radial;
  return sqrt(sqrt(squish * radial + pows.y)) - radius2;
}

float conventional_smin(in highp float a, in highp float b, in highp float k) {
  float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
  return mix( b, a, h ) - k*h*(1.0-h);
}

float super_smooth_max(in highp float a, in highp float b, in highp float k) {
  // return 0.25 * k * log(exp(4.0 * a / k) + exp(4.0 * b / k));
  return 0.1732802*k*log2(exp2(5.771*a/k) + exp2(5.771*b/k));
}

float super_smooth_min(in float a, in float b, in float k) {
  return -super_smooth_max(-a, -b, k);
}

float checked_smooth_min(in highp float a, in highp float b, in highp float k) {
  float val = conventional_smin(a, b, k);
  if (abs(val) < 4.0 * k) {
    return super_smooth_min(a, b, k);
  }
  return val;
}


float sdf(in vec3 pt) {
  float v1 = squircle_toroid(pt, rad1, rad2, 0.5);
  float v2 = squircle_toroid((-pt - 0.84 * vec3(1.25 * rad1, 0.0, 1.25 * rad1)).yzx +
                             vec3(0.25 * rad1, 0.25 * rad1, 0.0),
                             0.125 * rad1,
                             rad2,
                             4.0);
  return checked_smooth_min(v1, v2, 0.2 * rad1);
}

float rayCast(in vec3 orig, in vec3 dir) {
  float along = 0.0;

  for(int i = 0; i < 256; ++i) {
    vec3 p = orig + along * dir;
    float dist = sdf(p);
    if (along > MAX_DIST || dist < 1.0e-5) {
      return along;
    }
    along += 0.75 * dist;
  }
  return along;
}

vec3 sdfGrad(in vec3 pt) {
  const float h = 0.001;
  const float inv_h = 1000.0;
  float v = sdf(pt);
  return inv_h * vec3(sdf(pt + vec3(h, 0.0, 0.0)) - v,
                      sdf(pt + vec3(0.0, h, 0.0)) - v,
                      sdf(pt + vec3(0.0, 0.0, h)) - v);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 1.0 / min(iResolution.x, iResolution.y);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0 * fragCoord - iResolution.xy) * scale;

    vec3 dir = normalize(vec3(uv, -3.0));
    vec3 orig = vec3(0.0, 0.0, 4.0);

    const mat3 pitch = mat3(1.0, 0.0, 0.0,
                            0.0, 0.8, -0.6,
                            0.0, 0.6, 0.8);

    float ct = cos(iTime);
    float st = sin(iTime);
    mat3 yaw = mat3(ct, 0.0, st,
                    0.0, 1.0, 0.0,
                    -st, 0.0, ct);

    dir = pitch * yaw * dir;
    orig = pitch * yaw * orig;
    vec3 bounce = dir;

    float blend = 0.25;
    float d = rayCast(orig, dir);

    if (d < MAX_DIST) {
      vec3 pt = orig + d * dir;
      vec3 norm = normalize(sdfGrad(pt));
      blend = 1.0;
      bounce = reflect(dir, norm);
    }

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * bounce;

    col = mix(vec3(0.5), col, blend);

    // Output to screen
    fragColor = vec4(col,1.0);
}

