#define R iResolution
#define plotBelt(c1,c2, func) { vec3 fn =func;  dist=min(dist,fn.x); ldist+= (fn.x<=0.? fn.y*fn.z : fn.z); if(tdist==0. && fn.x<0.) tdist =ldist; \
    col = mix(mix(c1,c2,fn.y),col,smoothstep(0.,.004,fn.x));  }
#define plotGear(sw, c1,c2,pos, func) {float fn = func; dist=min(dist,fn); \
    col = mix((sw?c1:c2)* (sw? angle(pos,vec2(sin(-iTime/r),cos(-iTime/r))): angle(pos,vec2(sin(iTime/r),cos(iTime/r))))/2./PI ,col,smoothstep(0.,.004,fn)); }

#define PI 3.1415
const int N= 8; //number of nodes 
//#define RANDOM //randomize nodes

//------------------------------------

// line/line intersection (Iapafoto)
vec2 intersect(vec2 p0, vec2 n0, vec2 p1, vec2 n1) {
    float d = n0.x*n1.y-n0.y*n1.x; 
    if(d==0.) return vec2(0);
    vec2 p = (p0-p1)/d;
    return vec2(p.y*n1.x-p.x*n1.y, p.y*n0.x-p.x*n0.y); 
}

// line/circle intersection
vec2 intersect(vec2 p0, vec2 n, vec2 center, float r) {
    vec2 c=p0-center;
    float b= dot(n,c),
          d = sqrt(b*b +r*r - dot(c,c));
     return vec2( -b+d,-b-d);    
}
//angle between normals
float angle(in vec2  p,in  vec2 n){
    vec2 c= p *mat2(n.x,n.y,-n.y,n.x);
    float a= atan(c.y,c.x);
    if(a<0.)  a= 2.*PI +a;  
    return a;
}

//based on Iq SDF function
float sdArc( in vec2 p, in vec2 sca, in vec2 scb, in float ra, in float rb )
{   
    p *= mat2(sca.x,sca.y,-sca.y,sca.x);
    p.x = abs(p.x);
    if (scb.y*p.x>scb.x*p.y) {
        vec2  b = scb*(ra+rb), a=scb*(ra-rb),pa=p-a, ba = b-a;
        float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
        return length( pa - ba*h );
    }
    return abs( length(p)-ra) -rb;
}

// arc with normals as parameters
//type=0: ordered arc, =1: always shorter, -1= always longer
vec3 sdArcExt( in vec2 p, in vec2 na, in vec2 nb, in float ra,in float rb,bool swap,bool longer)
{  
    if(swap) {vec2 temp=na;na=nb;nb=temp;}
    float an =  angle(p ,na),at=angle(nb,na) ;      
    vec2 ta =  normalize(na+nb) *mat2(0,1,-1,0) *sign(na.x*nb.y - na.y*nb.x),  
         tb =  na *mat2(ta.x,ta.y,-ta.y,ta.x); 
    if(tb.x<0.) tb.x=-tb.x;
    return vec3(sdArc(p,ta,tb,ra,rb),(swap? 1.-  an /at : an / at), at*ra );
}

vec3 sdSegmentExt( in vec2 p, in vec2 a, in vec2 b , in float tk)
{
    vec2 n=normalize(b-a), l= vec2(length(b-a)*.5,tk);
    p=(p-a)*mat2(n.x,n.y,-n.y,n.x) -vec2(l.x,0);
    vec2 d = abs(p)-l;
    return vec3(length(max(d,0.0)) + min(max(d.x,d.y),0.0),p.x/l.x/2.+.5,l.x*2.);
}

//segment-arc-segment
vec4 roundCorner( vec2 a, vec2 b, vec2 c, float r)
{   
    //segment ab
    vec2 nab= normalize(b-a),
         tab=vec2(nab.y,-nab.x),
         pab=r* tab *sign(dot(c-a,tab));
    //parallel ab on c side
    vec2 a1 = a + pab;

    //segment bc
    vec2 nbc= normalize(c-b),
         tbc= vec2(nbc.y,-nbc.x),
         pbc= r* tbc *sign(dot(a-c,tbc));
    //parallel bc on a side
    vec2 b1 = b + pbc;  

    //intersection
    vec2 d = a1 + intersect(a1,nab,b1,nbc).x*nab;  //center of inscribed circle

    //arc-segment intersection
    vec2 e = d  - pab;  //intersection arc, segment ab
    vec2 f = d  - pbc;  //intersection arc, segment cb

    float de= distance(e,b),df=distance(f,b);
    return vec4(de,df,d);             
}

//segment-arc-arc-segment
vec4 roundCorner2( vec2 a, vec2 b, vec2 c, float r){
    vec2 nab= normalize(b-a),
         tab=vec2(nab.y,-nab.x),
         pab=r* tab *sign(dot(c-a,tab));
    vec2 d = b + pab;
    vec2 nbc= normalize(b-c),
         tbc= vec2(nbc.y,-nbc.x),
         pbc= r* tbc *sign(dot(a-c,tbc));
    vec2 b1 = b -  pbc,
         e = b1  + nbc*  intersect(b1,nbc,d,2.*r).y;      
    return vec4( d ,  e);
}   

//segment-arc-arc-arc-segment
vec4 roundCorner3( vec2 a, vec2 b, vec2 c, float r){
    vec2 nab= normalize(b-a),
         tab=vec2(nab.y,-nab.x),
         pab=r* tab *sign(dot(c-a,tab));
    vec2 a1 = b - pab,
         e = a1  + nab*  intersect(a1,nab,b,2.*r).y; 
    vec2 nbc= normalize(b-c),
         tbc= vec2(nbc.y,-nbc.x),
         pbc= r* tbc *sign(dot(a-b,tbc));
    vec2 c1 = b -  pbc,
         f = c1  + nbc*  intersect(c1,nbc,b,2.*r).y;     
    return vec4( e ,  f);
}  

vec2 hash21(float p)
{
	vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
	p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
vec2 getK(int i){
        float s= float(i*i)/float(N*N) +.05;
#ifdef RANDOM        
        vec2 k =hash21(float(i))*.8-.4;
#else        
        const vec2[8] KK = vec2[8](vec2(.4,.4), vec2(0,0), vec2(-.3,.4), vec2(-.4,0),vec2(-.2,-.4), vec2(0,-.4), vec2(0.2,0.1), vec2(.3,-.3)) ;  
        vec2 k= KK[i];
#endif        
        k.x*=R.x/R.y;
        k +=vec2(sin(iTime*(s-.3) +s  +5.),cos(iTime*(s-.3)+s +5.))*.07 ;
        return k;
}
