void mainImage( out vec4 O, in vec2 U )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = (-R.xy*.5 + U)/R.y *1.;
    vec3 col = vec3(0.25);  

    float r =iMouse.z>0.? iMouse.y/R.y *.07 +.04 : .08,
          tk=iMouse.z>0.? iMouse.x/R.x *.02 : .01;

    float dist=1e5,tdist=0.,ldist=0.; //SDF distance, cumulated path distance, local path distance

    for(int i=min(iFrame,0);i<N;i++)
    {
        //b = node, a and c = polyline mid points
        vec2 b=getK((i+1)%N), a=(getK(i)+b)/2., c=(getK((i+2)%N)+b)/2.;

        //control point:
         col = mix(vec3(0),col,smoothstep(0.,.002,-tk+ length(p-b)));

        // try to use 1 arc
        vec4 v = roundCorner(a,b,c, r);      
        if((i%2==1)&&  v.x<distance(a,b) && v.y < distance(c,b)) {
            //ok is compatible, draw it                        
            vec2 nab=normalize(b-a),
                 ncb=normalize(c-b),
                 e= b - v.x*nab,
                 f= b + v.y*ncb,
                 d=v.zw;

            bool swap =nab.x*ncb.y>nab.y*ncb.x ;
            plotGear(swap, vec3(.2,.3,.7), vec3(.2,.5,.3), p-d, tk-r  + length(p-d));
            plotBelt(vec3(.8),vec3(8.,0,.8), sdSegmentExt(p,a,e,tk));                      
            plotBelt(vec3(1,0,1),vec3(1,0,0),  sdArcExt(p-d,normalize(e-d),normalize(f-d),r,tk,!swap,false));           
            plotBelt(vec3(.8,0,0),vec3(.8), sdSegmentExt(p,f,c,tk));            
        } else {
            // try to use 2 arcs 
            //vec2 b=kj, a=(ki+b)/2., c=(km+b)/2.;            
            //if(  distance(c,b)<distance(a,b)) {a=(ki+b)/2.; c=(km+b)/2.;}

            vec4 v2 = roundCorner2(  a,b,c,  r);
            vec2 nab=normalize(b-a),
                 nbc=normalize(b-c),
                 a1=v2.xy,
                 c1=v2.zw,
                 f=(a1+c1)/2.,
                 a2=b,
                 c2=b+ intersect(b,nbc,c1,vec2(nbc.y,-nbc.x)).x*nbc;

            if( distance(b,c)>distance(b,c2) ){
                bool swap = nab.x*nbc.y>nab.y*nbc.x;
                plotGear(!swap, vec3(.2,.3,.7), vec3(.2,.5,.3), p-a1, tk-r  + length(p-a1));
                plotGear(swap, vec3(.2,.3,.7), vec3(.2,.5,.3), p-c1, tk-r  + length(p-c1));                
                plotBelt(vec3(.8),vec3(.8,0,.8),  sdSegmentExt(p,a,a2,tk));                                         
                plotBelt(vec3(1,0,0),vec3(1,0,1), sdArcExt(p-a1,normalize(a2-a1),normalize(f-a1),r,tk,swap,true));
                plotBelt(vec3(1,0,1), vec3(1,1,0), sdArcExt(p-c1,normalize(f-c1),normalize(c2-c1),r,tk,!swap,false));
                plotBelt(vec3(.8,.8,0), vec3(.8), sdSegmentExt(p,c2,c,tk));

            }else{
                // try to use 3 arcs
                //vec2 b=kj, a=(ki+b)/2., c=(km+b)/2.;
                vec4 v2 = roundCorner3(  a,b,c,r);
                vec2 nab=normalize(b-a),
                     nbc=normalize(b-c),
                     e = v2.xy,
                     f = v2.zw,                
                     a1=(e+b)/2.,
                     c1=(f+b)/2.,
                     a2= b+ intersect(b,nab,e,vec2(nab.y,-nab.x)).x*nab,
                     c2= b+ intersect(b,nbc,f,vec2(nbc.y,-nbc.x)).x*nbc;       

                  if(distance(a,b)>distance(b,a2) && distance(b,c)>distance(b,c2)){
                    //ok is compatible, draw it 
                    bool swap = nab.x*nbc.y>nab.y*nbc.x;

                    plotGear(swap, vec3(.2,.3,.7), vec3(.2,.5,.3), p-e, tk-r  + length(p-e));
                    plotGear(swap, vec3(.2,.3,.7), vec3(.2,.5,.3), p-f, tk-r  + length(p-f));
                    plotGear(!swap, vec3(.2,.3,.7), vec3(.2,.5,.3), p-b, tk-r  + length(p-b));
                    plotBelt(vec3(.8),vec3(.8,.8,.0), sdSegmentExt(p,a,a2,tk));                               
                    plotBelt(vec3(1,1,0),vec3(1,0,1), sdArcExt(p-e,normalize(a2-e),normalize(a1-e),r,tk,!swap,false));
                    plotBelt(vec3(1,0,1),vec3(1,0,0), sdArcExt(p-b,normalize(a1-b),normalize(c1-b),r,tk,swap,false));                               
                    plotBelt(vec3(1,0,0),vec3(1,1,0), sdArcExt(p-f,normalize(c1-f),normalize(c2-f),r,tk,!swap,false));
                    plotBelt(vec3(.8,.8,0.),vec3(.8), sdSegmentExt(p,c2,c,tk));                
                  }else{
                    //segments too short - skip rounding
                    plotBelt(vec3(.8),vec3(8.,0,0), sdSegmentExt(p,a,b,tk));
                    plotBelt(vec3(.8,0,0),vec3(.8), sdSegmentExt(p,b,c,tk));
                  }
              }
        }
    }

     if(tdist>0.) col+=  smoothstep(0.1,.15, mod(iTime-tdist, .3));
     if(tdist==0.) col +=vec3(dist*1.);

     if(iMouse.z>0.){
         col = vec3(0.9,0.6,0.3); if(dist<0.) col= vec3(1,1,tdist*10.);
        col *= 1.0 - exp(-3.0*abs(dist));
        col *= 0.8 + 0.2*cos(250.0*dist);
        col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,0.005,abs(dist)) );
     }  
    // Output to screen
    O = vec4(col,1.0);
}

float hash31(vec3 p) {
    float q = dot(p,vec3(170.1,110.7,100.6));
	return fract(sin(q)*480.5453);
}

float fbm(vec3 p) {
    float f = 0.;
    f += hash31(floor(p*2.5+100.)) * .1;
    f += hash31(floor(p*2.+200.)) * .5;
    f += hash31(floor(p*1.+200.)) * 1.;
    f += hash31(floor(p/2.+200.)) * .5;
    f += hash31(floor(p/4.+400.)) * .25;
    f += hash31(floor(p/8.+400.)) * .25;
    f += hash31(floor(p/16.+400.)) * .25;
    return f / 3.65;
}
vec3 calcNormal(vec3 p) {
    float d = .4;
    return normalize(vec3(
        fbm(p+vec3(d,0.,0.)) - fbm(p-vec3(d,0.,0.)),
        fbm(p+vec3(0.,d,0.)) - fbm(p-vec3(0.,d,0.)),
        fbm(p+vec3(0.,0.,d)) - fbm(p-vec3(0.,0.,d))));
}

// The ray is rotated along z axis while marching, which gives a slight spiral.
vec3 rot(vec3 x, float t) {
    return normalize(vec3(x.x-x.y*t, x.y+x.x*t, x.z));
}
vec3 map(vec3 ro, vec3 rd) {
    float t = 0.9, d = 10.;
    for (int it=0; it<53; it++) {
        vec3 rp = ro + t*rd;
        rd = rot(rd, .023);
        d = fbm(rp);
        t += d * .992;
    }
    return ro + t * rd;
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( 6.28318*(c*t+d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x += fract(sin(uv.x*250.3+uv.y*400.)*280.24) * .00051;

    vec3 col = vec3(0.);



    //vec3 ro = vec3(cos(iTime), sin(iTime) ,-2.5 + iTime * .71);
    vec3 ro = vec3(0.,0. ,-2.5 + iTime);
    //vec3 ro = vec3(iTime,iTime ,-2.5 + iTime);

    vec3 rd = normalize(vec3((uv-.5)*vec2(1.0,iResolution.y/iResolution.x),.075));

    if (iMouse.z > .5) {
        float pitch = (iMouse.y/iResolution.y-.5)*3., yaw = (iMouse.x/iResolution.x-.5)*3.;
        rd = mat3(1., 0., 0.,   0., cos(pitch), -sin(pitch),   0., sin(pitch), cos(pitch)) *
             mat3(cos(yaw), 0., -sin(yaw),   0.,1.,0.,  sin(yaw),0.,cos(yaw)) * rd;
    }

    vec3 rp = map(ro,rd);
    float d = length(rp-ro);

    float v = clamp(d * .034, 0.,1.);

    col.rgb = clamp(pow(v,9.)*(10.*pal(v*v, vec3(0.0,0.0,0.0),vec3(0.5,0.50,0.98),vec3(-1.,.50,.0),vec3(0.10,0.72,0.1))),0.,1.);
    col.rgb += .8 * clamp(v*pal(v, vec3(0.01,0.0,0.07),vec3(0.9,0.0,0.999),vec3(.85,.0,.65),vec3(0.4,0.0,0.5) ),0.,1.);

    vec3 n = calcNormal(rp);
    col.rgb += clamp(v*v*pow(abs(n.z),3.) * vec3(.05,.716,.91) * 1.2, 0.,1.);
    col.rgb += clamp(v*pow((n.y-.1),5.) * vec3(.75,.7216,.01) * .97, 0.,1.);
    col.r = pow(col.r,1.25);
    //col.g = pow(col.g,2.5);
    //col.b = pow(col.b,2.5);

    fragColor = vec4(col,1.0);
}

