// Render section //
////////////////////

vec2 castRay(vec3 ro, vec3 rd, int octaves) {
	const float p = 0.001;
	float t = 0.0;
	float h = p * 2.0;
	float m = -1.0;
	for (int i=0; i<36; i++) {
		if (abs(h)<=p || t>=28.0) break;
        t += h;
        vec2 res = map(ro + rd*t, octaves);
        h = res.x;
        m = res.y;
	}
	if (t>28.0) m = -1.0;
	return vec2(t, m);
}

vec3 calcNormal(vec3 p, int octaves) {
	const vec3 eps = vec3(0.002, 0.0, 0.0);
	return normalize( vec3(map(p+eps.xyy, octaves).x - map(p-eps.xyy, octaves).x,
			       map(p+eps.yxy, octaves).x - map(p-eps.yxy, octaves).x,
			       map(p+eps.yyx, octaves).x - map(p-eps.yyx, octaves).x) );
}

float shadows(vec3 ro, vec3 rd, float tMax, float k, int octaves) {
    float res = 1.0;
	float t = 0.1;
	for(int i=0; i<22; i++) {
        if (t>=tMax) break;
        float h = map(ro + rd*t, octaves).x;
        res = min( res, k*h/t );
        t += h;
    }
    return clamp(res, 0.2, 1.0);
}

vec3 render(vec3 ro, vec3 rd) {
	const int geoLOD = 4;

	vec2 res = castRay(ro, rd, geoLOD);

	vec3 lPos = normalize(vec3(1.0, 0.5, 0.0));
	vec3 lCol = vec3(1.0, 0.9, 0.8);

	vec3 pos = ro + rd*res.x;

	// mat -1 = Background / sky
    vec3 color = vec3(0.45,0.5,0.6);
    float sun = clamp(dot(rd,lPos),0.0,1.0);
    color += 0.6 * lCol * sun*sun;
    if (res.y < -0.5) {
		return color;
	}

    vec3 skyColor = color;

	int norLOD = int(max(2.0, 12.0-11.0*res.x/28.0));
	vec3 nor = calcNormal(pos, norLOD);

	// mat 0 = Rock / mountain
	if (res.y>-0.5 && res.y<0.5) {
		// Base rock
		color = mix( vec3(0.4, 0.1, 0.0), vec3(0.7, 0.6, 0.3), step(0.9, nor.y) );

		// Layer noise
		float n = 0.5*(snoise(pos.xy*vec2(2.0, 15.0))+1.0);
		color = mix( vec3(0.6, 0.5, 0.4), color, n*smoothstep(0.0, 0.7, 1.0-nor.y) );

        // Sand on top
        color = mix(color, vec3(0.7, 0.6, 0.3), smoothstep(0.0, 0.2, nor.y-0.8));
	}
	// mat 1 = Sand
	if (res.y>0.5) {
		// Base sand and rock color
		color = mix( vec3(0.3, 0.2, 0.0), vec3(0.7, 0.6, 0.3), nor.y );
	}

    // Lighting and shadows
    float lAmb = clamp( 0.5 + 0.5 * nor.y, 0.0, 1.0);
    float lDif = clamp( dot( nor, lPos ), 0.0, 2.0);

    if (lDif>0.05) lDif *= shadows(pos, lPos, 8.0, 12.0, geoLOD);

    color += (0.4*lAmb) * lCol;
    color *= (1.8*lDif) * lCol;	

	// Fog
	float fog = exp(-0.003 *res.x*res.x);
	color = mix(skyColor, color, fog);

	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 pos = 2.0 * ( fragCoord.xy / iResolution.xy ) - 1.0;
	pos.x *= iResolution.x / iResolution.y;
    float t1 = iTime;
	// Camera
	float x = 0.0 + (0.5*t1);
	float y = 0.0;
	float z = 0.0 + sin(0.1*t1)*2.;
	vec3 cPos = vec3(x, y, z);
	cPos.y = terrain(cPos.xz, 1) + 2.5;

	const vec3 cUp = vec3(0., 1., 0.);
	vec3 cLook = vec3(cPos.x + 1.0, cPos.y*0.85, 0.0);

	// Camera matrix
	vec3 ww = normalize( cLook-cPos );
	vec3 uu = normalize( cross(ww, cUp) );
	vec3 vv = normalize( cross(uu, ww) );

	vec3 rd = normalize( pos.x*uu + pos.y*vv + 2.0*ww );

	// Render
	vec3 color = render(cPos, rd);

	fragColor = vec4( color, 1.0 );
}

/* source: https://iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm */
float sdPolygon(in vec2 p, in vec2[6] v) {
  const int num = v.length();
  float d = dot(p-v[0],p-v[0]);
  float s = 1.0;
  for( int i=0, j=num-1; i<num; j=i, i++ ) {
    // distance
    vec2 e = v[j] - v[i];
    vec2 w =    p - v[i];
    vec2 b = w - e*clamp( dot(w,e)/dot(e,e), 0.0, 1.0 );
    d = min( d, dot(b,b) );

    // winding number from http://geomalgorithms.com/a03-_inclusion.html
    bvec3 cond = bvec3( p.y>=v[i].y, 
        p.y <v[j].y, 
        e.x*w.y>e.y*w.x );
    if( all(cond) || all(not(cond)) ) s=-s;  
  }

  return s*sqrt(d);
}

float hex(vec2 p, float s) {
  p.x *= 0.57735*2.0;
  p.y += mod(floor(p.x), 2.0)*0.5;
  p = abs((mod(p, 1.0) - 0.5));
  return smoothstep(1.0, 0.1, abs(max(p.x*1.5 + p.y, p.y*2.0) - 1.0) / (0.38 + s));
}

float inv_lerp(float from, float to, float value){
  return max(0.0, min(1.0, (value - from) / (to - from)));
}

/* source: https://easings.net */
float easeOutCirc(float x) {
  return sqrt(1.0 - pow(x - 1.0, 2.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = fragCoord/iResolution.xy;
  float hp = 1.0 - easeOutCirc(mod(iTime, 50.0) / 50.0);

  vec2[] polygon = vec2[](
    vec2(0.97, 0.97),
    vec2(0.13, 0.97),
    vec2(0.03, 0.32),
    vec2(0.73, 0.32),
    vec2(0.77, 0.03),
    vec2(0.97, 0.03)
  );
  for (int i = 0; i < polygon.length(); i++)
    polygon[i] = (polygon[i] + vec2(0, 3)) / vec2(1, 5);

  float d = sdPolygon(uv, polygon);

  float cd = 0.7*(1.0-smoothstep(0.01,0.02, abs(d))) + (1.0-smoothstep(-0.03,0.03, abs(0.02 - d)));
  // cd = step(0.4, cd)*cd;
  vec3 c = vec3(cd) * vec3(0.575, 0.5, 0.7);
  float lowhp = inv_lerp(0.3, 0.27, hp);
  float h = hex(fragCoord * 0.04, lowhp * 0.2 * abs(sin(7.0*iTime)));
  float hpx = (1.15 * (1.0 - uv.x)) - 0.08;
  vec3 cbase = mix(vec3(1.0, 0.0, 1.0-0.8*lowhp), vec3(0.4, 0.2, 0.4), inv_lerp(hp-0.03, hp, hpx));
  c += float(!(c.x > 0.0)) * max(0.0, -d)/0.06 * cbase * h;

  fragColor = vec4(vec3(c*0.7), 1.0-sign(d));
}

// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 64
#define MAX_DIST 64.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define Slice(p,a) dot(p,vec2(cos(a),sin(a)))
#define S(p,d,b) smoothstep(fwidth(p.y),0.0,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define EDGE_WIDTH 0.003
#define MATERIAL_BODY 0
#define MATERIAL_SKELTON 1
#define MATERIAL_EYE 2
#define MATERIAL_BOX 3

#define ZERO (min(iFrame,0))

float edge;

// IQ's 3D noise function. It's faster than the FBM and enough for me to design.
// The compile is also faster.
float noise3d( vec3 p )
{
	const vec3 s = vec3(27, 111, 57);
	vec3 ip = floor(p); p -= ip; 
    vec4 h = vec4(0., s.yz, s.y + s.z) + dot(ip, s);
    p = p*p*(3. - 2.*p); 
    //p *= p*p*(p*(p*6. - 15.) + 10.); // option
    h = mix(fract(sin(h) * 43758.545), fract(sin(h + s.x) * 43758.545), p.x);
    h.xy = mix(h.xz, h.yw, p.y);
    return mix(h.x, h.y, p.z); 
}

// by Dave_Hoskins
float hash12(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

// https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCappedCylinder( vec3 p, float h, float r )
{
  vec2 d = abs(vec2(length(p.yz),p.x)) - vec2(h,r);
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float B3D(vec3 p, vec3 s) {
    p = abs(p)-s;
    return max(max(p.x,p.y),p.z);
}

float dTaperBox(vec3 p, vec3 b, float angle, float bottomDist) {
    vec3 prevP = p;
    float d = B3D(p,b);
    float a = radians(angle);
    p.x = abs(p.x);
    p.x-=bottomDist;    
    d = max(Slice(p.xy,a),d);

    p = prevP;
    a = radians(angle);
    p.z = abs(p.z);
    p.z-=bottomDist;
    d = max(Slice(p.zy,a),d);

    return d;
}

float leg(vec3 p){
    vec3 prevP = p;
    float d = B3D(p,vec3(0.06,0.09,0.07));

    float a = radians(45.0);
    p.zy = abs(p.zy);
    p.zy-=0.065;    
    d = max(Slice(p.zy,a),d);

    p = prevP;
    a = radians(30.0);
    p.xz = abs(p.xz);
    p.xz-=0.055;    
    d = max(Slice(p.xz,a),d);

    p = prevP;
    p.xy = abs(p.xy);
    p.xy-=0.055;    
    d = max(Slice(p.xy,a),d);    

    return d;
}

vec2 drawLeg(vec3 p){
    vec3 prevP = p;

    p.z*=0.7;
    p.x=abs(p.x);
    p.x-=0.028;
    float d = dTaperBox(p,vec3(0.021,0.035,0.14),60.0,0.1);
    p = prevP;

    p.zy = abs(p.zy);
    p.zy-=0.2;    
    float maskd = max(Slice(p.zy,radians(-42.0)),B3D(prevP-vec3(0.0,-0.03,0.0),vec3(0.1,0.02,0.2)));

    d = max(-maskd,d);
    vec2 res = vec2(d, MATERIAL_SKELTON);

    p = prevP;
    p.yz*=Rot(radians(10.0));
    d = leg(p-vec3(0.0,0.12,0.01));

    p = prevP;
    p.yz*=Rot(radians(-20.0));
    p*=vec3(0.8,0.7,0.8);

    float d2 = leg(p-vec3(0.0,0.21,-0.07));

    d = min(d,d2);

    p = prevP;
    d2 = sdCappedCylinder(p-vec3(0.0,0.21,-0.01),0.035,0.08);
    d = min(d,d2);

    p.yz*=Rot(radians(20.0));
    d2 = B3D(p-vec3(0.0,0.17,-0.04),vec3(0.015,0.08,0.01));
    d = min(d,d2);

    vec2 res2 = vec2(d, MATERIAL_BODY);
    return combine(res,res2);
}

float sholuder(vec3 p){
    vec3 prevP = p;
    float d = B3D(p,vec3(0.13,0.1,0.07));

    float a = radians(50.0);
    p.zy = abs(p.zy);
    p.zy-=0.075;    
    d = max(Slice(p.zy,a),d);

    p = prevP;
    p.xz = abs(p.xz);
    p.xz-=0.085;    
    d = max(Slice(p.xz,a),d);

    p = prevP;
    a = radians(30.0);
    p.x+=0.12;    
    d = max(-Slice(p.xy,a),d);    

    p = prevP;
    p.z = abs(p.z);
    p.z-=0.065;
    d = max(-B3D(p-vec3(0.07,0.0,0.0),vec3(0.01,0.06,0.02)),d); 

    return d;
}

float armWithSheild(vec3 p){
    vec3 prevP = p;
