    p.xz-=0.06;    
    d = max(Slice(p.xz,a),d);

    p = prevP;
    p.xy = abs(p.xy);
    p.xy-=0.055;    
    d = max(Slice(p.xy,a),d);    

    p = prevP;
    float d2 = B3D(p-vec3(-0.075,0.0,0.05),vec3(0.01,0.05,0.12));

    p.y = abs(p.y);
    p.y-=0.03;
    d2 = max(-B3D(p-vec3(-0.082,0.0,0.05),vec3(0.005,0.005,0.13)),d2);

