float hand(vec3 p){
    vec3 prevP = p;
    float d = B3D(p,vec3(0.02,0.04,0.04));

    float a = radians(-120.0);
    p.xz+=0.01;    
    d = max(Slice(p.xz,a),d);

    p = prevP;
    d = max(-B3D(p-vec3(0.015,0.0,0.01),vec3(0.01,0.05,0.015)),d);

    return d;
