vec2 drawArm(vec3 p){
    vec3 prevP = p;
    float d = sholuder(p);

    vec2 res = vec2(d, MATERIAL_BODY);

    float x = 0.01;
    d = B3D(p-vec3(x,-0.15,0.0),vec3(0.04,0.1,0.035));

    float d2 = B3D(p-vec3(x,-0.25,-0.03),vec3(0.04,0.04,0.065));
    d = min(d,d2);
    d2 = sdCappedCylinder(p-vec3(x,-0.24,0.0),0.06,0.05);
    d = min(d,d2);

    vec2 res2 = vec2(d, MATERIAL_SKELTON);

    d = armWithSheild(p-vec3(x,-0.25,-0.165));
    vec2 res3 = vec2(d, MATERIAL_BODY);

    d = hand(p-vec3(x,-0.25,-0.298));
    vec2 res4 = vec2(d, MATERIAL_SKELTON);

    return combine(res,combine(res2,combine(res3,res4)));
}

