vec2 GetDist(vec3 p) {
    vec3 originalP = p;

    p.x += sin(iTime*2.2)*0.12;
    p+=noise3d(p*300.0)*0.001;
    vec3 prevP = p;

    p.x = abs(p.x);
    p.x-=0.12;
    vec2 dleg = drawLeg(p-vec3(0.0,-0.42,-0.05));
    p = prevP;

    p.x = abs(p.x);

    p.x-=0.29;
    p.x*=-1.0;
    vec2 dCannon = drawCannon(p-vec3(0.0,0.42,0.03));
    vec2 darm = drawArm(p-vec3(0.0,0.28,0.0));
    p = prevP;
    vec2 dBody = drawBody(p-vec3(0.0,0.1,0.0));
    vec2 dhead = drawHead(p-vec3(0.0,0.39,0.015));

    p = originalP;
    vec2 dBox = drawBox(p-vec3(0.0,-0.47,0.0));

    vec2 model = combine(combine(dleg,darm),combine(dBody,dhead));
    return combine(combine(model,dCannon),dBox);
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    float lastDistEval = 1e10; 
    float dist;
    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dist = dS.x;
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(stepnum == MAX_STEPS){
            if (lastDistEval < EDGE_WIDTH && dist > lastDistEval + 0.0005) {
                edge = 1.0;
            }
            if (dist < lastDistEval) lastDistEval = dist;
        }

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}

// https://www.shadertoy.com/view/3lsSzf
float calcOcclusion( in vec3 pos, in vec3 nor )
{
	float occ = 0.0;
    float sca = 1.0;
    for( int i=ZERO; i<4; i++ )
    {
        float h = 0.01 + 0.15*float(i)/4.0;
        vec3 opos = pos + h*nor;
        float d = GetDist( opos ).x;
        occ += (h-d)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 2.0*occ, 0.0, 1.0 );
}

vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    float occ = calcOcclusion(p,n);
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,2,-2));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow*occ;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff*occ;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff*occ;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 20.)*occ; // spec

    return diffCol;
}

float bodyPaint(vec2 p, float sc, float flip){
    vec2 uv = fract(p)-0.5;
    vec2 id = floor(p);

    float rand = hash12(id);
    float lineW = 0.14;

    if(rand<0.5 || rand>=0.8){
        float dir = (rand>=0.8)?1.0:-1.0;
        uv*=Rot(radians(dir*45.0*flip));
        uv.x = abs(uv.x);
        uv.x-=0.355;
        lineW = 0.1;
    }

    lineW*=sc;
    float d = max(-(uv.x+(lineW*0.5)),(uv.x-(lineW*0.5)));
    return d;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    vec3 np = p;
    float xanim = sin(iTime*2.2)*0.12;
    if(mat == MATERIAL_BODY){
        np.x += xanim;
        float d = bodyPaint(np.xy*30.0,1.0,1.0);
        float nn = noise3d(np*30.0)*0.2;
        float sn = hash12(np.xy*100.0)*0.1;

        vec3 bcol = mix(vec3(0.3,0.4,0.3)+nn-sn,vec3(0.1,0.2,0.1),S(p.xy,d,0.0));
        col = diffuseMaterial(n,rd,p,bcol);
    } else if(mat == MATERIAL_SKELTON) {
        np.x += xanim;
        float nn = noise3d(np*100.0)*0.1;
        float sn = hash12(np.xy*100.0)*0.1;
        col = diffuseMaterial(n,rd,p,vec3(0.2)+nn+sn);
        col*=1.5;
    } else if(mat == MATERIAL_BOX) {
        np.z-=iTime*1.5;
        vec3 prevNp = np;
        float nn = noise3d(np*5.0)*0.08;
        float sn = hash12(np.xz*100.0)*0.1;
        vec3 bcol = vec3(0.6,0.65,0.7)+nn+sn;

        np.z= mod(np.z,0.8)-0.4;
        float d = B(np.xz,vec2(0.02,0.2));
        bcol = mix(bcol,vec3(1.0),S(p.xy,d,0.0));
        np.x = abs(np.x);
        np.x-=1.5;
        d = B(np.xz,vec2(0.02,0.4));
        bcol = mix(bcol,vec3(1.0),S(p.xy,d,0.0));

        col = diffuseMaterial(n,rd,p,bcol);
    } else if(mat == MATERIAL_EYE){
        col = diffuseMaterial(n,rd,p,vec3(0.5,0.0,0.0));
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m = iMouse.xy/iResolution.xy;

    float handShakeY = noise3d(vec3(iTime*2.0,iTime*2.1,iTime*2.2))*0.15;
    float handShakeX = noise3d(vec3(iTime*2.1,iTime*2.2,iTime*2.1))*0.3;
    #if USE_MOUSE == 1
    handShakeY = 0.0;
    handShakeX = 0.0;
    #endif

    vec3 ro = vec3(handShakeX, handShakeY, 1.3);
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-10.0));
    if(iMouse.z>0.){
        ro.xz *= Rot(-m.x*6.2831);
    } else {
        ro.xz *= Rot(radians(180.0+iTime*20.0));
    }
    #endif

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(1.0);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
        int mat = int(d.y);
        col = materials(mat,n,rd,p,col);
        col = mix(col,vec3(0.6),edge);
        col *= exp( -0.038*d.x*d.x*d.x );//fog
    } else {
        rd.z-=iTime*1.2;
        // dust effect
        float c = noise3d(rd) * 0.5 + noise3d(rd * 5.0) * 0.25 + noise3d(rd * 10.0) * 0.1;
        uv.y-=0.07;
        col = mix(vec3(0.3,0.3,0.1)+(c*0.2),vec3(0.0),-uv.y+1.0);   
    }

    // POST EFFECTS
    float cline = mod(fragCoord.y, 4.0) < 2.0 ? 0.5 : 1.0;

    // gamma correction
    col = pow( col, vec3(0.9545) );    

    fragColor = vec4(col,1.0)*(cline*1.5);
}

float N11(float t) {
	return fract(sin(t*10234.324)*123423.23512);
}
vec3 N31(float p) {
    //  3 out, 1 in... DAVE HOSKINS
   vec3 p3 = fract(vec3(p) * vec3(.1031,.11369,.13787));
   p3 += dot(p3, p3.yzx + 19.19);
   return fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}
float N12(vec2 p)
{	// Dave Hoskins - https://www.shadertoy.com/view/4djSRW
	vec3 p3  = fract(vec3(p.xyx) * vec3(443.897, 441.423, 437.195));
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

