vec3 N13(float p) {
    p=p*SEED;
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx + 33.33);
return fract((p3.xxy+p3.yzz)*p3.zyx);
}

mat2 rotate2D(float r){
    return mat2(cos(r), sin(r), -sin(r), cos(r));
}


float map01(float min,float max, float val){
    return val=clamp((val-min)/(max-min),0.,1.);
}



vec4 getSphere(float n){
    float nb=n;
    float t=iTime/ANIM_SPEED;
    vec3 p=N13(nb);
    nb=nb*p.x;
    float b=4.;
    float z=-6.+3.*(sin(t*2.+b*float(nb))*0.5+.5);
    float w=min(b/1.5,1.2)*pow((1.-(-z-3.)/3.),2.);
    return vec4(p.x*1.5+cos(z*3.+n)*.4,p.y*1.5+sin(z*3.+n)*.4,z,w*1.2);

}

//from https://www.iquilezles.org/www/articles/smin/smin.htm
vec2 sminN( float a, float b, float k, float n )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    float m = pow(h, n)*0.5;
    float s = m*k/n; 
    return (a<b) ? vec2(a-s,m) : vec2(b-s,m-1.0);
}

float computeId(vec3 p,vec3 rep){

    return dot(floor((rep*.5+p)/rep),vec3(1.,10.,100.));

}


Hit getDist(vec3 p){
    float fact=.8;

    vec3 orip=p;

    vec3 rep=vec3(3.*fact,3.*fact,15.);
    float idOffset;


    p=mod(p+rep/2.,rep)-rep/2.;



    float dist=MAX_DIST;
    int objId=-1;
    int sphereId=0;


   vec3 col=vec3(0.);


    for(float i=-1.;i<=1.;i++){
        for(float j=-1.;j<=1.;j++){

            vec3 p1=vec3(orip.x+i*rep.x,orip.y+j*rep.y,orip.z);

            idOffset=computeId(p1,rep);
            vec4 sphere=getSphere(idOffset);
            sphere.x=sphere.x+i*rep.x;
            sphere.y=sphere.y+j*rep.y;

            float d1=length(p-sphere.xyz)-sphere.w;
            //float d1=sdBox(p-sphere.xyz,vec3(sphere.w*fact));
            col+=N13(idOffset)*pow(max(min(1.-d1,1.),0.),3.);

            dist=sminN(dist,d1,2.*fact,4.).x;
        }
    }


    objId=int(idOffset);

    dist*=.5;
    return Hit(dist,vec4(objId,col),vec3(0),vec3(0));
}



vec3 getNormal(vec3 pos){
    vec2 e=vec2(.05,0);
    float dist=getDist(pos).dist;
    vec3 n=vec3(
        dist-getDist(pos-e.xyy).dist,
        dist-getDist(pos-e.yxy).dist,
        dist-getDist(pos-e.yyx).dist);
    return normalize(n);
}

Hit rayMarch(vec3 o,vec3 ray){
    float totalDist=0.;
    Hit hit;
    for(int i=0;i<MAX_STEP;i++){
        vec3 p=o+totalDist*ray;
        hit=getDist(p);
        totalDist+=hit.dist*2.*(.5+map01(0.,60.,totalDist));
        if(hit.dist<MIN_DIST||totalDist>MAX_DIST) break ;
    }
    if(totalDist<MAX_DIST){
        vec3 pos=o+ray*totalDist;
        return Hit(totalDist,hit.objId,pos,getNormal(pos));
    }
    else{
        return Hit(totalDist,vec4(-1),vec3(0),vec3(MAX_DIST));
    }

}

vec3 shadePixel(vec3 pos, vec3 n, Light light,vec3 viewDir){

    float intensity=1./pow(length(pos-light.pos),2.)*light.intensity;
    float diffuse=dot(normalize(light.pos-pos),n);
    float shadow=1.;//softshadow( pos, light.pos, 0.01, 10., 5.);
    diffuse=clamp(diffuse,0.,1.)*shadow;


    vec3 r=normalize(light.pos-viewDir-pos);
    float spec=pow(max(dot(n, r),0.), 90.)*shadow;

    float ret=diffuse;
    ret +=  spec;

    return ret*intensity*light.color;
}

