vec3 computeLighting(vec3 hitPos,vec3 n,vec3 ray){

  bool disableLight1=texelFetch( iChannel1, ivec2(KEY_LEFT,2),0 ).x>0.;
    bool disableLight2=texelFetch( iChannel1, ivec2(KEY_RIGHT,2),0 ).x>0.;
    bool disableLight3=texelFetch( iChannel1, ivec2(KEY_UP,2),0 ).x>0.;
float zoffset=getzoffset();

Light lights[]=Light[](Light(vec3(getmousePos()*20.,30.-zoffset),disableLight3?0.:100.,vec3(1.,1.,1.)),
                           Light(vec3(-8,3.,8.-zoffset),disableLight1?0.:100.,vec3(1.,.8,0.)),
                           Light(vec3(8,-3.,8.-zoffset),disableLight2?0.:150.,vec3(0.,0.,1.)));


    vec3 col=vec3(0.);
    for(int i=0;i<3;i++){
        vec3 shadingColor=shadePixel(hitPos,n,lights[i],ray);

        col+=shadingColor;
   }
   return col;
}



vec4 render(inout vec3 ro, inout vec3 ray,inout float fresnel, out bool doreflect){



    Hit hit=rayMarch(ro,ray);

    ro=hit.pos+hit.normal*0.003;
    ray=reflect(ray,hit.normal);
    fresnel=1.-dot(hit.normal,ray);

    vec4 colSphere;

    //only compute reflexion on close objects
    if(hit.dist<15.)
        doreflect=true;

    if(hit.objId.y!=-1.){
        vec3 col=computeLighting(hit.pos,hit.normal,ray);
        vec3 colsp=hit.objId.yzw;
        colSphere = vec4(col.xyz*colsp,1.0);

    }

    return colSphere;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


     vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    float t=iTime/ANIM_SPEED;

    vec2 mousePos=getmousePos();

    float zoffset=t*10.;

