    vec3 camera=vec3(mousePos*8.,30.-zoffset);//-20.*(1.+sin(t)));
    vec3 lookAt=vec3(mousePos*4.,-zoffset);
    float zoom=1.;
    vec3 f=normalize(camera-lookAt);
    vec3 r=cross(vec3(0,1.,0),f);
    vec3 u=cross(f,r);

    vec3 c=camera-f*zoom;
    vec3 i=c+uv.x*r+uv.y*u;
    vec3 ray=normalize(i-camera);

    bool doreflect=false;

    float fresnel=0.;
    fragColor=render(camera,ray,fresnel,doreflect);
    if(doreflect){
    float savefresnel=fresnel;
    fragColor+=render(camera,ray,fresnel,doreflect)*pow(map01(.3,.7,savefresnel),3.);
    }
   //gamma correction
    fragColor.r=lin2sRGB(fragColor.r);
    fragColor.g=lin2sRGB(fragColor.g);
    fragColor.b=lin2sRGB(fragColor.b);


}

