#define NUM_LAYERS 5.0

/**
 * create a rotation matrix for the given angle in radians
 */
mat2 getRotationMatrix(float angle) {
    float s = sin(angle);
    float c = cos(angle);

    return mat2(c, -s,
                s, c);
}

/**
 * random that returns float in range [0.0, 1.0]
 */
float random(vec2 pos) {
    pos = fract(pos * vec2(123.34, 456.21));
    pos += dot(pos, pos + 45.32);
    return fract(pos.x * pos.y);
}

/**
 * scale a range [min,max] to [a,b]
 * f(x) = (b-a)*(x - min)/(max - min) + a
 */
float convertRange(float minimal, float maximal, float a, float b, float x){
    return (b-a)*(x - minimal)/(maximal - minimal) + a;
}

/**
 * draw a drop :)
 */
float createDrop(vec2 uv, float width) {

    float soft = 0.0; //width / 0.5;

    // draw a centered vertical line
    float drop = smoothstep(0.5 + width, 0.5 + width + soft, uv.x) + smoothstep(uv.x, uv.x + soft, 0.5 - width);

    drop = (1.0 - drop) * (step(0.5, uv.y));

    // make it white
    return drop;
}

vec3 createLayer(vec2 uv){

    vec3 result = vec3(0);

    // create multiple copies of the screen
    vec2 copies = fract(uv);
    vec2 index = floor(uv);

    // create a drop
    float drop = createDrop(copies, 0.005);

    // create a random color
    float rnd = random(index);
    vec3 randomColor = sin(vec3(0.349, 0.4039, 1.0) * rnd);

    // set drop color
    result = mix(result, randomColor, drop);

    return result;
}

