/**
 * entry point
 */
void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    // normalize coordinates
    vec2 uv = fragCoord.xy / iResolution.xy;
    float time = iTime * 3.0;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // set background color
    vec3 color = mix(col, vec3(0.0), sqrt(uv.y));

    // rotate the rain ;)
    mat2 matr = getRotationMatrix(10.0 * 3.14 / 180.0);
    uv = (matr * (uv - 0.5)) + 0.5;

    for(float i=0.0; i<1.0; i += 1.0 / NUM_LAYERS) {
        float depth = fract(i) + 0.5;

        // make depth in the required range
        float scale = mix(15.0, 5.0, depth);

        // create one time
        vec2 bbb = vec2(0.0, time * depth);
        vec3 layer = createLayer((uv + bbb) * scale);
        color = mix(color, vec3(1.0, 1.0, 1.0), layer);
    }

    fragColor = vec4(color, 1.0);
}

#define bridthness 1.2

vec3 point(float dist, float phase, float speed){
    float r = abs(cos(radians(iTime*speed + phase)))*1.5/dist ;
    float g = abs(cos(radians(iTime*speed+90. + phase)))*1.25/dist;
    float b = abs(cos(radians(iTime*speed+45. + phase)))*2./dist;
    return vec3(r, g, b);
}



