#define PI 3.1415926538

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float zoom = cos(iTime)*5.0+2.5;
    vec2 uv = fragCoord.xy/iResolution.xy*zoom-zoom/2.0;
    float n = cos(0.03*iTime)*4.0+4.0;
    float d = sin(0.03*iTime)*4.0+4.0;

    float r = 0.0;
    float a = 3.0;
    float x = 0.0;
    float theta = atan(uv.y, uv.x);

    for(int i=0; i<15; i++) {
        r = a*(cos(n/d*theta));
        theta += PI *2.0;
        float temp = abs(length(uv) - r);
        x += 1.0 - smoothstep(-3.5,3.5, temp / fwidth(length(uv) - r));       
    }

    fragColor.r = sin(x*7.0+sin(iTime*0.8))*.5+.5;
    fragColor.g = sin(x*7.0+sin(iTime*0.6))*.5+.5;
    fragColor.b = sin(x*7.0+sin(iTime*0.6))*.5+.5;

    fragColor.rgb = vec3(fragColor);
}

// http://www.iquilezles.org/www/articles/palettes/palettes.htm 
vec3 cosPalette( float t , vec3 brightness, vec3 contrast, vec3 osc, vec3 phase)
{

    return brightness + contrast*cos( 6.28318*(osc*t+phase) );
} 


void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    //vec2 uv = squareFrame(iResolution.xy, fragCoord);

    vec2 uv = fragCoord/iResolution.xy * 2.0 -1.;
    uv *=2.0;
    uv.x *= iResolution.x/iResolution.y;
    // heart equation from a comment from iq https://www.shadertoy.com/view/XdcyW8
    //https://www.wolframalpha.com/input/?i=%28x%5E2%2B%281.2*y-sqrt%28abs%28x%29%29%29%5E2%E2%88%921%29+%3D+0+for+-1.5+%3C+x+%3C+1.5%2C+-1.5+%3C+y+%3C+1.5
    vec3 heart = vec3((pow(abs(uv.x),2.)+pow(abs(1.2*uv.y-sqrt(abs(uv.x))),2.)-1.) );
    vec3 brightness = vec3(0.8);
    vec3 contrast = vec3(0.15,0.1,0.3);
    // the numbers that divide time are pretty arbitrary, as long as they are not the same and are somewhere between 10-100 id say it gives the desired effect
    vec3 osc = vec3(0.1,cos(iTime/13.),cos(iTime/11.));
    vec3 phase = vec3(0.2,0.5,0.1);
    vec3 col = cosPalette(heart.x/3., brightness, contrast, osc, phase);
    fragColor.rgb = col;
    fragColor.a   = 1.0;
}

float fade( float holdDuration0, float holdDuration1, float transitionDuration, float time ) {
    float pi = 3.1415926535897932384626433832795;

    time = mod(time, (holdDuration0 + holdDuration1 + 2. * transitionDuration));

    if (time < holdDuration0) {
        return 0.;
    } else if (time < holdDuration0 + transitionDuration) {
        return 0.5 * (1. - cos((time - holdDuration0) * pi / transitionDuration));
    } else if (time < holdDuration0 + transitionDuration + holdDuration1) {
        return 1.;
    } else {
        return 0.5 * (1. + cos((time - holdDuration0 - transitionDuration - holdDuration1) * pi / transitionDuration));
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float pi = 3.1415926535897932384626433832795;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 pos = vec2(uv.x * iResolution.x / iResolution.y, uv.y);
    vec2 center = vec2(iResolution.x / iResolution.y / 2.0, 0.5);

    //float time = 1.;
    float time = iTime;

    float r1 = 0.08;
    float r2 = 0.06;
    float r3 = 0.04;
    float r4 = 0.1;
    float r5 = 0.04;
    float r6 = 0.05;
    //float rMouse = 0.1;

    vec2 v1 = vec2(cos(time * sqrt(2.0)) * 0.7, 0.0) + center;
    vec2 v2 = vec2(0.0, sin(time) * (0.5 - 2.0 * r2)) + center;
    vec2 v3 = vec2(cos(time) * 0.6, sin(time) * 0.3) + center;
    vec2 v4 = vec2(cos(time * 0.2) * (center.x - r4) * 0.8, sin(time * 2.5) * 0.3) + center;
    vec2 v5 = vec2(cos(time * 5. / pi) * 0.3 - 0.3, sin(time * 5. / pi) * 0.3) + center;
    vec2 v6 = vec2(cos(time * 6. / pi) * 0.3 + 0.3, -sin(time * 6. / pi) * 0.3) + center;
    //vec2 vMouse = iMouse.xy / iResolution.yy;

    float d1 = distance(pos, v1);
    float d2 = distance(pos, v2);
    float d3 = distance(pos, v3);
    float d4 = distance(pos, v4);
    float d5 = distance(pos, v5);
    float d6 = distance(pos, v6);
    //float dMouse = distance(pos, vMouse);

    float field =  1. / (r1 / d1 + r2 / d2 + r3 / d3 + r4 / d4 + r5 / d5 + r6 / d6);

    float circWidth = 0.003;

    //Anti-aliasing
    float inGoop = 1. - smoothstep(1. - 2. * fwidth(field), 1., field);

    vec3 color;

    //rainbow
    if (iMouse.z > 0.5) {
        float sCurve = 0.5 * (1. - cos(field * pi));

        vec3 magenta = vec3(1., 0., 1.);
        vec3 blue = vec3(0., 0., 1.);
        vec3 cyan = vec3(0., 1., 1.);
        vec3 green = vec3(0., 1., 0.);
        vec3 yaller = vec3(1., 1., 0.);
        vec3 rot = vec3(1., 0., 0.);
        float interpVal = mod(sCurve, 0.2) * 5.;
        vec3 colorRainbow;
        if (sCurve < 0.2) {
            colorRainbow = mix(rot, yaller, sCurve * 5.);
        } else if (sCurve < 0.4) {
            colorRainbow = mix(yaller, green, (sCurve - 0.2) * 5.);
        } else if (sCurve < 0.6) {
            colorRainbow = mix(green, cyan, (sCurve - 0.4) * 5.);
        } else if (sCurve < 0.8) {
            colorRainbow = mix(cyan, blue, (sCurve - 0.6) * 5.);
        } else {
            colorRainbow = mix(blue, magenta, (sCurve - 0.8) * 5.);
        }
        color = colorRainbow * inGoop;
    } else {

        //non-rainbow
        // old colors without gamma correction
        //vec3 goopThickColor = vec3(1., 0.8, 0.9);
        //vec3 goopThinColor = (vec3(0.25, 1., 1.) + goopThickColor) * 0.5;
        vec3 goopThickColor = vec3(0.9, 0.4, 0.7);
        vec3 goopThinColor = (vec3(0.2, 0.8, 0.8));
        float circleQuadrant = sqrt(1.-field*field);
        vec3 colorNonRainbow = mix(goopThinColor, goopThickColor, circleQuadrant);
        color = colorNonRainbow * inGoop;
    }

    //float rainbowFade = fade(10.0, 1.0, 0.5, iTime);
    //vec3 color = mix(colorNonRainbow, colorRainbow,  rainbowFade) * inGoop;

    //gamma correction
    //like this really needs it
    color = pow(color, vec3(0.4545));

    fragColor = vec4(color, inGoop);
}

#define H(s,v)   sin( s + (v).zxy - cos(s.zxy + (v).yzx) + cos(s.yzx + v) )

void mainImage( out vec4 O, vec2 U )
{
    U = U/8. + sin(iTime);

    vec3 d = vec3(11.1, 14.3, 8.2),
         t = iTime/d,
         v = (U.x * H( vec3(2,6,4), t - d.yzx)
                + U.y * H( vec3(3,7,5), t + d    )
                ) /4.,
         K = vec3(3,5,6);

    O.rgb = H(3.+K,((v+H(K,v))/2. +H(1.62+K, (v+H(K,v))/2. ) *3.)) * .5 + .5;
}

