float noise(in vec2 uv)
{
    return sin(uv.x)+cos(uv.y);
}


#define OCTAVES 8
float fbm(in vec2 uv,int octaves)
{
    //this function generates the terrain height
    uv *= 1.25;
    float value = 0.;
    float amplitude = 1.;
    float freq = 0.5;
    float n1 = 0.;
    for (int i = 0; i < octaves; i++)
    {
        n1 = noise(uv*freq) -n1*value*amplitude;
        //n1 = noise(uv*freq+n1/2.);
        //n1 = noise(uv * freq)-n1;
        //float n1 = noise(uv * freq+noise(uv1));

        // From Dave_Hoskins https://www.shadertoy.com/user/Dave_Hoskins
        value = abs(value-abs(n1-1.) * amplitude);

        amplitude *= 1./5.+1./(5.-value);

        freq *= 2.05;
        //uv1 = uv*freq;

        uv = uv.yx-(n1*(amplitude)-n1/freq);
        //uv = uv.yx-vec2(n1*(amplitude)-n1/freq,n1/freq);
    }

    return value;
}

float fbm(in vec2 uv){
    return fbm(uv,OCTAVES);
}

float f(in vec3 p,int iters)
{   
    float h = fbm(p.xz,iters);
    return h;
}

float f(in vec3 p)
{   
    float h = fbm(p.xz,OCTAVES+2);
    return h;
}

vec3 getNormal(vec3 p, float t)
{
    vec3 eps=vec3(.001 * t, .0, .0);
    vec3 n=vec3(f(p - eps.xyy) - f(p + eps.xyy),
                2. * eps.x,
                f(p - eps.yyx) - f(p + eps.yyx));

    return normalize(n);
}

float rayMarching(in vec3 ro, in vec3 rd, float tMin, float tMax)
{
    float t = tMin;
	for( int i = 0; i < 300; i++ )
	{
        vec3 pos = ro + t * rd;
		float h = pos.y - f(pos,OCTAVES);
		if( abs(h) < (0.0015 * t) || t > tMax ) 
            break;
		t += 0.4 * h;
	}

	return t;
}

vec3 lighting(vec3 p, vec3 normal, vec3 L, vec3 V)
{
    vec3 sunColor = vec3(1., .956, .839);
    vec3 albedo = vec3(1.);
   	vec3 diff = max(dot(normal, L) * albedo, 0.);

    vec3 refl = normalize(reflect(L, normal));
    float spec = max(dot(refl, -normalize(V)), 0.);
    spec = pow(spec, 18.);
    spec = clamp(spec, 0., 1.);
    float sky = max(0.0, dot(vec3(0.,1.,0.), normal));

    //float amb = 0.5 * smoothstep(0.0, 2.0, p.y);

    vec3 col = diff * sunColor;
    col += spec * sunColor;
    col += sky * vec3(0., .6, 1.) * .1;
    //col += amb * .2;

   	return col;
}

mat3 lookAt(vec3 origin, vec3 target, float roll)
{
    vec3 rr = vec3(sin(roll), cos(roll), 0.0);
    vec3 ww = normalize(target - origin);
    vec3 uu = normalize(cross(ww, rr));
    vec3 vv = normalize(cross(uu, ww));

    return mat3(uu, vv, ww);
}

vec3 camerapath(float t)
{
    vec3 p=vec3(-13.0+3.5*cos(t),3.3,-1.1+2.4*cos(2.4*t+2.0));
	return p;
} 

#define rot(spin) mat2(cos(spin),sin(spin),-sin(spin),cos(spin))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    vec3 lightDir = normalize(vec3(-.8, .15, -.3));

    vec3 camStep = vec3(lightDir.x, 0., lightDir.z) * (iTime+12.)/4.;
    vec3 camPos = vec3(8., 2., 5.) + camStep;
    vec3 camTarget = vec3(1., 1., 4.) + camStep;
    mat3 mat = lookAt(camPos, camTarget, 0.0);

    vec3 ro = camPos;
    ro.y += fbm(ro.xz,4)-1.8;

    vec3 rd = normalize(mat * vec3(uv.xy, 1.0));


    //if (length(iMouse.xy) > 40.0) {
        rd.yx *= rot(3.14*0.5-iMouse.y/iResolution.y*3.14);
        rd.xz *= rot(3.14-iMouse.x/iResolution.x*3.14*2.0);
    //}

    float tMin = .1;
    float tMax = 20.;
    float t = rayMarching(ro, rd, tMin, tMax);

    vec3 col = vec3(0.);

    if (t > tMax)
    {
        // from iq's shader, https://www.shadertoy.com/view/MdX3Rr
        float sundot = clamp(dot(rd, lightDir), 0.0, 1.0);
        col = vec3(0.3,0.5,0.85) - rd.y*rd.y*0.5;
        col = mix( col, 0.85*vec3(0.7,0.75,0.85), pow( 1.0-max(rd.y,0.0), 4.0 ) );
        // sun
		col += 0.25*vec3(1.0,0.7,0.4)*pow( sundot,5.0 );
		col += 0.25*vec3(1.0,0.8,0.6)*pow( sundot,64.0 );
		col += 0.2*vec3(1.0,0.8,0.6)*pow( sundot,512.0 );
        // clouds
		vec2 sc = ro.xz + rd.xz*(SC*1000.0-ro.y)/rd.y;
		col = mix( col, vec3(1.0,0.95,1.0), 0.5*smoothstep(0.5,0.8,fbm(0.0005*sc/SC)) );
        // horizon
        col = mix( col, 0.68*vec3(0.4,0.65,1.0), pow( 1.0-max(rd.y,0.0), 16.0 ) );
    }
    else
    {
        vec3 p = ro + rd * t;
        vec3 normal = getNormal(p, t);
        vec3 viewDir = normalize(ro - p);

        // lighting terrian
        col = lighting(p, normal, lightDir, viewDir);

        // fog
        float fo = 1.0-exp(-pow(30. * t/SC,1.5) );
        vec3 fco = 0.65*vec3(0.4,0.65,1.0);
        col = mix( col, fco, fo);
    }

    // Gama correction
    col = pow(clamp(col, 0., 1.), vec3(.45)); 

    fragColor = vec4(vec3(col), 1.0);
}

// License CC0: Simple circle tiling
//  Been working too much lately to do shader stuff.
//  But today I experimented a bit with tiling so thought I share
#define TIME        iTime
#define RESOLUTION  iResolution
#define ROT(a)      mat2(cos(a), sin(a), -sin(a), cos(a))
#define PI          3.141592654
#define TAU         (2.0*PI)

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}

// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
//  Macro version of above to enable compile-time constants
#define HSV2RGB(c)  (c.z * mix(hsv2rgb_K.xxx, clamp(abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www) - hsv2rgb_K.xxx, 0.0, 1.0), c.y))

float circle(vec2 p, float r) {
  return length(p) - r;
}

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

// License: Unknown, author: Hexler, found: Kodelife example Grid
float hash(vec2 uv) {
  return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

float df(vec2 p, out float n, out float sc) {
  vec2 pp = p;

  float sz = 2.0;

  float r = 0.0;

  for (int i = 0; i < 5; ++i) {
    vec2 nn = mod2(pp, vec2(sz));
    sz /= 3.0;
    float rr = hash(nn+123.4);
    r += rr;
    if (rr < 0.5) break;
  }

  float d = circle(pp, 1.25*sz);

  n = fract(r);
  sc = sz;
  return d;
}

// License: MIT, author: Inigo Quilez, found: https://www.iquilezles.org/www/index.htm
vec3 postProcess(vec3 col, vec2 q) {
  //  Found this somewhere on the interwebs
  col = clamp(col, 0.0, 1.0);
  // Gamma correction
  col = pow(col, 1.0/vec3(2.2));
  col = col*0.6+0.4*col*col*(3.0-2.0*col);
  col = mix(col, vec3(dot(col, vec3(0.33))), -0.4);
  // Vignetting
  col*= 0.5+0.5*pow(19.0*q.x*q.y*(1.0-q.x)*(1.0-q.y),0.7);
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/RESOLUTION.xy;
  vec2 p = -1. + 2. * q;
  p.x *= RESOLUTION.x/RESOLUTION.y;
  float aa = 2.0/RESOLUTION.y;

  const float r = 25.0;
  float a = 0.05*TAU*TIME/r;
  const float z = 1.0;
  p /= z;
  p += r*vec2(cos(a), sin(a));
  p *= ROT(-a+0.25);
  float n = 0.0;
  float sc = 0.0;
  float d = df(p, n, sc)*z;

  vec3 col = vec3(0.0);
  vec3 hsv = vec3(n-0.25*d/sc, 0.5+0.5*d/sc, 1.0);
  vec3 rgb = hsv2rgb(hsv);
  col = mix(col, rgb, smoothstep(aa, -aa, d));

  col = postProcess(col, q);

  fragColor = vec4(col, 1.0);
}

/**
 * create a rotation matrix for the given angle in radians
 */
mat2 getRotationMatrix(float angle) {
    float s = sin(angle);
    float c = cos(angle);

    return mat2(c, -s,
                s, c);
}


/**
 draw a rectangle
*/
vec3 rect(vec2 position, vec2 center, vec2 anchor, vec2 size) {
    vec3 color = vec3(1.0, 1.0, 1.0);

    vec2 pos = position - center;

    float halfW = size.x / 2.0;
    float halfH = size.y / 2.0;

    color *= step(-halfW - anchor.x, pos.x) - step(halfW - anchor.x, pos.x);
    color *= step(-halfH - anchor.y, pos.y) - step(halfH - anchor.y, pos.y);

    return color;
}

/**
 * draw a stick
 */
vec3 stick(vec2 position, vec2 center){
    vec3 color = vec3(1.0, 1.0, 1.0);
    vec2 pos = position - center;

    float lineSize = 0.005;
    float verticalLine = smoothstep(lineSize, lineSize + lineSize / 2.0, abs(pos.x + 0.1 * sin(5.0 * pos.y)));
    color *= 1.0 - (1.0 - verticalLine) * (smoothstep(0.1, 0.0, pos.y));

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y - vec2(0.5, 0.0);
    uv = mod(uv, 1.0);

    vec3 color = vec3(0.31, 0.02, 0.37);

    vec2 center = vec2(0.5, 0.5);
    mat2 mat = getRotationMatrix((iTime + uv.x));
    vec2 pos = mat * (uv - center) + center;

    // stick
    color = mix(vec3(0.5922, 0.4078, 0.6), color, stick(uv, center));
    vec2 size = vec2(0.4, 0.2);

    vec3 rect1 = rect(pos, center, -size / 2.0, size);
    color = mix(color, vec3(1.0, 0.8549, 0.9765), rect1);

    vec3 rect2 = rect(pos, center, vec2(size.y, -size.x) / 2.0, size.yx);
    color = mix(color, vec3(0.8549, 0.9608, 1.3), rect2);

    vec3 rect3 = rect(pos, center, size / 2.0, size);
    color = mix(color, vec3(0.8549, 1.0, 0.9451), rect3);

    vec3 rect4 = rect(pos, center, vec2(-size.y, size.x) / 2.0, size.yx);
    color = mix(color, vec3(0.9961, 1.0, 0.8549), rect4);

    fragColor = vec4(color, 1.0);
}

