    float blur;

    vec4 layer;

    vec4 col = vec4(0.0);
    col += Stars(uv);

    float moon = S(0.01, -0.01, length(uv-vec2(0.4, 0.2))-0.15);
    col *= 1.0-moon;
    moon *= S(-0.01, 0.1, length(uv-vec2(0.48, 0.24))-0.15);
    col += moon;

    for(float i = 0.0; i<1.0; i+=1.0/10.){
        blur = mix(0.04, 0.001, i);
        float scale = mix(30.0, 1.0, i);
        layer = Layer(uv*scale+vec2(t+sin(i)*1354.2, i*2.0)-M, blur);
        layer.rgb *= (1.0-i)*vec3(0.9, 0.9, 1.0);

        col = mix(col, layer, layer.a);
    }
    layer = Layer(uv+vec2(t, 1.5)-M, 0.07);

    col = mix(col, layer*0.1, layer.a);

    fragColor = col;
}

// written by mossan-hoshi( https://twitter.com/mossan_hoshi )

#define MAX_GAMMA  2.2
#define MARGIN  0.1
#define IN_OUT_COLOR cyan

void drawLine(vec2 p1, vec2 p2){

}

float gamma_function(float x, float gamma){
  return  pow(x,1.0/gamma);
}

void gamma_curve(vec2 uv, float gamma, inout vec4 fragColor){
  if(abs(uv.y - gamma_function(uv.x,gamma)) < 0.002 ){
      vec3 color = vec3(0.8,0.5,0.8);
      fragColor=vec4(color,1.0);
  }
}

void in_out_line(float x, vec2 uv, float gamma, inout vec4 fragColor){
  if((uv.y < gamma_function(x,gamma)&&(abs(uv.x - x) < 0.002))){
      vec3 color = vec3(0.5,0.8,0.8);
      fragColor=vec4(color,1.0);
  }else if((abs(uv.y - gamma_function(x,gamma)) < 0.002)&&(uv.x < x)){
      vec3 color = vec3(0.5,0.8,0.8);
      fragColor=vec4(color,1.0);
  }
}

void in_out_wave(float x, float input_range, vec2 uv, float gamma, float iTime, inout vec4 fragColor){
  float x_max = x+input_range;
  float x_min = x-input_range;
  float y_max = gamma_function(x_max,gamma);
  float y_min = gamma_function(x_min,gamma);
  float y = (y_min + y_max) / 2.;
  if((uv.y < gamma_function(uv.x,gamma)&&(abs(uv.x - (x + input_range * cos(100. * uv.y - 10. *iTime))) < 0.004))){
      vec3 color = vec3(0.2,0.6,0.6);
      fragColor=vec4(color,1.0);
  }else if((uv.y > gamma_function(uv.x,gamma))&&
           (abs(uv.y - ( y + ((y_max - y_min)/2.0) * cos(100. * uv.x + 10. *iTime))) < 0.004)){
      vec3 color = vec3(0.2,0.6,0.6);
      fragColor=vec4(color,1.0);
  }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
  // const 
  // normalize position(x,y:.0~1.0)
  vec2 uv= fragCoord.xy/ min(iResolution.x,iResolution.y);
  uv = uv * (1.0 + (2. * MARGIN));
  vec2 offset = ((iResolution.xy / min(iResolution.x,iResolution.y)) - 1.0)/2.0;

  // draw color tiles
  if((uv.x >= offset.x+MARGIN)&&(uv.x <= (1.007 + offset.x+MARGIN))&&
     (uv.y >= offset.y+MARGIN)&&(uv.y <= (1.007 + offset.y+MARGIN))){

    // background
    if((uv.x>offset.x+MARGIN)&&(uv.x<1.0 + offset.x+MARGIN)){
      float value = 0.9;
      fragColor=vec4(value,value,value,1.0);
    }

    // thin lines
    if(((mod(uv.x-offset.x, MARGIN) < 0.002)&&(mod(uv.y-offset.y,0.005) < 0.002))||
       ((mod(uv.y-offset.y, MARGIN) < 0.002)&&(mod(uv.x-offset.x,0.005) < 0.002))){
      float value = 0.2;
      fragColor=vec4(value,value,value,1.0);
    }

    // thick lines
    if((mod(uv.x-offset.x-MARGIN,0.5) < 0.007)||(mod(uv.y-offset.y-MARGIN,0.5) < 0.007)){
      float value = 0.2;
      fragColor=vec4(value,value,value,1.0);
    }

    // gamma curve
    float range = MAX_GAMMA - 1.0 / MAX_GAMMA;
    float gamma = MAX_GAMMA + (range / 2.)*(cos(iTime) - 1.0);
    gamma_curve(uv - offset - MARGIN, gamma, fragColor);

    // lines
    float line_x = 0.3;
    float input_range = 0.1;
    in_out_line(line_x - input_range, uv - offset - MARGIN, gamma,fragColor);
    in_out_line(line_x + input_range, uv - offset - MARGIN, gamma,fragColor);

    // wave 
   in_out_wave(line_x, input_range, uv - offset - MARGIN, gamma, iTime,fragColor);

  }
}

#define DEPTH 21
