const float PI = 3.14;

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}


mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}


float sphere(vec3 pos, vec3 spherePos, float sphereRadius) {
    return length(pos - spherePos) - sphereRadius;
}

float prism(vec3 p, vec2 h ) {
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.7+p.y*0.3,-p.y)-h.x*0.5);
}


float smin ( float a, float b, float k )
{
	float res = exp ( -k*a ) + exp ( -k*b );
	return -log ( res ) / k;
}


float smax ( float a, float b, float k )
{
	float res = exp ( k*a ) + exp ( k*b );
	return log ( res ) / k;
}


float scene_eyes(vec3 pos, mat3 m) {
    vec3 q = m * pos;
    float k = min(
        sphere(q, vec3(0.45, -0.7, 0.2), 0.3),
        sphere(q, vec3(-0.45, -0.7, 0.2), 0.3)
    );
    return k;
}


float scene_head(vec3 pos, mat3 m) {
    vec3 q = m * pos;

    // prism transform
    vec3 q2 = q + vec3(0., 1.6, 1.);
    mat3 m2 = rotateY(PI / 2.) * rotateZ(PI / 180. * 30.);
    q2 *= m2;

    float k = max(
        -prism(q2, vec2(1., 1.5)),
        sphere(q, vec3(0., 0., 0.), 1.)
    );

    // ear radius
    float a = 2.;
    // ear thickness
    float d = 0.2;

    vec3 ear_position1 = vec3(-1.1, 0, 1.0);
    vec3 ear_position2 = vec3(1.1, 0, 1.0);

    float s1 =  smax(
        -sphere(q,ear_position1 +  vec3(0., -a, 0.), a),
        sphere(q, ear_position1, 1.), 10.0
    );

    float s2 = smax(
        sphere(q, ear_position1 + vec3(0., -a + d, 0.), a),
        s1, 10.0
    );

    float s3 =  smax(
        -sphere(q,ear_position2 +  vec3(0., -a, 0.), a),
        sphere(q, ear_position2, 1.), 10.0
    );

    float s4 = smax(
        sphere(q, ear_position2 + vec3(0., -a + d, 0.), a),
        s3, 10.0
    );


    return min(s4, min(s2, k));
}


float scene(vec3 pos, mat3 m, int n) {
    float dist;
    if (n == 0) {

        dist = scene_head ( pos, m );
    }
    else {
        dist = scene_eyes ( pos, m );
    } 
    return dist;
}


vec3 trace ( in vec3 from, in vec3 dir, out bool hit, mat3 m, int n)
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

    int stepNum = 70;
	for ( int steps = 0; steps < stepNum; steps++ )
	{
        float dist = scene(p, m, n);

        p += dist * dir;

		if ( dist < 0.01 )
		{
			hit = true;
			break;
		}

		totalDist += dist;

		if ( totalDist > 10.0 )
			break;	
	}

	return p;
}

vec3 generateNormal ( vec3 z, float d, mat3 m, int n)
{
    float eps = 0.01;
    float e   = max (d * 0.5, eps );
    float dx1 = scene(z + vec3(e, 0, 0), m, n);
    float dx2 = scene(z - vec3(e, 0, 0), m, n);
    float dy1 = scene(z + vec3(0, e, 0), m, n);
    float dy2 = scene(z - vec3(0, e, 0), m, n);
    float dz1 = scene(z + vec3(0, 0, e), m, n);
    float dz2 = scene(z - vec3(0, 0, e), m, n);

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}



mat2 rotate(float angle)
{
    float c = cos(angle);
    float s = sin(angle);

    return mat2(c, -s, s, c);
}


vec4 background(vec2 uv) {
    //source: https://www.shadertoy.com/view/Xt2BDG

    vec4 result = vec4(0,0,0,1);

    float t = 1.;
    float offset = -5. * iTime;
    float base = 100. * length(uv);

    float d = sin(-iTime + 15. * length(uv));
    d *= d * d;

    mat2 rot = rotate(5. * length(uv));
    uv += .5;

    uv = abs(rot * uv);

    for (int p = 0; p < 3; p++)
    {
        result[p] = sin(offset + t * base) - cos(20. * uv.x) - cos(20. * uv.y);
        t += 0.05;
    }

    result.xyz *= result.xyz;
    result.xyz = 1. - result.xyz;

    return result * d;
}

vec3 lightPos = vec3(0, -6., 0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    mat3 m;

    vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    if ( iMouse.z > 0.) {
        m     = rotateX ( 6.0*mouse.y ) * rotateY ( 6.0*mouse.x );
    }
    else {
        m = mat3(
            vec3(1, 0, 0),
            vec3(0, 1, 0),
            vec3(0, 0, 1)
        );
    }

    vec3 cameraPos = vec3(0.,-5., 0.);
    vec3 cameraForward = vec3(0., 1, 0.);
    vec3 cameraUp = vec3 (0., 0., 1.);
    float cameraFocus = 5.;

    vec2 scale = 9. * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );


    vec3 from = vec3(uv.x, (cameraPos + cameraForward * cameraFocus).z, uv.y);
    vec3 dir = normalize(from - cameraPos);

    bool hit;
    vec3 p1 = trace(cameraPos, dir, hit, m, 0);
    float d1 = length(cameraPos - p1);
     float c1 = length(p1);

    vec3 backgroundColor = vec3(0., 0., 0.);
    vec3 surfaceColor = vec3(1., 1., 1.);

    vec4 col;

    if (hit) {

       vec3 l = normalize(lightPos - p1);
       vec3 n = generateNormal(p1, 0.001, m, 0);
       vec3 v = normalize(cameraPos - p1);
       float diffuse = max(0., dot(n, l));

       vec3  h  = normalize ( l + v );
       float hn = max ( 0.0, dot ( h, n ) );
       float specular = pow ( hn, 150.0 );

       col = 0.5*vec4 ( diffuse ) * vec4(1.0, 0.1, 0.4, 1) + 0.5 * specular * vec4 ( 1, 1, 1, 1 );
    }
    else {
       col = background(uv / 2.);
    }

    vec3 p2 = trace(cameraPos, dir, hit, m, 1);
    float d2 = length(cameraPos - p2);
    float c2 = length(p2);

    if (hit && (d2 < d1)) {

       vec3 l = normalize(lightPos - p2);
       vec3 n = generateNormal(p2, 0.001, m, 1);
       vec3 v = normalize(cameraPos - p2);
       float diffuse = max(0., dot(n, l));

       vec3  h  = normalize ( l + v );
       float hn = max ( 0.0, dot ( h, n ) );
       float specular = pow ( hn, 150.0 );

       float t = abs(sin(70. * c2 + 20. * iTime) - 0.9);

       col = 0.5 * vec4 ( diffuse ) * vec4(t, abs(t - 0.9), abs(t - 0.3), 1) + 0.5 * specular * vec4 ( 1, 1, 1, 1 );
    }


    // Output to screen
    fragColor = col;
}

// set to 0 to disable the waving animation
#define WAVE 1

// adjust the number of stripes
const float stripes = 6.0;

//  Function from Iñigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}


// linear value remap
float remap(float value, float low1, float high1, float low2, float high2){
    return low2 + (value - low1) * (high2 - low2) / (high1 - low1);
}

// step function w/ floor
float stepf(float value,float steps){
    return remap(floor(remap(value,0.0,1.0,0.0,steps)),0.0,steps,0.0,1.0);
}

// used to control position on the rainbow
float hsbinfcufve(float x){
    return pow(x,1.5);
}

// used to control brightness
float b(float x){
    return 1.0 - pow(x,3.0);
}

// prevent wraparound
float clampInRange(float x){
    return ((x >= 0.0 && x <= 1.0) ? x : 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = fragCoord/iResolution.xy;
     #if WAVE
     uv.y += (sin(10.0*(-iTime/3.0+uv.x))*0.03);
     uv.y = clampInRange(uv.y);
     #endif

     // calculate where to sample on rainbow
     float coord = hsbinfcufve(stepf(1.0-uv.y,stripes));

     // calculate color & brightness
     vec3 resCol = hsb2rgb(vec3(coord,1.0,b(coord)));


    // Output to screen
    fragColor = vec4(resCol,1.0);
}

float sphereSDF ( vec3 position, vec3 center, float radius ) {
    float distance = length(center - position) - radius;
    return distance;
}

float planeSDF ( vec3 position, vec3 start, vec3 direction ) {
    float distance = dot(position - start, direction );
    return distance;
}


float smin(float k, float a, float b) {
    float h = clamp(0.5 + 0.5*(a-b)/k, 0.0, 1.0);
    float y = mix(a, b, h) - k*h*(1.0-h);
    return y;
}


float SDF ( vec3 position ) {
    // Add all of the walls
    float ground = planeSDF(position, vec3(0, -10, 0), vec3(0, 1, 0));
    float roof = planeSDF(position, vec3(0, 10, 0), vec3(0, -1, 0));
    float left = planeSDF(position, vec3(-20, 0, 0), vec3(1, 0, 0));
    float right = planeSDF(position, vec3(28, 0, 0), vec3(-1, 0, 0));
    float back = planeSDF(position, vec3(0, 0, 50), vec3(0, 0, -1));

    // Add the spheres
    float t = iTime / 5.0;
    float s1 = sphereSDF(position, vec3(0, 0, 30) + vec3(5, 20, 3) * sin(t), 3.0);
    float s2 = sphereSDF(position, vec3(-4, 0, 30) + vec3(0, 20, 0) * sin(3.2 * t), 2.0);
    float s3 = sphereSDF(position, vec3(2, 0, 32) + vec3(5, 20, 0) * sin(2.16 * t), 1.0);
    float s4 = sphereSDF(position, vec3(1, 0, 34) + vec3(0, 20, 2) * sin(2.86 * t), 2.1);
    float s5 = sphereSDF(position, vec3(2, 0, 30) + vec3(0, 20, 0) * sin(3.46 * t), 2.3);

    // Blend everything together to make goo
    float blend = iMouse.y == 0.0 ? 10.0 : iMouse.y/50.0;
    float nearest = 
        // Blend the walls
        smin( blend, ground,
        smin( blend, roof,
        smin( blend, left,
        smin( blend, right,
        smin( blend, back,

        // Blend the spheres
        smin( blend, s1,
        smin( blend, s2,
        smin( blend, s3,
        smin( blend, s4,
        s5
    )))))))));
    return nearest;
}


vec3 getNormal ( vec3 p ) {
    #define eps 0.0001
    float dx = SDF(p + vec3(eps, 0, 0)) - SDF(p - vec3(eps, 0, 0));
    float dy = SDF(p + vec3(0, eps, 0)) - SDF(p - vec3(0, eps, 0));
    float dz = SDF(p + vec3(0, 0, eps)) - SDF(p - vec3(0, 0, eps));
    vec3 approxGrad = vec3(dx, dy, dz);
    vec3 normal = normalize(approxGrad);
    return normal;
}



float raymarch ( vec3 start, vec3 direction ) {
    // Take a number of steps in the right direction
    #define maxSteps 100
    float converged = 0.0001;
    float distance = 0.0;
    vec3 current = start;
    for(int i=0; i < maxSteps;i++){;
        // Find the minimum distance and move by that amount
        float surfaceDistance = SDF(current);
        if ( surfaceDistance < converged ) return distance;

        // Take a step by the given amount
        distance += surfaceDistance;
        current += surfaceDistance * direction;
    }
    return distance;
}


vec3 lighting ( vec3 surface ) {
    // Define the lighting parameters
    vec3 light = vec3(0, 5, 18) + 15.0 * vec3(cos(iTime), 0, sin(iTime));
    float strength = iMouse.x == 0.0 ? 0.6 : iMouse.x / iResolution.x;
    vec3 specular = vec3(0.96, 0.74, 0.08);
    vec3 diffuse = vec3(0.62, 0.46, 0.12);

    // Calculate the sphere's brightness
    vec3 lightDelta = light - surface;
    vec3 N = getNormal(surface);
    vec3 L = normalize(lightDelta);
    float lightDistance = raymarch(surface + 0.2 * N, L);
    float brightness = strength * dot(N, L);

    // Add a Shadow color
    float straightLightDistance = length(lightDelta);
    bool occluded = straightLightDistance < 300.0
        && straightLightDistance + 0.3 > lightDistance;
    vec3 shadow = occluded
        ? 0.5 * vec3(1, 1, 1)
        : vec3(1, 1, 1);

    // Calculate the color
    if ( brightness < 0.0 ) return diffuse;
    vec3 color = brightness * specular * shadow + diffuse ;
    return color;
}




void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    // Get the UV coordinates
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    // Get the normalized coordinates and the ray position
    vec3 camera = vec3(0.0, 0.0, -1.0-0.15*sin(iTime));
    vec3 start = vec3(uv, 0.0);
    vec3 direction = start - camera;

    // Get the distance to the nearest object
    float distance = raymarch(start, direction);
    if ( distance > 100.0 ) {
        fragColor = vec4(0, 0, 0, 0);
        return;
    }

    // Find the surface point and the corresponding normal 
    vec3 surface = start + distance * direction;
    vec3 color = lighting(surface);
    fragColor = vec4(color, 0.0);
}

