void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col;
    // Time varying pixel color
    if(uv.x > 0.25 && uv.x < 0.75 && uv.y > 0.25 && uv.y < 0.75){
        col = vec3(sin(iTime + vec3(0,2,4)) * cos(uv.x * iResolution.x / 10.0) / cos(uv.y * iResolution.y / 10.0));
    }
    else{
        if(mod(iTime, 10.0) < 5.0){
            col = vec3(sin(uv.x) * cos(uv.y) * mod(-iTime, 5.0), 0.5, cos(uv.x) * sin(uv.y) * mod(iTime, 5.0));
        }
        else{
            col = vec3(sin(uv.x) * cos(uv.y) * mod(iTime, 5.0), 0.5, cos(uv.x) * sin(uv.y) * mod(-iTime, 5.0));
        }
    }

    vec4 pic = texture(iChannel0, col.xy);

    vec3 pic3 = vec3(pic.xyz);

    // Output to screen
    fragColor = vec4(mix(col.rgb,pic3.rgb, cos(iTime*0.7)),1.0);
}

const int MAX_STEPS = 64;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 1e-6;

//https://www.shadertoy.com/view/3s3GDn
float getGlow(float dist, float radius, float intensity){
	return pow(radius / max(dist, 1e-6), intensity);	
}


//----------------------------- Camera ------------------------------

vec3 rayDirection(float fieldOfView, vec2 fragCoord) {
    vec2 xy = fragCoord - iResolution.xy / 2.0;
    float z = (0.5 * iResolution.y) / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

//https://www.geertarien.com/blog/2017/07/30/breakdown-of-the-lookAt-function-in-OpenGL/
mat3 lookAt(vec3 camera, vec3 targetDir, vec3 up){
    vec3 zaxis = normalize(targetDir);    
    vec3 xaxis = normalize(cross(zaxis, up));
    vec3 yaxis = cross(xaxis, zaxis);

    return mat3(xaxis, yaxis, -zaxis);
}


//-------------------------- SDF and scene ---------------------------

vec3 rotate(vec3 p, vec4 q){
  return 2.0 * cross(q.xyz, p * q.w + cross(q.xyz, p)) + p;
}

//https://www.iquilezles.org/www/articles/distfunctions/distfunctions.htm
float torusSDF( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q) - t.y;
}

float getSDF(vec3 position) {
   	float angle = iTime;
    vec3 axis = normalize(vec3(1.0, 1.0, 1.0));
    position = rotate(position, vec4(axis * sin(-angle*0.5), cos(-angle*0.5))); 
    return torusSDF(position, vec2(1.0, 0.2));

}


//---------------------------- Raymarching ----------------------------

// Glow variable is passed in by reference using the keyword inout. The result written in this
// function can be read afterwards from where it was called.
float distanceToScene(vec3 cameraPos, vec3 rayDir, float start, float end, inout float glow) {

    // Start at a predefined distance from the camera in the ray direction
    float depth = start;

    // Variable that tracks the distance to the scene at the current ray endpoint
    float dist;

    // For a set number of steps
    for (int i = 0; i < MAX_STEPS; i++) {

        // Get the SDF value at the ray endpoint, giving the maximum 
        // safe distance we can travel in any direction without hitting a surface
        dist = getSDF(cameraPos + depth * rayDir);

        // Calculate the glow at the current distance using the distance based glow function
        // Accumulate this value over the whole view ray
        // The smaller the step size, the smoother the final result
        glow += getGlow(dist, 1e-3, 0.55);

        // If the distance is small enough, we have hit a surface
        // Return the depth that the ray travelled through the scene
        if(dist < EPSILON){
            return depth;
        }

        // Else, march the ray by the sdf value
        depth += dist;

        // Test if we have left the scene
        if(depth >= end){
            return end;
        }
    }

    // Return max value if we hit nothing but remain in the scene after max steps
    return end;
}


//----------------------- Tonemapping and render ------------------------

//https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x){
    return clamp((x * (2.51 * x + 0.03)) / (x * (2.43 * x + 0.59) + 0.14), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // Get the default direction of the ray (along the negative Z direction)
    vec3 rayDir = rayDirection(60.0, fragCoord);

    //----------------- Define a camera -----------------

    vec3 cameraPos = vec3(2.0);

    vec3 target = -normalize(cameraPos);
    vec3 up = vec3(0.0, 1.0, 0.0);

    // Get the view matrix from the camera orientation
    mat3 viewMatrix = lookAt(cameraPos, target, up);

    //---------------------------------------------------

    // Transform the ray to point in the correct direction
    rayDir = viewMatrix * rayDir;

    // Initialize glow to 0
    float glow = 0.0;

    // Find the distance to where the ray stops, pass in the glow variable to be accumulated
    float dist = distanceToScene(cameraPos, rayDir, MIN_DIST, MAX_DIST, glow);

    // Dist can now be used to render surfaces in the scene. We will only render the glow

    vec3 glowColour = vec3(0.2, 0.5, 1.0);

    // Glow now holds the value from the ray marching
    vec3 col = glow * glowColour;

    // Tonemapping
    col = ACESFilm(col);

    // Gamma
    col = pow(col, vec3(0.4545));

    fragColor = vec4(col, 1.0);
}

mat2 rot(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}
float bo(vec3 p,vec3 s){p = abs(p) - s;return max(p.x,max(p.y,p.z));}
float scaleSphere( vec3 p, vec3 scale, float s )
{
    return (length(p/scale)-s)*min(scale.x, min(scale.y, scale.z));
}
float pi = acos(-1.);
vec3 pmod(vec2 p, float r){float a = atan(p.x,p.y)+pi/r;float n = (pi*2.)/r;return vec3(p*rot(-(floor(a/n)*n)),floor(a/n) );}
float rand(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float noise(vec3 p)
{
    vec3 fp = floor(p);
	vec3 fs = fract(p);
    vec3 sf = vec3(1.,0.,0.);

    float a = rand(fp);
    float b = rand(fp + sf);
    float c = rand(fp + sf.yxz);
    float d = rand(fp + sf.xxz);
    float e = rand(fp + sf.yyx);
    float f = rand(fp + sf.xyx);
    float g = rand(fp + sf.yxx);
    float h = rand(fp + sf.xxx);

    vec3 u = smoothstep(0.,1.,fs);
  //  u = pow(fs,vec3(3.));

    return mix(mix(
        		mix(a,b,u.x),
        		mix(c,d,u.x),u.y),
               mix(
                mix(e,f,u.x),
                mix(g,h,u.x),u.y),u.z
            );
}

float fbm(vec3 p)
{
    float v = 0.;
    float a = .5;
    float f = 0.;
    for(int i=0;i<3;i++)
    {
        v+=a*noise(p);
        p*=2.;
        a*=.5;
    }
    return v;
}
float random (in vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}
vec3 objectpos;
vec2 toPolar(vec2 p)
{
    return vec2(length(p),atan(p.y,p.x));
}
float shell(vec3 p , float a)
{
    p.xz *= rot(pi);
    vec3 scale = vec3(1.,1.,1.);
    p.xz = clamp(p.xz,-pi/2.,pi/2.);
    scale.x *= (sin(p.z + .96) + 1.4)/2.;
    scale.y /= (sin(length(p.xz + vec2(0.,1.) * 2.) + 3.1) + 3.)/1. * 1.;
    p.y = abs(p.y)+.06;
    vec2 shift = vec2(0.,1.);
    vec3 pp = p;
    p.yz = (p.yz + shift) * rot( step(0.,p.y)*(sin(iTime/3. + a * 10.) - 1.)/2. ) - shift;
    objectpos = p;
    float o = scaleSphere(p,scale,1.);
    o = max(o,-scaleSphere(p + vec3(0.,.4,-0.03),scale/.95,1.));

    if(o < 1.)
    {
        o -= sin(length(p.xz + vec2(0.,1.)) * 100.)/(3. + length(p.xz + vec2(0.,1.))*1700. );
    }

    return o;
}

vec2 map(vec3 p)
{
    float o = 10.;
    float id = 0.;
    vec3 op = p;
    float range = 4.3;
    vec2 areaid = floor(p.xz/range);
    float a = random(areaid);
    //p.x += areaid.y/12.;
    p.xz = mod(p.xz,range) - range/2.;

    p.x += random(areaid.yy * 12.) * 1.3;
    p.xz *= rot(a * 10.);
    p.yz *= rot(-a * 1.3);
    p.y += sin(a*10.) * .3;
    p.y -= .5;
    if(random(areaid*10.) < .3)
    {
        o = shell(p,a);
    }

    p.xz = mod(p.xz,3.) - 1.5;


    float sand = -op.y + 0.2 + noise(op/3.);
    if(sand < 1.)
    {
    	sand -= fbm(op)/2.;
        if(sand < 0.1)
        {
            sand -= noise(op*30.)/100.;
        }
    }
    if(o > sand)
    {
		o = min(sand,o);
        id = 1.;
    }
    o *= .9;
    return vec2(o,id);
}

vec2 march(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i< 128 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.001)
        {
            return vec2(depth,d.y);
        }
        if(depth > 60.)break;
        depth += d.x;
    }
    return vec2(-depth , 0.);

}

vec2 shadowmarch(vec3 cp , vec3 rd)
{
    float depth = 0.;
    for(int i = 0 ; i< 66 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec2 d = map(rp);
        if(abs(d.x) < 0.01)
        {
            return vec2(depth,d.y);
        }
        if(depth > 4.)break;
        depth += d.x;
    }
    return vec2(-depth , 0.);

}

vec3 shellCol(vec3 pos)
{
    vec3 o = vec3(0.);
    o = objectpos;
    vec3 op = objectpos;
    o = vec3(1.,.9,.9);
    o -= fbm(pos*10.) * vec3(0.1,0.1,.5);
    o -= vec3(1.) * fbm(op) * smoothstep(0.,1.,length(op.yz)*sign(op.z) ) * 1.;
    o -= mix((sin(length(op.xz + vec2(0.,1.5)) * 60. + fbm(op * 2. + pos) * 20.) + 1. )/10. , 0. , .5);
    vec2 pol = toPolar(op.xz + vec2(0.,1.));
    o -= sin(pol.y * 40. + fbm(pos * 4.)*10. ) * fbm(pos) * .3;
    return o;
}

vec3 skyCol(vec3 rd)
{
    vec3 o = vec3(1.);
    float y = smoothstep(0.,1.,-rd.y);
    o = mix(vec3(0.6,1.,1.),vec3(0.8,0.7,0.7),y);
    o *= step(rd.y-.06 , 0.);
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (fragCoord.xy * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    vec3 f = vec3(0.);

    float t = floor(iTime*1.3) + pow(fract(iTime*1.3),1.1);
    f.z += t*1.8;
    p *= rot(sin(sin(iTime ))/62.);
	f.y += f.x + sin( iTime*1.3 * pi)/13.;
	vec3 cp = vec3(16.,-3.,-16.) ;

    cp += f;
    //cp.yz *= rot(sin(iTime)/2. + 1.5);
    f.x += (fbm(vec3( iTime * 1.))-.5) * 1.3;
   // f.y += f.x +cos(iTime/2.) * 2. ;
    vec3 target = vec3(0.) + f;

    vec3 col = vec3(1.,0.,1.);

    vec3 cd = normalize(vec3(target - cp));
    vec3 cs = normalize(cross(cd , vec3(0.,1.,0.)));
    vec3 cu = normalize(cross(cd,cs));

    float fov = 1.2 - dot(p,p)/8.;

    vec3 rd = normalize(cd * fov + cs * p.x + cu * p.y);

    vec2 d = march(cp,rd);

    vec3 sky = skyCol(rd);
    col = sky;
    if( d.x > 0.)
    {
        vec2 e = vec2(0.,0.01);
        vec3 pos = cp + rd * d.x;
        vec3 N = normalize(map(pos).x - vec3(map(pos - e.xyy).x , map(pos - e.yxy).x,map(pos - e.yyx).x));
        col = N;
        vec3 sun = normalize(vec3(2.,4.,8.));
        //sun.xz *= rot(iTime);
        float diff = max(0.,dot(-sun,N));
        diff = mix(diff , 1.,.1);
        float sp = max(0.,dot(rd,reflect(N,sun)));
        vec3 mat = vec3(1.);
        if(d.y == 0.)
        {
            mat = shellCol(pos);
            sp = pow(sp,10.)/6.;
        }else {
            sp = pow(sp,103.)/2.;
        }
        float up = max(0.,dot(N,vec3(0.,-1.,0.)) );
        float down = max(0.,dot(N,vec3(0.,1.,0.)) );

        col = sp * vec3(1.) + diff * mat;
        float shadow = step(shadowmarch(pos + N * 0.1,-sun).x,0.);
        col.rgb *= shadow;
        col.rgb +=  + up * vec3(.2,0.2,.5) + down * vec3(.3,.3,.4);
        float t = 1. - exp(-.0000003 * d.x * d.x);
        //col = mix(col,sky,t);
    }

    fragColor = vec4(col, 1.0);
}

// CC0 licensed, do what thou wilt.

// change the seed to any not-too-huge float and the colors/shapes will change.
const float SEED = 69.42; // starts off with a "melting face"
const vec3 COEFFS = fract((SEED + 23.4567) * vec3(0.8191725133961645, 0.6710436067037893, 0.5497004779019703)) + 0.5;
const vec3 SECTION = fract(COEFFS.zxy - COEFFS.yzx * 1.618);

// Quilez Basic Noise, from https://www.shadertoy.com/view/3sd3Rs (MIT-license)
vec3 bas( vec3 x )
{
    // setup    
    vec3 i = floor(x);
    vec3 f = fract(x);
    vec3 s = sign(fract(x/2.0)-0.5);

    // use some hash to create a random value k in [0..1] from i
    vec3 k = fract(SECTION * i + i.yzx);

    // quartic polynomial
    return s*f*(f-1.0)*((16.0*k-4.0)*f*(f-1.0)-1.0);
}

// this is different from other swayRandomized in Northern demos because it uses Quilez basic noise instead of trigonometry.
vec3 swayRandomized(vec3 seed, vec3 value)
{
    return bas(seed.xyz + value.zxy - bas(seed.zxy + value.yzx) + bas(seed.yzx + value.xyz));
}

// this function, if given steadily-increasing values in con, may return exponentially-rapidly-changing results.
// even though it should always return a vec3 with components between -1 and 1, we use it carefully.
vec3 cosmic(vec3 c, vec3 con)
{
    return (con
    + swayRandomized(c, con)
    ) * 0.5;
    //+ swayRandomized(c + 1.1, con.xyz)
    //+ swayRandomized(c + 2.2, con.xyz)) * 0.25;
}

