{
     // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = fragCoord/iResolution.xy;
     #if WAVE
     uv.y += (sin(10.0*(-iTime/3.0+uv.x))*0.03);
     #endif

     // green, white, blue
     vec3 col1 = vec3(0.24,0.54,0.44);
     vec3 col2 = vec3(1.0,1.0,1.0);
     vec3 col3 = vec3(0.22,0.13,0.45);

     float diff = 0.4;

     // gradient + step function
     vec3 colorTop = mix(col1,col2,stepf(1.0-remap(uv.y,1.0-diff,1.0,0.0,1.0)));
     vec3 colorBot = mix(col2,col3,stepc(1.0-remap(uv.y,0.0,diff,0.0,1.0)));

     vec3 resCol = uv.y < 0.5 ? colorBot : colorTop;


    // Output to screen
    fragColor = vec4(resCol * clampInRange(uv.y),1.0);
}

