{
    const vec3 k = vec3(0.809016994,0.587785252,0.726542528);
    p.x = abs(p.x);
    p -= 2.0*min(dot(vec2(-k.x,k.y),p),0.0)*vec2(-k.x,k.y);
    p -= 2.0*min(dot(vec2( k.x,k.y),p),0.0)*vec2( k.x,k.y);
    p -= vec2(clamp(p.x,-r*k.z,r*k.z),r);    
    return length(p)*sign(p.y);
}

float GetDist(vec3 p){
    vec2 torRad = vec2(1.3, 1.);
    vec2 cp = vec2(length(p.xz) - torRad.x, p.y);
    float a = atan(p.x, p.z); // polar angle between -pi and pi
    cp *= Rot(a * 3. + iTime);
    cp.y = abs(cp.y) - .3;

    float d = length(cp) - torRad.y;
    d = sdPentagon2d(cp, .1) - .1;

    float t = .2 * (sin(iTime) + 80.);
    float displacement = sin(t * p.x) * cos(t * p.y) * sin(t * p.z) * (sin(iTime * .35) + 1.);

    return min(d, sdSphere(vec3(0., 0., 0.), .1)) + displacement;
}

float RayMarch(vec3 ro, vec3 rd){
    float dO = 0.;

    for(int i=0; i < MAX_STEPS; i++){
        vec3 p = ro + rd * dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO > MAX_DIST || abs(dS) < SURF_DIST) break;
    }

    return dO;
}
vec3 GetNormal(vec3 p){
    float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p - e.xyy),
        GetDist(p - e.yxy),
        GetDist(p - e.yyx));

    return normalize(n);
}

float GetLight(vec3 p, vec3 lp){
    vec3 l = normalize(lp - p);
    vec3 n = GetNormal(p);

    float dif = clamp(dot(n, l), 0., 1.);
    float d = RayMarch(p + n*SURF_DIST*2., l);

    if(d < length(lp - p)) dif *= .6;

    return dif;
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z){
    vec3 f = normalize(l - p),
        r = normalize(cross(vec3(0, 1, 0), f)),
        u = cross(f, r),
        c = f * z,
        i = c + uv.x * r + uv.y * u,
        d = normalize(i);

     return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14 + 1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0, 0, 0), 1.);
    vec3 col = vec3 (0.);

    float d = RayMarch(ro, rd);

    if(d < MAX_DIST){
        vec3 p = ro + rd * d;
        float dif = GetLight(p, vec3(3, 4, -3));
        dif += GetLight(p, vec3(-3, -4, 3));
        col = vec3(.2, .9, .2) * dif;
    }

    col *= pow(col, vec3(.4545));

    fragColor = vec4(col, 1.);
}

