int lightsLength = 2;

float minDist(vec3 ray, out int object)
{ 
    float dist = 1000.0;
    float compareDist = 1000.0;

    for(int i = 0; i + 1 <= sceneLength; i++) 
    {
        if(getObject(i).type == 1)
        {
            compareDist = sphereDist(ray, getObject(i).position, getObject(i).data[0]);
        }
        else if(getObject(i).type == 2)
        {
            compareDist = planeDist(ray, getObject(i).position);
        }
        //more types here

        if(abs(compareDist) < dist)
        {
            dist = compareDist;
            object = i;
        }
    }

    return dist;

}

vec3 getNormal(vec3 ray, int object)
{
    vec3 normal;

    if(getObject(object).type == 1)
    {
        normal = sphereN(ray, getObject(object).position);
    }
    else if(getObject(object).type == 2)
    {
        normal = planeN();
    }
    //more types here

    return normal;
}

vec2 fresnel(float ior, float theta1, float theta2)
{
    //it's actually cosine of theta, not theta itself
    float n1 = ior;
    float n2 = 1.0;
    float Fpa = pow((n2*theta1-n1*theta2)/(n2*theta1+n1*theta2),2.0);
    float Fpe = pow((n1*theta2-n2*theta1)/(n1*theta2+n2*theta1),2.0);
    float Fr = (Fpa+Fpe)/2.0;
    float Ft = 1.0-Fr;
    return vec2(Fr, Ft);
}

vec4 castShadow(vec3 position) //TODO: colored lights, multiple light sources
{
    vec3 ray = position;
    int objectHit;
    float dist;
    vec3 color;
    vec3 direction;
    float intensity;

    for(int i = 0; i + 1 <= lightsLength; i++)
    {
        if(getLight(i).sphere == 0)
        {
            direction = normalize(getLight(i).position);


        } else 
        {
           direction = normalize(getLight(i).position - position);
        }

        ray += direction; //possibly replace this with one based on normal
        do 
        {
            dist = minDist(ray, objectHit);
            ray += direction * (dist + bias);  
        } while(dist >= epsilon && length(ray) < maxDistance);

        if(dist >= epsilon)
        {
             color += getLight(i).color * getLight(i).intensity;
             intensity += getLight(i).intensity;
             intensity = 1.0;
        }

    }
    return vec4(color, intensity);
}

vec4 selfShadow(vec3 position, int object) //TODO: same as castShadow()
{
    int objectHit;
    vec4 color;
    for(int i = 0; i + 1 <= lightsLength; i++)
    {
        if(getLight(i).sphere == 0)
        {
            return vec4(getLight(i).color, max(0.0, min(1.0, minDist(position + normalize(getLight(i).position), objectHit))));

        } else 
        {
           return vec4(getLight(i).color, max(0.0, min(1.0, minDist(position + normalize(getLight(i).position - position), objectHit))));
        }
    }

    //return color;
}


Ray castReflection(vec3 position, vec3 incident, int object)
{
    vec3 ray = position;
    int objectHit;
    float dist;
    vec3 color;
    vec3 normal;
    int reflections = 0;
    vec3 direction;

    normal = getNormal(ray, object);
    ray += normal * 0.3; //this may be too low! check this if issues come up
    direction = reflect(incident, normal); //introduce randomness with roughness (via rotating normal)
    do 
    {
        dist = minDist(ray, object);
        ray += direction * (dist + bias);  
    } 
    while(dist >= epsilon && length(ray) < maxDistance);
    if(dist <= epsilon)
    {
        return Ray(ray,direction,object,-1,-1,getObject(object).material.color);
    } else
    {
        return Ray(ray,direction,-1,-1,-1,getSkyColor(direction));
    }
}

Ray castRefraction(vec3 position, vec3 incident, int object) 
{
    vec3 ray = position;
    int objectHit = object;
    float dist;
    vec3 color;
    vec3 normal;
    int reflections = 0;
    vec3 direction;
    vec3 refractionIncident;

    normal = getNormal(ray, object);
    ray -= normal * 0.3; 
    direction = refract(incident, 1.0*normal, 0.55); //introduce randomness with roughness (via rotating normal)
    if(direction == vec3(0.0,0.0,0.0))
    {
        direction = reflect(incident, normal);
    }
    refractionIncident = direction;
    do 
    {
        dist = abs(minDist(ray, object));
        ray += direction * (dist + bias);  
    } 
    while(dist >= epsilon && length(ray) < maxDistance);


    if(object == objectHit)
    {
        //return Ray(ray,direction,1,-1,-1,vec3(1.0,1.0,1.0));
        normal = getNormal(ray, object);
        ray += normal * 1.0; 
        direction = refract(refractionIncident, -1.0*normal, 1.8); //introduce randomness with roughness (via rotating normal)
        if(direction == vec3(0.0,0.0,0.0))
        {
            direction = reflect(refractionIncident, -1.0*normal);
        }
        do 
        {
            dist = abs(minDist(ray, object));
            ray += direction * (dist + bias);  
        } 
        while(dist >= epsilon && length(ray) < maxDistance);
    }


    if(dist <= epsilon)
    {
        //return Ray(ray,direction,int(ray.x)%3,-1,-1,vec3(1.0,1.0,1.0));
        return Ray(ray,direction,object,-1,-1,getObject(object).material.color);
    } else
    {
        //return Ray(ray,direction,int(direction.x*100.0)%3,-1, -1,vec3(1.0,1.0,1.0));
        return Ray(ray,direction,-1,-1,-1,getSkyColor(direction));
    }
}

vec3 castRay(vec3 position, vec3 direction)
{
    vec3 ray = position;
    int objectHit;
    float dist;
    vec3 color;
    float shadowColor;
    vec3 reflectionColor;
    vec3 refractionColor;
    vec3 objectColor;
    int reflections = 0;
    float spec;
    float tran;
    int object;
    int newObject;
    const int raysIndexLength = int(pow(2.0,float(maxReflections))-1.0);
    Ray rays[raysIndexLength];//pos inc ob lc rc col

    do 
    {
        dist = minDist(ray, objectHit);
        ray += direction * dist + bias;  
    } while(dist >= epsilon && length(ray) < maxDistance);

    if(dist <= epsilon)
    {
        spec = getObject(objectHit).material.specularity;
        tran = getObject(objectHit).material.transmission;
        if(spec > 0.0 || tran > 0.0)
        {
            color = getObject(objectHit).material.color * min(castShadow(ray).w, selfShadow(ray, objectHit).w);
            rays[0] = Ray(ray, direction, objectHit, 1, 2, color);
            if(spec > 0.0)
            {
            rays[1] = Ray(ray, direction, objectHit, 0, 0, vec3(0.0));
            } else { rays[1] = Ray(vec3(0.0),vec3(0.0),-1,-1,-1,vec3(0.0)); }

            if(tran > 0.0)
            {
            rays[2] = Ray(ray, direction, objectHit, 0, 0, vec3(0.0));
            } else { rays[2] = Ray(vec3(0.0),vec3(0.0),-1,-1,-1,vec3(0.0)); }
        } else
        {
            return getObject(objectHit).material.color * min(castShadow(ray).w, selfShadow(ray, objectHit).w);
        }

    } else
    {
        return getSkyColor(direction);
    }

    for(int i=1;i < rays.length(); i++)
    {
        if(rays[i].object != -1)
        {
            ray = rays[i].position;
            direction = rays[i].incident;
            object = rays[i].object;

            if(getObject(rays[i].object).material.specularity > 0.0) //float point error possible?
            {
                rays[i] = castReflection(ray, direction, object);
                if((2*(i+1)) <= raysIndexLength)
                {
                    rays[(2*(i+1))-1] = rays[i];
                    rays[i].reflectChild = (2*(i+1))-1;
                    rays[i].refractChild = 2*(i+1);
                } else
                {
                    rays[i].reflectChild = -1;
                    rays[i].refractChild = -1;
                }

            } else
            {
                rays[(2*(i+1))-1] = Ray(vec3(0.0,0.0,0.0), vec3(0.0,0.0,0.0), -1, -1, -1, vec3(0.0,0.0,0.0));
            }

            if(getObject(rays[i].object).material.transmission > 0.0) //float point error possible?
            {
                rays[i] = castRefraction(ray, direction, object);
                if((2*(i+1)-1) <= raysIndexLength)
                {
                    rays[(2*(i+1))] = rays[i];
                    rays[i].reflectChild = (2*(i+1))-1;
                    rays[i].refractChild = 2*(i+1);
                } else
                {
                    rays[i].reflectChild = -1;
                    rays[i].refractChild = -1;
                }
            } else 
            {
                rays[2*(i+1)] = Ray(vec3(0.0,0.0,0.0), vec3(0.0,0.0,0.0), -1, -1, -1, vec3(0.0,0.0,0.0));
            }


        } else if(rays[i].object == -1)
        {
            rays[(2*(i+1))-1] = Ray(vec3(0.0,0.0,0.0), vec3(0.0,0.0,0.0), -1, -1, -1, vec3(0.0,0.0,0.0));
            rays[2*(i+1)] = Ray(vec3(0.0,0.0,0.0), vec3(0.0,0.0,0.0), -1, -1, -1, vec3(0.0,0.0,0.0));
        }
    }
    for(int i=1;i <= rays.length(); i++)
    {
        if(rays[rays.length()-i].reflectChild != -1)
        {
            reflectionColor = rays[rays[rays.length()-i].reflectChild].color;
        }
        if(rays[rays.length()-i].refractChild != -1)
        {
            refractionColor = rays[rays[rays.length()-i].refractChild].color;
        }
        //get snells law (fresnel) in here eventually instead of this lerp

        if(length(rays[rays.length()-i].position) < maxDistance)
        {
            object = rays[rays.length()-i].object;
            spec = getObject(object).material.specularity;
            tran = getObject(object).material.transmission;

            objectColor = getObject(object).material.color * min(castShadow(rays[rays.length()-i].position).w, selfShadow(rays[rays.length()-i].position, object).w);
            if(spec > 0.0)
            {
                objectColor = mix(objectColor,reflectionColor, spec);
            }
            if(tran > 0.0)
            {
                objectColor = mix(objectColor,refractionColor, tran);
            }

            rays[rays.length()-i].color = objectColor;
        } else {rays[rays.length()-i].color = getSkyColor(rays[rays.length()-i].incident);} 
    }
    return rays[0].color; //the issue is happening with objects 1+. Object 0 reflects ok but everything else breaks
}

