}

#define PI 3.14159265359

float pretty_sine(vec2 uv)
{
    float d = (sin(uv.x) - uv.y);
    float d1 = 4.0 * d;
    float d2 = 16.0 * d;
    float sine = 0.5 * sin(uv.x + iTime);
    float s = mix(d1, d2, sine + 0.5);
    float result = 1.0 + 0.75 * (-sine + 0.5) - abs(s);
    return result;
}

float circle(vec2 p, float r) 
{
    float result = length(p) - r;
    return result;
}

// NOTE(rytis): Taken from https://iquilezles.org/www/articles/smin/smin.htm
vec2 smin( float a, float b, float k )
{
    float h = max( k-abs(a-b), 0.0 )/k;
    float m = h*h*0.5;
    float s = m*k*(1.0/2.0);
    return (a<b) ? vec2(a-s,m) : vec2(b-s,m-1.0);
}

vec2 random2(vec2 p)
{
    vec2 result = fract(sin(vec2(dot(p, vec2(389.9, 441.1)),
                                 dot(p, vec2(598.3, 290.4)))) * PI * 20000.0);
    return result;
}

struct Pretty_Sine_Data
{
    vec2 uv;
    vec3 color;
    float multiplier;
};
vec4 edge(vec2 uv, vec2 p0, vec2 p1)
{
    vec2 diff = p1 - p0;
    vec2 n_diff = normalize(diff);
    float half_diff_length = 0.5 * length(diff);
    vec2 uv_x = vec2(1.0, 0.0);
    vec2 uv_y = vec2(0.0, 1.0);

    float cos_a = dot(uv_x, n_diff);
    float sin_a = dot(uv_y, n_diff);
    vec2 p = vec2(cos_a * uv.x + sin_a * uv.y,
                  -sin_a * uv.x + cos_a * uv.y);

    vec2 offset = 0.5 * (p0 + p1);
    vec2 xn = cos_a * uv_x + sin_a * uv_y;
    vec2 yn = -sin_a * uv_x + cos_a * uv_y;

    vec2 new_offset = vec2(dot(offset, xn), dot(offset, yn));

    p -= new_offset;

    Pretty_Sine_Data data[8] = Pretty_Sine_Data[8](
        Pretty_Sine_Data(      vec2(p.x + 0.22 * iTime, 2.0 * p.y),       vec3(0.5, 0.5, 2.0),  1.0),
        Pretty_Sine_Data(      vec2(p.x + 0.13 * iTime, 1.6 * p.y),       vec3(0.3, 0.7, 1.0),  1.0),
        Pretty_Sine_Data(2.0 * vec2(p.x + iTime, 2.0 * p.y),              vec3(0.8, 0.2, 0.6),  1.0),
        Pretty_Sine_Data(1.3 * vec2(p.x + 0.5 * iTime, 1.7 * p.y),        vec3(0.4, 0.3, 0.7),  1.0),
        Pretty_Sine_Data(0.8 * vec2(p.x - iTime, 2.5 * p.y),              vec3(0.8, 1.3, 0.8),  0.5),
        Pretty_Sine_Data(0.8 * vec2(p.x - iTime + 0.5, 2.5 * p.y), 0.8  * vec3(0.8, 1.3, 0.8),  0.5),
        Pretty_Sine_Data(0.8 * vec2(p.x - iTime + 0.9, 2.5 * p.y), 0.64 * vec3(0.8, 1.3, 0.8),  0.5),
        Pretty_Sine_Data(3.5 * vec2(p.x - 1.24 * iTime, p.y),             vec3(1.7, 0.7, 0.0),  1.0)
    );

    float output_dist = 0.0;
    vec3 output_color = vec3(0.0);

    for(int i = 0; i < 8; ++i)
    {
        float d = pretty_sine(data[i].uv);
        float limit = (p.x < -half_diff_length || p.x > half_diff_length) ? 0.0 : 1.0;
        float cd = limit * data[i].multiplier * clamp(d, 0.0, 1.0);
        output_color += cd * data[i].color;
        output_dist += cd;
    }
    return vec4(output_color, -0.125 * output_dist);
}

// NOTE(rytis): Taken from https://fgiesen.wordpress.com/2013/02/06/the-barycentric-conspirac/
float edge_f(vec2 p, vec2 e0, vec2 e1)
{
    float result = (e0.y - e1.y) * p.x + (e1.x - e0.x) * p.y + e0.x * e1.y - e0.y * e1.x;
    return result;
}

struct Point
{
    vec3 color;
    float d;
    vec2 p;
};
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.xy;
    float aspect_ratio = iResolution.x / iResolution.y;
    uv.x *= aspect_ratio;
    uv.x -= 0.5 * (aspect_ratio - 1.0);
    uv = uv * 2.0 - 1.0;

    uv *= 8.0;

    vec2 p0 = vec2(-3.0 + sin(iTime), -5.0 + cos(iTime));
    vec2 p1 = vec2(7.0 + cos(0.5 * iTime), 0.0 - sin(iTime));
    vec2 p2 = vec2(-2.0 + cos(0.5 * iTime), 5.0 - sin(0.44 * iTime));

    Point points[3] = Point[3](
        Point(vec3(0.5), circle(uv - p0, 1.2), p0),
        Point(vec3(0.5), circle(uv - p1, 1.2), p1),
        Point(vec3(0.5), circle(uv - p2, 1.2), p2)
    );

    vec3 color = vec3(0.0);
    float area_tri = 0.5 * edge_f(p0, p1, p2);
    float inv_area_tri = 1.0 / area_tri;
    float area_p01 = edge_f(uv, p0, p1);
    float area_p12 = edge_f(uv, p1, p2);
    float area_p20 = edge_f(uv, p2, p0);

    float multiplier = smoothstep(-0.25, 0.5, -inv_area_tri * min(min(area_p01, area_p12), area_p20));
    float waves = 0.0;
    waves += multiplier * smoothstep(1.0, 1.5, pretty_sine(uv / 8.0));
    waves += multiplier * smoothstep(1.0, 1.5, pretty_sine(vec2(uv.x, -uv.y) / 4.0));
    waves += multiplier * smoothstep(1.0, 1.5, pretty_sine(uv / 2.0));

    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);

    float min_dist = 1.0;
    for(int i = -1; i <= 1; ++i)
    {
        for(int j = -1; j <= 1; ++j)
        {
            vec2 neighbor = vec2(float(i), float(j));
            vec2 random_point = random2(i_uv + neighbor);
            random_point = 0.5 + 0.5 * vec2(sin(2.0 * PI * random_point + 0.5 * iTime));
            vec2 diff = neighbor + random_point - f_uv;
            float dist = length(diff);
            min_dist = min(dist, min_dist);
        }
    }
    vec2 waves_smooth = smin(min_dist, waves, 0.1);
    color += multiplier * 3.0 * smoothstep(0.5, -0.5, -waves_smooth.y) * vec3(0.20, 0.23, 0.3);
    color += multiplier * min_dist * vec3(0.20, 0.23, 0.3);


    if(area_p01 >= 0.0 && area_p12 >= 0.0 && area_p20 >= 0.0)
    {
        color = mix(color, vec3(0.5), 3.0 * inv_area_tri * min(min(area_p01, area_p12), area_p20));
    }

    vec4 e0 = edge(uv, p0, p1);
    vec4 e1 = edge(uv, p1, p2);
    vec4 e2 = edge(uv, p2, p0);
    color += e0.xyz;
    color += e1.xyz;
    color += e2.xyz;
    float edge_dist = min(min(e0.w, e1.w), e2.w);
    for(int i = 0; i < 3; ++i)
    {
        if(points[i].d < edge_dist)
        {
            vec2 smooth_dist = smin(points[i].d, edge_dist, 0.3);
            vec2 p_on_sphere = uv - points[i].p;
            vec3 sphere_normal = normalize(vec3(p_on_sphere.x, p_on_sphere.y, smooth_dist.x));
            vec3 light_dir = normalize(-vec3(uv.x, uv.y, 4.0));
            float atten = max(dot(sphere_normal, light_dir), 0.0);
            color = mix(points[i].color * (0.5 + 0.5 * atten * atten), color, smooth_dist.y);
        }
    }

    fragColor = vec4(color, 1.0);
}

#define S smoothstep
#define P 3.14159265
mat2 r(float a){
    return mat2(cos(a),-sin(a),
                sin(a),cos(a));
}
float m (vec2 u, float t) {
    u *= r(t);
    return S(-P,P,atan(u.x, u.y)) * S(0.45, 1., 1. - length(u));
}
void mainImage( out vec4 O, in vec2 I )
{
    vec2 R = iResolution.xy;
    float t = iTime;
    vec2 u = (I-.5*R)/R.y;
    float c1 = m(vec2(u.x + .25, u.y), sin(t)*P+P*.5);
    float c2 = m(vec2(u.x - .25, u.y), sin(-t)*P+P*.5);
    vec3 C1 = vec3(0.1, 0.1, 0.9);
    vec3 C2 = vec3(0.9, 0.1, 0.1);
    O = vec4(mix(C1, C2, c1) * mix(C1, C2, 1. - c2),1.0);
}

float rand(vec2 p){
    return fract(sin(dot(p, vec2(12.9898, 78.233))) * 43758.5453);
}

float drawLayer(float cellNum,vec2 uv){
    //纹理坐标放大cellNum倍，目的是为了将屏幕分割成cellNum * (cellNum*ratio)块，然后计算出coord和pt
    vec2 uv0 = uv*cellNum;
    //避免排列太整齐，根据y坐标随机左右平移
    uv0.x+=rand(vec2(floor(uv0.y)));

    //当前是哪一个格子
    vec2 coord = floor(uv0);
    //当前这个点，在这个格子内的坐标，xy的值范围【0,1】
    vec2 pt = fract(uv0);

    //随机出一个半径大小，此时为【0,1】
    float radius = rand(coord);
    //一个跟半径相关的亮度
    float strength = radius;

    //真实径范围【0.2,0.45】*1.0/cellNum;
    radius = radius*0.25+0.2;

    //去掉2/3的点
    if(mod(radius*10.0,3.0)<2.0){
        return 0.0;
    }

    //当前点距离圆心的范围
    float dis = distance(pt,vec2(0.5,0.5));

    //强度最后再整体根据cellNum区分一下，加强层次感
    strength*=1.0/cellNum*12.0;
    //strength*=1.0-dis*2.0;

    //画圆
    float a = smoothstep(radius-0.03,radius,dis);
    a = 1.0-a;

    a*=strength;
    return a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //纹理坐标归一化，y轴0-1
    vec2 uv = fragCoord/iResolution.y;
    vec3 col = vec3(0.0,0.0,0.0);

    //三层颜色
    vec3 arr[3];
    arr[0] = vec3(0.64,0.18,0.80);    
    arr[1] = vec3(0.67,0.71,0.31);
    arr[2] = vec3(0.9,0.0,0.2);

    //背景色
    vec3 color_left = vec3(0.79,0.28,0.29);
    vec3 color_right = vec3(0.4,0.4,0.2);

    float z = 0.0;//记录这个点是否已经绘制过
    //分三层绘制，颜色相加
    for(int i=0;i<=2;i++){
        vec3 color = arr[i];
        //cellNum 每一层将屏幕分成多少个格子
        float cellNum = float(i)*2.5+5.0;
        //随机出一个方向，根据时间做偏移
        vec2 uvoff = vec2( rand( vec2(cellNum*2.0) ), 0.5+0.5*rand( vec2( cellNum * 12.0 ) ) );
        uvoff = uvoff * iTime * 0.1;
        vec2 pt = uv+uvoff;

        //当前坐标的透明度
        float a=drawLayer(cellNum,pt);

        //已经有颜色的，避免覆盖，以免分层穿帮
        if(z<0.01){
            col += color*a;
        }
        z += a;
    }

    col+=mix(color_left,color_right,uv.x);

    // Output to screen
    fragColor = vec4(col,1.0);

}

// Lagrangian variant of https://shadertoy.com/view/fscSWH

#define hash(p)  fract(sin((p)*mat2(127.1,311.7, 269.5,183.3)) *43758.5453123)
#define T(c)     texelFetch(c, ivec2(U) % textureSize(c,0).xy, 0).xy 

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, U, P = u/R, T,D,H;
    u.x += float(iFrame);
    T = u / 16.;
    O-=O;
    float d = 99.;
    for ( float i=0.; i < 256.*(1.-P.x); i++ ) {
        U = floor(T) + i*13.7;
        H =   P.y > .66 ? hash(U)
            : P.y > .33 ? T(iChannel0) 
            :             T(iChannel1);
        D = fract(T) - H;
        d = min(d, dot(D,D));
     // O = max(O, 1.3 - 16.*sqrt(d) );
     // O += (1.-O) * clamp( 1.3 - 16.*sqrt(d) , 0.,1.);
    }

    O = vec4( 1.3 - 16.*sqrt(d) );   // draw closest point
    if ( int(u.y) % (int(R.y)/3) == 0 ) O = vec4(1,0,0,1); // horizontal separator
    u = fract((u-.5)/16.);
    if ( iMouse.z>0. && u.x*u.y==0.) O = vec4(0,0,1,1);    // if click, draw grid
}

const int MAX_STEP = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.001;
/* 一个立方体的SDF */
float cubeSDF(vec3 p) {
    // If d.x < 0, then -1 < p.x < 1, and same logic applies to p.y, p.z
    // So if all components of d are negative, then p is inside the unit cube
    vec3 d = abs(p) - vec3(1.0, 1.0, 1.0);

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

/* 一个球的SDF */
float sphereSDF(vec3 samplePoint){
    return length(samplePoint) - 1.414;
}

/* 场景的SDF，为球和立方体的交集 */
float sceneSDF(vec3 samplePoint){
    float res = max(cubeSDF(samplePoint),sphereSDF(samplePoint));
    return res;
}

/* 从眼睛朝某一方向，发出光线，直到击中场景表面，之间的距离 */
float shortestDistanceFromEyeToSurface(vec3 eye, vec3 rayDirection, float start, float end){
    float depth = start;
    for(int i = 0; i < MAX_STEP && depth < end;i++){
        float dist = sceneSDF(eye+depth*rayDirection);
        if(dist < EPSILON) 
            return depth;
        else 
            depth += dist;
    }
    return end;
}
/* 根据视场角以及视口的尺寸，生成到每一个像素的光线方向 */
vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

/* 根据SDF的梯度来估计法线方向 
   p 点在SDF表面，n = (dp/dx,dp/dy,dp/dz)

*/
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}
/* 计算 Phong 光照 c = c_r(c_a + c_l*max(0.0,dot(N,L)) + c_l*c_p*pow(max(0.0,dot(R,E)),s))
*/
vec3 PhongLight(vec3 c_r, vec3 c_a, vec3 c_p, vec3 c_l,vec3 eye, vec3 p_l, vec3 p, float s){
    vec3 N = estimateNormal(p);
    vec3 L = normalize(p_l - p);
    vec3 E = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotNL = max(0.0,dot(N,L));
    float dotRE = max(0.0,dot(R,E));
    vec3 c = c_r*(c_a + c_l*dotNL + c_l*c_p*pow(dotRE,s));
    return c;
}
/* 计算光照，在这个函数设定光源参数，然后调用 PhongLight 计算光照
*/
vec3 illumination(vec3 c_r, vec3 c_a, vec3 c_p, vec3 eye, vec3 p, float s) {
    vec3 c_l = vec3(1.0);
    vec3 p_l = vec3(4.0 * sin(iTime),
                          1.0,
                          4.0 * cos(iTime));
    vec3 color = PhongLight(c_r,c_a,c_p,c_l,eye,p_l,p,s);    
    return color;
}
/* 摄像机的旋转矩阵 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    vec3 v = normalize(eye - center);
    vec3 r = normalize(cross(up,v));
    vec3 u = cross(v,r);
    vec3 t = vec3(-dot(eye,r),-dot(eye,u),-dot(eye,v));
    return mat4(
        vec4(r,t.x),
        vec4(u,t.y),
        vec4(v,t.z),
        vec4(0.0,0.0,0.0,1.0)
    );
}
