    vec3 worldDir = (rotateMatrix*vec4(viewDir,0.0)).xyz;
    float dist = shortestDistanceFromEyeToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
