bool PixelInTriangle(vec3 p, vec3 v0, vec3 v1, vec3 v2) {
    // my thanks to /user/meteotrix
    vec3 projd_v0 = vec3(v0.xy, 0.0);
    vec3 projd_v1 = vec3(v1.xy, 0.0);
    vec3 projd_v2 = vec3(v2.xy, 0.0);

    vec3 edge01 = projd_v1 - projd_v0;
    vec3 edge12 = projd_v2 - projd_v1;
    vec3 edge20 = projd_v0 - projd_v2;
    vec3 edge0P = p - projd_v0;
    vec3 edge1P = p - projd_v1;
    vec3 edge2P = p - projd_v2;

    return (cross(edge01, edge0P).z >= 0.0) && (cross(edge12, edge1P).z >= 0.0) && (cross(edge20, edge2P).z >= 0.0);
}

bool RecursiveInTriangle(int count, vec3 p, vec3 v0, vec3 v1, vec3 v2) {
    bool answer = false;

    while (count > 0) {
        if (PixelInTriangle(p, v0, v1, v2)) {
            answer = !answer;
        }
        vec3 t0 = (v0 + v1) / 2.0;
        vec3 t1 = (v1 + v2) / 2.0;
        vec3 t2 = (v2 + v0) / 2.0;
        v0 = t0;
        v1 = t1;
        v2 = t2;
        count--;
    }

    return answer;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;
    vec3 col = vec3(0);

    float size = (iResolution.x * (0.5 + iTime * iTime * 0.02));
    float angle = iTime * 0.7; // 
    vec3 center = vec3(iResolution.xy / 2.0, 0.0);

    //vertices
    vec3 v0 = center + vec3(cos(angle) * size, sin(angle) * size, 0.0);
    angle += 120.0 * 3.14159265359 / 180.0;
    vec3 v1 = center + vec3(cos(angle) * size, sin(angle) * size, 0.0);
    angle += 120.0 * 3.14159265359 / 180.0;
    vec3 v2 = center + vec3(cos(angle) * size, sin(angle) * size, 0.0);

    vec3 pixel = vec3(uv.xy,0.0);

    col += (RecursiveInTriangle(20, pixel, v0, v1, v2) ? 1.0 : 0.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

/* Original code from https://patriciogonzalezvivo.github.io/PixelSpiritDeck */

// These 2 are the main tweaks we're gonna add to an icon
// We can make bigger tweaks, but we don't wanna write the 
// icon differently; just small deltas.
#define S (1. + sin(iTime) / 4.)
#define C (1. + cos(iTime) / 4.)

// Aliasing is apparent when things move. The original Pixel
// Spirit cards use `step` everywhere. Change most of them to
// smoothstep for anti-aliasing. Some later icons still need 
// regular `step` due to their idiosyncratic usages
float sstep(float a, float b) {
    return smoothstep(a - .005, a + .005, b);
}

// === Start!

const float PI = 3.14159;
const float TAU = PI * 2.;
const float QTR_PI = PI / 4.;

// === All the utils, with their original card numbers

float stroke(float x, float s, float w) { // 04
    float d = sstep(s, x + w / 2.) - sstep(s, x - w / 2.);
    return clamp(d, 0., 1.);
}
float circleSDF(vec2 st) { // 08
    return length(st - 0.5) * 2.;
}
float fill(float x, float size) { // 09
    return 1. - sstep(size, x);
}
float rectSDF(vec2 st, vec2 s) { // 10
    st = st * 2. - 1.;
    return max(abs(st.x / s.x), abs(st.y / s.y));
}
float crossSDF(vec2 st, float s) { // 11
    vec2 size = vec2(0.25, s);
    return min(rectSDF(st, size.xy), rectSDF(st, size.yx));
}
float flip(float v, float pct) { // 12
    return mix(v, 1. - v, pct);
}
float vesicaSDF(vec2 st, float w) { // 14
    vec2 offset = vec2(w * .5, 0.);
    return max(circleSDF(st - offset), circleSDF(st + offset));
}
float triSDF(vec2 st) { // 16
    st = (2. * st - 1.) * 2.;
    return max(abs(st.x) * 0.866025 + st.y * .5, -st.y * .5);
}
float rhombSDF(vec2 st) { // 17
    return max(triSDF(st), triSDF(vec2(st.x, 1. - st.y)));
}
vec2 rotate(vec2 st, float a) { // 19
    st = mat2(cos(a), -sin(a), sin(a), cos(a)) * (st - .5);
    return st + .5;
}
float polySDF(vec2 st, int V) { // 26
    st = st * 2. - 1.;
    float a = atan(st.x, st.y) + PI;
    float r = length(st);
    float v = TAU / float(V);
    return cos(floor(.5 + a / v) * v - a) * r;
}
float hexSDF(vec2 st) { // 27
    st = abs(st * 2. - 1.);
    return max(abs(st.y), st.x * 0.866025 + st.y * .5);
}
float starSDF(vec2 st, int V, float s) { // 28
    st = st * 4. - 2.;
    float a = atan(st.y, st.x) / TAU;
    float seg = a * float(V);
    a = ((floor(seg) + 0.5) / float(V) +
        mix(s, -s, step(.5, fract(seg))))
        * TAU;
    return abs(dot(vec2(cos(a), sin(a)), st));
}
float raysSDF(vec2 st, int N) { // 30
    st -= .5;
    return fract(atan(st.y, st.x) / TAU * float(N));
}
float heartSDF(vec2 st) { // 34
    st -= vec2(.5, .8);
    float r = length(st) * 5.;
    st = normalize(st);
    return r -
        ((st.y * pow(abs(st.x), 0.67)) /
        (st.y + 1.5) - 2. * st.y + 1.26);
}
float bridge(float c, float d, float s, float w) { // 35
    c *= 1. - stroke(d, s, w * 2.);
    return c + stroke(d, s, w);
}
float spiralSDF(vec2 st, float t) { // 47
    st -= .5;
    float r = dot(st, st);
    float a = atan(st.y, st.x);
    return abs(sin(fract(log(r) * t + a * 0.159)));
}
vec2 scale(vec2 st, vec2 s) {
    return (st - .5) * s + .5;
}
float flowerSDF(vec2 st, int N) {
    st = st * 2. - 1.;
    float r = length(st) * 2.;
    float a = atan(st.y, st.x);
    float v = float(N) * .5;
    return 1. - (abs(cos(a * v)) * .5 + .5) / r;
}

// === Cards

float draw(vec2 st, vec2 tileXY, vec2 count) {
    // y is bottom to top, but we want to show things from top to bottom. Convert
    int cardNumber = int(tileXY.x + (-tileXY.y + count.y - 1.) * count.x);
    float color = 0.;

    switch (cardNumber) {
    case 0: { // void
        color = 0.;
        break;
    }
    case 1: { // justice
        color = sstep(0.5 * S, st.x);
        break;
    }
    case 2: { // strength
        color = sstep(0.5 + cos(st.y * PI + iTime/2.) * 0.25, st.x);
        break;
    }
    case 3: { // death
        color = sstep(0.5, (st.x * S + st.y * C) * 0.5);
        break;
    }
    case 4: { // wall
        color = stroke(st.x, 0.5, 0.15*S);  
        break;
    }
    case 5: { // temperance
        float offset = cos(st.y * PI + iTime) * 0.15;
        color = stroke(st.x, .28 + offset, 0.1);
        color += stroke(st.x, .5 + offset, 0.1);
        color += stroke(st.x, .72 + offset, 0.1);
        break;
    }
    case 6: { // branch
        float offset = 0.5 + (st.x - st.y) * 0.5;
        color = stroke(offset, 0.5, 0.1 * S);
        break;
    }
    case 7: { // the hanged man
        float sdf = 0.5 + (st.x - st.y) * 0.5;
        color = stroke(sdf, 0.5, 0.1 * C);
        float sdf_inv = (st.x + st.y) * 0.5;
        color += stroke(sdf_inv, 0.5, 0.1 * C);
        break;
    }
    case 8: { // the high priestess
        color = stroke(circleSDF(st), 0.5 * S, 0.05 * C);
        break;
    }
    case 9: { // the moon
        color = fill(circleSDF(st), 0.65);
        vec2 offset = vec2(0.1, 0.05);
        color -= fill(circleSDF(st - offset * S), 0.5);
        break;
    }
    case 10: { // the emperor
        float sdf = rectSDF(st, vec2(1.));
        color = stroke(sdf, .5 * C, .125);
        color += fill(sdf, .1 * S);
        break;
    }
    case 11: { // the hierophant
        float rect = rectSDF(st, vec2(1));
        color = fill(rect, .5);
        float cross = crossSDF(st, 1.);
        color *= sstep(.5, fract(cross * 3. + iTime));
        color *= sstep(1., cross);
        color += fill(cross, .5);
        color += stroke(rect, .65, .05);
        color += stroke(rect, .75, .025);
        break;
    }
    case 12: { // the tower
        float rect = rectSDF(st, vec2(.5, 1.));
        float diag = (st.x * C + st.y * S) * .5;
        color = flip(fill(rect, .6), stroke(diag, .5, .01));
        break;
    }
    case 13: { // merge
        vec2 offset = vec2(.15 * S, 0);
        float left = circleSDF(st + offset);
        float right = circleSDF(st - offset);
        color = flip(stroke(left, .5, .05), fill(right, 0.525));
        break;
    }
    case 14: { // hope
        float sdf = vesicaSDF(st, .2 * S);
        color = flip(fill(sdf, .5), sstep((st.x + st.y) * .5, .5));
        break;
    }
    case 15: { // the temple
        st.y = 1. - st.y;
        vec2 ts = vec2(st.x, .82 - st.y);
        color = fill(triSDF(st), .7);
        color -= fill(triSDF(ts), .36);
        break;
    }
    case 16: { // the summit
        float circle = circleSDF(st - vec2(.0, .1));
        float triangle = triSDF(st + vec2(.0, .1));
        color = stroke(circle, .5 * C, .1);
        color *= sstep(.55, triangle);
        color += fill(triangle, .45);
        break;
    }
    case 17: { // the diamond
        float sdf = rhombSDF(st);
        color = fill(sdf, .425 * S);
        color += stroke(sdf, .5 * S, .05);
        color += stroke(sdf, .6 * C, .03);
        break;
    }
    case 18: { // the hermit
        color = flip(fill(triSDF(st), .5), fill(rhombSDF(st), .4));    
        break;
    }
    case 19: { // intuition
        st = rotate(st, radians(-25.) * S);
        float sdf = triSDF(st);
        sdf /= triSDF(st + vec2(0., .2 * C));
        color = fill(abs(sdf), .56);
        break;
    }
    case 20: { // the stone
        st = rotate(st, radians(45.));
        color = fill(rectSDF(st, vec2(1.)), .4);
        color *= 1. - stroke(st.x, .5 * S, .02);
        color *= 1. - stroke(st.y, .5 * C, .02);
        break;
    }
    case 21: { // the mountain
        st = rotate(st, radians(-45.));
        float off = .12 * S;
        vec2 s = vec2(1.);
        color = fill(rectSDF(st + off, s), .2 * C);
        color += fill(rectSDF(st - off, s), .2 * C);
        float r = rectSDF(st, s);
        color *= sstep(.33, r);
        color += fill(r, .3);
        break;
    }
    case 22: { // the shadow
        st = rotate(vec2(st.x, 1. - st.y), radians(45.));
        vec2 s = vec2(1.);
        color += fill(rectSDF(st - .025 * S, s), .4);
        color += fill(rectSDF(st + .025, s), .4);
        color *= sstep(0.38, rectSDF(st + .025, s));
        break;
    }
    case 23: { // opposite
        st = rotate(st, radians(-45.));
        vec2 s = vec2(1.);
        float o = .05 * S * 1.5;
        color += flip(
            fill(rectSDF(st - o, s), .4), 
            fill(rectSDF(st + o, s), .4)
        );
        break;
    }
    case 24: { // the oak
        st = rotate(st, radians(45.));
        float r1 = rectSDF(st, vec2(1.) * S);
        float r2 = rectSDF(st + .15 * S, vec2(1.));
        color += stroke(r1, .5, .05);
        color *= sstep(.325, r2);
        color += stroke(r2, .325, .05) * fill(r1, .525);
        color += stroke(r2, .2, .05);
        break;
    }
    case 25: { // ripples
        st = rotate(st, radians(-45.)) - .08;
        for (int i = 0; i < 4; i++) {
            float r = rectSDF(st, vec2(1.) * S);
            color += stroke(r, .19, .04);
            st += .05;
        }
        break;
    }
    case 26: { // the empress
        float d1 = polySDF(st, 5);
        vec2 ts = vec2(st.x, 1. - st.y);
        float d2 = polySDF(ts, 5);
        color = fill(d1, .75) * fill(fract(d1 * 5. - iTime/2.), .5);
        color -= fill(d1, .6) * fill(fract(d2 * 4.9 - iTime/2.), .45);
        break;
    }
    case 27: { // bundle
        st = st.yx;
        color = stroke(hexSDF(st), .6 * C, .1);
        color += fill(hexSDF(st - vec2(-.06, -.1) * S), .15);
        color += fill(hexSDF(st - vec2(-.06, .1) * S), .15);
        color += fill(hexSDF(st - vec2(.11, 0.) * S), .15);
        break;
    }
    case 28: { // the devil
        color += stroke(circleSDF(st), .8 * C, .05);
        st.y = 1. - st.y;
        float s = starSDF(st.yx, 5, .1);
        color *= sstep(.7 * C, s);
        color += stroke(s, .4 * S, .1);
        break;
    }
    case 29: { // the sun
        float bg = starSDF(st, 16, .1 * S);
        color += fill(bg, 1.3);
        float l = 0.;
        for (float i = 0.; i < 8.; i++) {
            vec2 xy = rotate(st, QTR_PI * i+iTime/4.);
            xy.y -= .3;
            float tri = polySDF(xy, 3);
            color += fill(tri, .3);
            l += stroke(tri, .3 * S, .03);
        }
        color *= 1. - l;
        float c = polySDF(st, 8);
        color -= stroke(c, .15, .04);
        break;
    }
    case 30: { // the star
        color = stroke(raysSDF(st, 8), .5, .15 * C * 2.);
        float inner = starSDF(st.xy, 6, .09 * S);
        float outer = starSDF(st.yx, 6, .09 * S);
        color *= sstep(.7, outer);
        color += fill(outer, .5);
        color -= stroke(inner, .25, .06);
        color += stroke(outer, .6, .05);
        break;
    }
    case 31: { // judgement
        color = flip(
            stroke(raysSDF(rotate(st, -iTime/8.), 28), .5, .2), 
            fill(st.y, .5)
        );
        float rect = rectSDF(st, vec2(1) * S);
        color *= sstep(.25, rect);
        color += fill(rect, .2);
        break;
    }
    case 32: { // wheel of fortune
        float sdf = polySDF(rotate(st.yx, C), 8);
        color = fill(sdf, .5);
        color *= stroke(raysSDF(rotate(st, C), 8), .5, .2);
        color *= sstep(.27, sdf);
        color += stroke(sdf, .2, .05);
        color += stroke(sdf, .6, .1);
        break;
    }
    case 33: { // vision
        float v1 = vesicaSDF(st, .5);
        vec2 st2 = st.yx + vec2(.04, .0);
        float v2 = vesicaSDF(st2, .7);
        color = stroke(v2, 1., .05);
        st = rotate(st, iTime/2.);
        color += fill(v2, 1.) * stroke(circleSDF(st - vec2(.05)), .3 , .05);
        color += fill(raysSDF(st, 50), .2) *
            fill(v1, 1.25) *
            sstep(1., v2);
        break;
    }
    case 34: { // the lovers
        color = fill(heartSDF(st), .5 * C * 1.2);
        color -= stroke(polySDF(st, 3), .15 * S * 1.1, .05);
        break;
    }
    case 35: { // the magician
        st.x = flip(st.x, step(.5, st.y));
        vec2 offset = vec2(.15 * S, .0);
        float left = circleSDF(st + offset);
        float right = circleSDF(st - offset);
        color = stroke(left, .4 * S, .075);
        color = bridge(color, right, .4 * S, .075);
        break;
    }
    case 36: { // the link
        st = st.yx;
        st.x = mix(1. - st.x, st.x, step(.5, st.y));
        vec2 o = vec2(.1, .0);
        vec2 s = vec2(1.) * C;
        float a = radians(45.) + iTime/2.;
        float l = rectSDF(rotate(st + o, a), s);
        float r = rectSDF(rotate(st - o, -a), s);
        color = stroke(l, .3, .1);
        color = bridge(color, r, .3, .1);
        color += fill(rhombSDF(abs(st.yx - vec2(.0, .5))), .1);
        break;
    }
    case 37: { // holding together
        st.x = mix(1. - st.x, st.x, step(.5, st.y));
        vec2 o = vec2(.05, .0);
        vec2 s = vec2(1.);
        float a = radians(45.);
        float l = rectSDF(rotate(st + o, a * S), s);
        float r = rectSDF(rotate(st - o, -a * S), s);
        color = stroke(l, .145, .098);
        color = bridge(color, r, .145, .098);
        break;
    }
    case 38: { // the chariot
        float r1 = rectSDF(st, vec2(1.));
        float r2 = rectSDF(rotate(st, radians(45.)), vec2(1.));
        float inv = step(.5, (st.x + st.y) * .5);
        inv = flip(inv, step(.5, .5 + (st.x - st.y) * .5));
        float w = .075 * S * 1.2;
        color = stroke(r1, .5, w) + stroke(r2, .5, w);
        float bridges = mix(r1, r2, inv);
        color = bridge(color, bridges, .5, w);
        break;
    }
    case 39: { // the loop
        float inv = sstep(.5, st.y);
        st = rotate(st, radians(-45.)) - .2;
        st = mix(st, .6 - st, sstep(.5, inv));
        for (int i = 0; i < 5; i++) {
            float r = rectSDF(st, vec2(1.));
            float s = .25;
            s -= abs(float(i) * .1 - .2);
            color = bridge(color, r, s, .05 * S);
            st += .1;
        }
        break;
    }
    case 40: { // turning point
        st = rotate(st, radians(-60.) + iTime/4.);
        st.y = flip(st.y, step(.5, st.x));
        st.y += .25;
        float down = polySDF(st, 3);
        st.y = 1.5 - st.y;
        float top = polySDF(st, 3);
        color = stroke(top, .4, .15 * S);
        color = bridge(color, down, .4, .15 * S);
        break;
    }
    case 41: { // trinity
        st.y = 1. - st.y;
        float s = .25 * C*1.3;
        float t1 = polySDF(st + vec2(.0, .175), 3);
        float t2 = polySDF(st + vec2(.1, .0), 3);
        float t3 = polySDF(st - vec2(.1, .0), 3);
        color = stroke(t1, s, .08) +
                stroke(t2, s, .08) +
                stroke(t3, s, .08);
        float bridges = mix(
            mix(t1, t2, step(.5, st.y)),
            mix(t3, t2, step(.5, st.y)),
            step(.5, st.x)
        );
        color = bridge(color, bridges, s, .08);
        break;
    }
    case 42: { // the cauldron
        float n = 12.;
        float a = TAU / n;
        for (float i = 0.; i < n; i++) {
            vec2 xy = rotate(st, a * i);
            xy.y -= .189;
            float vsc = vesicaSDF(xy, .3);
            color *= 1. - stroke(vsc, .45 * S, .1) * sstep(.5, xy.y);
            color += stroke(vsc, .45 * S, .05);
        }
        break;
    }
    case 43: { // the elders
        float n = 3.;
        float a = TAU / n;
        for (float i = 0.; i < n * 2.; i++) {
            vec2 xy = rotate(st, a * i);
            xy.y -= .09;
            float vsc = vesicaSDF(xy, .3);
            color = mix(
                color + stroke(vsc, .5, .1*S),
                mix(color, bridge(color, vsc, .5, .1*S), step(xy.x, .5) - step(xy.y, .4)),
                step(3., i)
            );

        }
        break;
    }
    case 44: { // the core
        float star = starSDF(st, 8, .063);
        color += fill(star, 1.22);
        float n = 8.;
        float a = TAU / n;
        for (float i = 0.; i < n; i++) {
            vec2 xy = rotate(st, 0.39 + a * i);
            xy = scale(xy, vec2(1., .72) * S);
            xy.y -= .125;
            color *= sstep(.235, rhombSDF(xy));
        }
        break;
    }
    case 45: { // inner truth
        st -= .5;
        float r = dot(st, st);
        float a = atan(st.y, st.x) / PI;
        vec2 uv = vec2(a, r);
        vec2 grid = vec2(5., log(r) * 20. * S);
        vec2 uv_i = floor(uv * grid);
        uv.x += .5 * mod(uv_i.y, 2.);
        vec2 uv_f = fract(uv * grid);
        float shape = rhombSDF(uv_f);
        color += fill(shape, .9) * sstep(.75, 1. - r);
        break;
    }
    case 46: { // the world
        color = fill(flowerSDF(rotate(st, -iTime/4.), 5), .25*C);
        color -= sstep(.95, starSDF(rotate(st, 0.628 - iTime/4.), 5, .1*S));
        color = clamp(color, 0., 1.);
        float circle = circleSDF(st);
        color -= stroke(circle, .1, .05);
        color += stroke(circle, .8, .07);
        break;
    }
    case 47: { // the fool
        color = sstep(.5, spiralSDF(rotate(st, iTime/2.), .13 * S));
        break;
    }
    case 48: { // enlightenment
        color = 1.;
        break;
    }
    case 49: { // elements. Added by me. Approximated
        st = rotate(st, -iTime/4.);
        float d = .15;
        float r = .3 * S;
        color = fill(circleSDF(st - vec2(cos(TAU / 3.), sin(TAU / 3.)) * d), r);
        color += fill(circleSDF(st - vec2(cos(TAU / 3. * 2.), sin(TAU / 3. * 2.)) * d), r);
        color += fill(circleSDF(st - vec2(d, 0.)), r);
        st = st.yx;
        st.y = 1. - st.y;
        color *= 1. - fill(triSDF(st-vec2(0, .02)), .13);
        color += stroke(circleSDF(st), .8, .08);
        break;
    }
    }
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord / iResolution.xy;
    float coordAspectRatio = iResolution.y / iResolution.x;

    vec2 count = vec2(10, 5);
    float tileW = iResolution.x / count.x;
    float tileH = iResolution.y / count.y;
    float tileAspectRatio = tileH / tileW;

    vec2 tileXY = floor(uv * count);
    // coordinates for each tile
    vec2 st = vec2(
        uv.x * count.x - tileXY.x, 
        (uv.y * count.y - tileXY.y - 0.5) * tileAspectRatio + .5
    );

    vec2 gridBars = clamp(cos(uv * TAU * count) * 10. - 9.8, 0., 1.); // ---^---^---
    float grid = max(gridBars.x, gridBars.y);

    float color = draw(st, tileXY, count);
    color = clamp(color + grid, 0., 1.);

    if (iMouse.z > 0.01) {
        tileXY = floor(iMouse.xy / iResolution.xy * count);
        st.x = (uv.x - .5) / coordAspectRatio + .5;
        st.y = uv.y;

        color *= 0.15;
        color += clamp(draw(st, tileXY, count), 0., .85);
    }

    fragColor = vec4(color);
}

// Length between a starting (t1) and ending (t2) position
float bezierArclength(in float t1, in float t2, in vec2 a, in vec2 b, in vec2 c) {
    vec2 abc = a - 2.0 * b + c;
    vec2 ba = b - a;

    float u = dot(abc, abc);
    float v = dot(abc, ba);
    float w = dot(ba, ba);

    float dis = u * w - v * v;
    vec2 t = (u * vec2(t1, t2) + v) / sqrt(dis);
    vec2 y = asinh(t) + t * sqrt(t * t + 1.0);
    return (y.y - y.x) * dis / pow(u, 1.5);
}

// Newton-Raphson solver for inverse arclength
float bezierInvArclength(in float d, in vec2 a, in vec2 b, in vec2 c) {
    vec2 abc = a - 2.0 * b + c;
    vec2 ba = b - a;

    float u = dot(abc, abc);
    float v = dot(abc, ba);
    float w = dot(ba, ba);

    float dis = u * w - v * v;
    float sdis = sqrt(dis);
    float disu = dis / u;
    float k = dis / pow(u, 1.5);

    float y = v / sdis;
    d += (asinh(y) + y * sqrt(y * y + 1.0)) * k;

    float t = ((sqrt(disu + u * abs(d)) - sqrt(disu)) * sign(d) - v) / u;
    for (int n=0; n < 5; n++) {
        float y = (u * t + v) / sdis;
        float arclen = (asinh(y) + y * sqrt(y * y + 1.0)) * k;
        float arcint = 2.0 * sqrt((u * t + 2.0 * v) * t + w);
        t -= (arclen - d) / arcint;
    }

    return t;
}

// ---------- Helper functions ----------
vec3 hue2rgb(in float hue) {
    return abs(2.0 * smoothstep(0.0, 1.0, fract(vec3(hue, hue - 1.0 / 3.0, hue + 1.0 / 3.0))) - 1.0);
}

mat2 Rotate2D(in float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

// https://www.shadertoy.com/view/4djSRW
float Hash11(in float p) {
    p = fract(p * 0.1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

// https://www.shadertoy.com/view/4djSRW
float Hash21(in vec2 p) {
	vec3 p3 = fract(p.xyx * 0.1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float snoise(in vec2 p) {
    vec2 c = floor(p);
    vec2 f = fract(p);//smoothstep(0.0, 1.0, fract(p));

    float bl = Hash21(c);
    float br = Hash21(c + vec2(1.0, 0.0));
    float tl = Hash21(c + vec2(0.0, 1.0));
    float tr = Hash21(c + 1.0);

    return mix(mix(bl, br, f.x), mix(tl, tr, f.x), f.y);
}

// ---------- SDFs ----------
// https://www.iquilezles.org/www/articles/distfunctions2d/distfunctions2d.htm
float sdStar5(in vec2 p, in float r, in float rf) {
    const vec2 k1 = vec2(0.809016994375, -0.587785252292);
    const vec2 k2 = vec2(-k1.x, k1.y);
    p.x = abs(p.x);
    p -= 2.0 * max(dot(k1, p), 0.0) * k1;
    p -= 2.0 * max(dot(k2, p), 0.0) * k2;
    p.x = abs(p.x);
    p.y -= r;
    vec2 ba = rf * vec2(-k1.y, k1.x) - vec2(0.0, 1.0);
    float h = clamp(dot(p, ba) / dot(ba, ba), 0.0, r);
    return length(p - ba * h) * sign(p.y * ba.x - p.x * ba.y);
}

// Returns vec2(shortest distance to the curve, parameter of the nearest position on the curve)
float dot2(in vec2 v) { return dot(v, v); }
vec2 sdBezier(in vec2 p, in vec2 v1, in vec2 v2, in vec2 v3) {
    vec2 c1 = p - v1;
    vec2 c2 = 2.0 * v2 - v3 - v1;
    vec2 c3 = v1 - v2;

    float t3 = dot(c2, c2);
    float t2 = dot(c3, c2) * 3.0 / t3;
    float t1 = (dot(c1, c2) + 2.0 * dot(c3, c3)) / t3;
    float t0 = dot(c1, c3) / t3;

    float t22 = t2 * t2;
    vec2 pq = vec2(t1 - t22 / 3.0, t22 * t2 / 13.5 - t2 * t1 / 3.0 + t0);
    float ppp = pq.x * pq.x * pq.x, qq = pq.y * pq.y;

    float p2 = abs(pq.x);
    float r1 = 1.5 / pq.x * pq.y;

    if (qq * 0.25 + ppp / 27.0 > 0.0) {
        float r2 = r1 * sqrt(3.0 / p2), root;
        if (pq.x < 0.0) root = sign(pq.y) * cosh(acosh(r2 * -sign(pq.y)) / 3.0);
        else root = sinh(asinh(r2) / 3.0);
        root = clamp(-2.0 * sqrt(p2 / 3.0) * root - t2 / 3.0, 0.0, 1.0);
        return vec2(length(p - mix(mix(v1, v2, root), mix(v2, v3, root), root)), root);
    }

    else {
        float ac = acos(r1 * sqrt(-3.0 / pq.x)) / 3.0;
        vec2 roots = clamp(2.0 * sqrt(-pq.x / 3.0) * cos(vec2(ac, ac - 4.18879020479)) - t2 / 3.0, 0.0, 1.0);
        float d1 = dot2(p - mix(mix(v1, v2, roots.x), mix(v2, v3, roots.x), roots.x));
        float d2 = dot2(p - mix(mix(v1, v2, roots.y), mix(v2, v3, roots.y), roots.y));
        return d1 < d2 ? vec2(sqrt(d1), roots.x) : vec2(sqrt(d2), roots.y);
    }
}

float sdBox(in vec2 p, in vec2 b) {
    p = abs(p) - b;
    return length(max(p, 0.0)) + min(0.0, max(p.x, p.y));
}

float sdLine(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length(pa - ba * clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0));
}

#define drawSDF(dist, col) color = mix(color, col, smoothstep(unit, 0.0, dist))
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 center = 0.5 * iResolution.xy;
    vec2 uv = (fragCoord - center) / iResolution.y * 2.0;
    vec2 mouse = (iMouse.xy - center) / iResolution.y * 2.0;
    float unit = 4.0 / iResolution.y;
    vec3 color = vec3(0.0);

    // Color bands in the background
    color = vec3(floor(snoise(vec2(uv.y * 4.0, 0.1 * iTime)) * 5.0) * 0.25);
    color *= step(0.0, 0.2 - snoise(vec2(uv.y * 10.0, iTime)));
    color *= fragCoord.y / iResolution.y;

    // Control points
    vec2 a = vec2(-1.0, -0.5);
    vec2 b = vec2(-0.5, 0.25);
    vec2 c = vec2(0.0, -0.5);

    float time = mod(iTime, 6.28);
    uv.x += time / 6.28 * 2.0 - 0.5;
    float transition = 0.5 - 0.5 * cos(min(time, 3.14));

    // Rotate back segment forward
    a -= c;
    a *= Rotate2D(3.14 * transition);
    a += c;

    b.x = mix(-0.5, 0.5, transition);

    // Repeat, but this time with the forward (now back) segment
    if (time > 3.14) {
        float transition = 0.5 + 0.5 * cos(time);

        c -= a;
        c *= Rotate2D(3.14 * transition);
        c += a;

        b.x = mix(0.5, 1.5, transition);
    }

    // Poles or whatever its walking on
    drawSDF(sdBox(vec2(fract(uv.x + 0.5) - 0.5, uv.y + 10.55), vec2(0.05, 9.95)) - 0.05, vec3(0.0, 0.8, 0.6));

    // Bezier curve
    vec2 bezClosest = sdBezier(uv, a, b, c);
    float bezLength = bezierArclength(0.0, 1.0, a, b, c);
    bezClosest.y = bezierArclength(0.0, bezClosest.y, a, b, c) / bezLength;
    drawSDF(bezClosest.x - 0.05, hue2rgb(bezClosest.y));

    // Plot rotating stars at regular intervals
    mat2 rot = Rotate2D(iTime);

    float du = bezLength / 5.0;
    for (float u=0.0; u < 6.0; u++) {
        float t = bezierInvArclength(u * du, a, b, c);
        vec2 pos = mix(mix(a, b, t), mix(b, c, t), t);

        mat2 starRot = rot;
        if (int(u) % 2 == 0) starRot = transpose(starRot); // Alternate rotation direction

        drawSDF(sdStar5((uv - pos) * starRot, 0.05, 0.5), vec3(0.25, 0.0, 0.5));
    }

    fragColor = vec4(color, 1.0);
}

// references: 
// https://gaz.hateblo.jp/entry/2019/05/11/215943
// https://gaz.hateblo.jp/entry/2019/05/11/225351

#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))
const float speed = 2.;

float deSphere( vec3 p, float s )
{
  return length(p)-s;
}


float de1(vec3 p)
{
    float h=sin(iTime)*.5+.5;
    p.x-=clamp(p.x,-h,h);
    p.zy *= rot(iTime*speed);
    return length(vec2(length(p.xy)- 1.,p.z))-.5;
}


float de2(vec3 p){

    vec3 bp = p - vec3(0,0,sin(iTime*speed)*5.);

    return deSphere(bp, .45);
}

float map(vec3 p)
{
    return min(de1(p), de2(p));
}

vec3 doColor(vec3 p)
{
    const float precis = 0.01;
    if (de1(p)<precis) return vec3(0,.3,.3);
    if (de2(p)<precis) return vec3(2,.4,.1);
    return vec3(1);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(fragCoord*2.0-iResolution.xy)/iResolution.y;
    vec3 ro=vec3(0,0,sin(iTime) * 3. -8.);
    ro.zx*=rot(iTime*0.5);
    vec3 rd=normalize(vec3(uv,2));

    //camera
    vec3 ta=vec3(0,0,0); 
    vec3 w=normalize(ta-ro);
    vec3 u=normalize(cross(w,vec3(0,1,0)));
    vec3 v=cross(u,w);
    mat3 lookat=mat3(u,v,w);
    rd=lookat*rd;
    //

    float d,i,t=0.0;
    vec3 p=ro;
    vec3 col=vec3(0);
    for(i=1.0;i>0.0;i-=1./50.0)
    {
        t+=d=map(p);
        if(d<0.001)
        {
            col = doColor(p);
            col /=i*i*i; 
            break;
        }
        p+=rd*d;
    }
    fragColor=vec4(col,1.0);
}

