#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001

#define S smoothstep
#define T iTime

mat2 Rot(float a) {
    float s=sin(a), c=cos(a);
    return mat2(c, -s, s, c);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}
float sdGyroid(vec3 p) {
    p *= -50.+ iTime;
    return dot(sin(p), cos(p.zxy));
}

float GetDist(vec3 p) {
    float box = sdBox(p, vec3(1));
    float gyroid = sdGyroid(p);
    return dot(box, gyroid);
    return gyroid;
    return box;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if(dO>MAX_DIST || abs(dS)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
	vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);

    vec3 rd = GetRayDir(uv, ro, vec3(0,0.,0),1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd);

    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = n * vec3(dif);
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

const float PI = 3.14;

mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}


mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}


float sphere(vec3 pos, vec3 spherePos, float sphereRadius) {
    return length(pos - spherePos) - sphereRadius;
}

float prism(vec3 p, vec2 h ) {
    vec3 q = abs(p);
    return max(q.z-h.y,max(q.x*0.7+p.y*0.3,-p.y)-h.x*0.5);
}


float smin ( float a, float b, float k )
{
	float res = exp ( -k*a ) + exp ( -k*b );
	return -log ( res ) / k;
}


float smax ( float a, float b, float k )
{
	float res = exp ( k*a ) + exp ( k*b );
	return log ( res ) / k;
}


float scene_eyes(vec3 pos, mat3 m) {
    vec3 q = m * pos;
    float k = min(
        sphere(q, vec3(0.45, -0.7, 0.2), 0.3),
        sphere(q, vec3(-0.45, -0.7, 0.2), 0.3)
    );
    return k;
}


float scene_head(vec3 pos, mat3 m) {
    vec3 q = m * pos;

    // prism transform
    vec3 q2 = q + vec3(0., 1.6, 1.);
    mat3 m2 = rotateY(PI / 2.) * rotateZ(PI / 180. * 30.);
    q2 *= m2;

    float k = max(
        -prism(q2, vec2(1., 1.5)),
        sphere(q, vec3(0., 0., 0.), 1.)
    );

    // ear radius
    float a = 2.;
    // ear thickness
    float d = 0.2;

    vec3 ear_position1 = vec3(-1.1, 0, 1.0);
    vec3 ear_position2 = vec3(1.1, 0, 1.0);

    float s1 =  smax(
        -sphere(q,ear_position1 +  vec3(0., -a, 0.), a),
        sphere(q, ear_position1, 1.), 10.0
    );

    float s2 = smax(
        sphere(q, ear_position1 + vec3(0., -a + d, 0.), a),
        s1, 10.0
    );

    float s3 =  smax(
        -sphere(q,ear_position2 +  vec3(0., -a, 0.), a),
        sphere(q, ear_position2, 1.), 10.0
    );

    float s4 = smax(
        sphere(q, ear_position2 + vec3(0., -a + d, 0.), a),
        s3, 10.0
    );


    return min(s4, min(s2, k));
}


float scene(vec3 pos, mat3 m, int n) {
    float dist;
    if (n == 0) {

        dist = scene_head ( pos, m );
    }
    else {
        dist = scene_eyes ( pos, m );
    } 
    return dist;
}


vec3 trace ( in vec3 from, in vec3 dir, out bool hit, mat3 m, int n)
{
	vec3	p         = from;
	float	totalDist = 0.0;

	hit = false;

    int stepNum = 70;
	for ( int steps = 0; steps < stepNum; steps++ )
	{
        float dist = scene(p, m, n);

        p += dist * dir;

		if ( dist < 0.01 )
		{
			hit = true;
			break;
		}

		totalDist += dist;

		if ( totalDist > 10.0 )
			break;	
	}

	return p;
}

vec3 generateNormal ( vec3 z, float d, mat3 m, int n)
{
    float eps = 0.01;
    float e   = max (d * 0.5, eps );
    float dx1 = scene(z + vec3(e, 0, 0), m, n);
    float dx2 = scene(z - vec3(e, 0, 0), m, n);
    float dy1 = scene(z + vec3(0, e, 0), m, n);
    float dy2 = scene(z - vec3(0, e, 0), m, n);
    float dz1 = scene(z + vec3(0, 0, e), m, n);
    float dz2 = scene(z - vec3(0, 0, e), m, n);

    return normalize ( vec3 ( dx1 - dx2, dy1 - dy2, dz1 - dz2 ) );
}



mat2 rotate(float angle)
{
    float c = cos(angle);
    float s = sin(angle);

    return mat2(c, -s, s, c);
}


vec4 background(vec2 uv) {
    //source: https://www.shadertoy.com/view/Xt2BDG

    vec4 result = vec4(0,0,0,1);

    float t = 1.;
    float offset = -5. * iTime;
    float base = 100. * length(uv);

    float d = sin(-iTime + 15. * length(uv));
    d *= d * d;

    mat2 rot = rotate(5. * length(uv));
    uv += .5;

    uv = abs(rot * uv);

    for (int p = 0; p < 3; p++)
    {
        result[p] = sin(offset + t * base) - cos(20. * uv.x) - cos(20. * uv.y);
        t += 0.05;
    }

    result.xyz *= result.xyz;
    result.xyz = 1. - result.xyz;

    return result * d;
}

vec3 lightPos = vec3(0, -6., 0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)

    mat3 m;

    vec3 mouse = vec3(iMouse.xy/iResolution.xy - 0.5,iMouse.z-.5);
    if ( iMouse.z > 0.) {
        m     = rotateX ( 6.0*mouse.y ) * rotateY ( 6.0*mouse.x );
    }
    else {
        m = mat3(
            vec3(1, 0, 0),
            vec3(0, 1, 0),
            vec3(0, 0, 1)
        );
    }

    vec3 cameraPos = vec3(0.,-5.-sin(iTime)*4., 0.);
    vec3 cameraForward = vec3(0., 1, 0.);
    vec3 cameraUp = vec3 (0., 0., 1.);
    float cameraFocus = 5.;

    vec2 scale = 9. * iResolution.xy / max ( iResolution.x, iResolution.y ) ;
    vec2 uv    = scale * ( fragCoord/iResolution.xy - vec2 ( 0.5 ) );


    vec3 from = vec3(uv.x, (cameraPos + cameraForward * cameraFocus).z, uv.y);
    vec3 dir = normalize(from - cameraPos);

    bool hit;
    vec3 p1 = trace(cameraPos, dir, hit, m, 0);
    float d1 = length(cameraPos - p1);
     float c1 = length(p1);

    vec3 backgroundColor = vec3(0., 0., 0.);
    vec3 surfaceColor = vec3(1., 1., 1.);

    vec4 col;

    if (hit) {

       vec3 l = normalize(lightPos - p1);
       vec3 n = generateNormal(p1, 0.001, m, 0);
       vec3 v = normalize(cameraPos - p1);
       float diffuse = max(0., dot(n, l));

       vec3  h  = normalize ( l + v );
       float hn = max ( 0.0, dot ( h, n ) );
       float specular = pow ( hn, 150.0 );

       col = 0.5*vec4 ( diffuse ) * vec4(1.0, 0.1, 0.4, 1) + 0.5 * specular * vec4 ( 1, 1, 1, 1 );
    }
    else {
       col = background(uv / 2.);
    }

    vec3 p2 = trace(cameraPos, dir, hit, m, 1);
    float d2 = length(cameraPos - p2);
    float c2 = length(p2);

    if (hit && (d2 < d1)) {

       vec3 l = normalize(lightPos - p2);
       vec3 n = generateNormal(p2, 0.001, m, 1);
       vec3 v = normalize(cameraPos - p2);
       float diffuse = max(0., dot(n, l));

       vec3  h  = normalize ( l + v );
       float hn = max ( 0.0, dot ( h, n ) );
       float specular = pow ( hn, 150.0 );

       float t = abs(sin(70. * c2 + 20. * iTime) - 0.9);

       col = 0.5 * vec4 ( diffuse ) * vec4(t, abs(t - 0.9), abs(t - 0.3), 1) + 0.5 * specular * vec4 ( 1, 1, 1, 1 );
    }


    // Output to screen
    fragColor = col;
}

