const float SceneDurationSeconds = 400.;
const float Complexity = 18.;

vec4 hash42(vec2 p)
{
	vec4 p4 = fract(vec4(p.xyxy) * vec4(.7031, 1500, .0953, .3099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

void mainImage(out vec4 o, vec2 C)
{
    vec2 uv = C/max(iResolution.x, iResolution.y);
    uv.x --;
    uv /= 4.;

    float scene = floor((iTime+98.)/SceneDurationSeconds);
    vec4 hscene = hash42(vec2(scene));
    uv += scene;

    float t = iTime*mix(.01,.06,hscene.y);
    uv -= t*.5;

    vec2 uvn = C/iResolution.xy-.5;
    float ds = max(abs(uvn.x), abs(uvn.y))*4.;

    for (float i = 1.; i < Complexity; ++ i) {
        vec2 c = floor(uv);
        vec4 h = hash42(c);
        vec2 p = fract(uv);
        vec2 a = abs(p-.5);
        float sd = .5-max(a.x, a.y);
        o = h*sqrt(sd)*4.;
        uv *= 2.;
        float sc = 1./i;
        uv += fract(abs(fract(uv.yx/sc+t)-.5)*sc-1.) * sign(hscene.x - .5);
    }

}

float scale = 1.; // try 2. or 3.

vec2 complex_log(vec2 z)
{
    return vec2(log(length(z)), atan(z.x, z.y));
}

vec2 complex_exp(vec2 z)
{
    return vec2(exp(z.x) * cos(z.y), exp(z.x) * sin(z.y));
}

vec2 position(float t)
{
    t *= 3.14;
    return vec2(cos(t), sin(t));
}

float potential(vec2 uv, float t)
{
    vec2 p = position(t);
    vec2 n = p; n.y = -n.y;
    float pot = -log(length(uv - p)) + log(length(uv - n));
    return 1. * pot;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;
    uv.x -= 0.5 * iResolution.x/iResolution.y;
    uv *= 3.14;
    float time = fract(0.04 * iTime);
    time = clamp(time, .0, .5) - 0.5 * time;
    time *= 8.;
    time -= .5;
    time = smoothstep(0., 1., time);
    uv *= scale;
    uv = (1. - time) * uv + time * complex_exp(uv);

    float t = 0.4 * sin(0.3 * iTime) + 0.5;
    vec2 pos = position(t);
    vec2 pos_neg = pos;
    pos_neg.y = - pos_neg.y;

    vec3 col = vec3(0.);

    col += vec3(1.) * max(0., cos((30. * atan(uv.x, uv.y))) * smoothstep(0.009, 0.003, abs(1. - length(uv))));
    col += vec3(1.) * smoothstep(0.02 * abs(uv.x), 0.01 * abs(uv.x), abs(uv.y));

    col.b += smoothstep(.04, .03, length(uv - pos));
    col.r += smoothstep(.04, .03, length(uv - pos_neg));

    float fw = fwidth(25./(sqrt(abs(potential(uv, t)))));
    float moire = abs(fw);
    moire = smoothstep(2.2, .2, moire);

    float pot = potential(uv, t);
    float bl = pot;
    bl = smoothstep(1. - moire, 1., cos(25./(sqrt(bl))));
    bl *= ( 1. - smoothstep(.1, .04, length(uv - pos)));

    float rd = -pot;
    rd = smoothstep(1. - moire, 1., cos(25./(sqrt(rd))));
    rd *= ( 1. - smoothstep(.1, .04, length(uv - pos_neg)));

    col.b += bl * moire;
    col.r += rd * moire;

    fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
    float tau = 3.1415926535*2.0;
    float a = atan(p.x,p.y);
    float r = length(p)*0.75;
    vec2 uv = vec2(a/tau,r);

	//get the color
	float xCol = (uv.x - (iTime / 3.0)) * 3.0;
	xCol = mod(xCol, 3.0);
	vec3 horColour = vec3(0.25, 0.25, 0.25);

	if (xCol < 1.0) {

		horColour.r += 1.0 - xCol;
		horColour.g += xCol;
	}
	else if (xCol < 2.0) {

		xCol -= 1.0;
		horColour.g += 1.0 - xCol;
		horColour.b += xCol;
	}
	else {

		xCol -= 2.0;
		horColour.b += 1.0 - xCol;
		horColour.r += xCol;
	}

	// draw color beam
	uv = (2.0 * uv) - 1.0;
	float beamWidth = (0.7+0.5*cos(uv.x*10.0*tau*0.15*clamp(floor(cos(iTime)), 0.0, 10.0))) * abs(1.0 / (30.0 * uv.y));
	vec3 horBeam = vec3(beamWidth);
	fragColor = vec4((( horBeam) * horColour), 1.0);
}

