}

float map(vec3 p) {
    return sdGrid(vec3(fract(p.x) - 0.5, p.y, fract(p.z) - 0.5)) - 0.02;
}

float march(vec3 ro, vec3 rd) {
    float d = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        float sd = map(ro + (rd * d));
        d += sd;
        if(d > MAX_DIST) return MAX_DIST;
        if(abs(sd) <= MIN_DIST) return d;
    }
    return MAX_DIST;
}

vec3 sky(vec3 rd) {
    vec3 col = vec3(0.0);

    float y = rd.y + 0.056;

    float k = smoothstep(0.4, 0.0, y);

    col += 0.2 * smoothstep(0.9, 1.0, texture(iChannel3, sin(rd.xy) + sin(rd.zy)).x) * smoothstep(1.0, 0.7, k);

    col += (mix(vec3(0.1, 0.2, 1.0), vec3(0.85, 0.2, 1.0), k) * k) * step(0.0, y);

    col += vec3(1.5) * smoothstep(0.01, 0.0, y);

    //col = mix(col, vec3(0.9, 0.8, 0.3) * 2.2, smoothstep(0.99, 0.993, rd.z) * (smoothstep(0.08, 0.1, rd.y) + (smoothstep(0.05, 0.065, rd.y) * smoothstep(0.08, 0.065, rd.y) * 0.8) + (smoothstep(0.05, 0.035, rd.y) * smoothstep(0.02, 0.035, rd.y) * 0.6) + (smoothstep(0.02, 0.005, rd.y) * smoothstep(0.005, 0.02, rd.y) * 0.4)));
    col = mix(col, vec3(0.9, 0.8, 0.3) * 2.2, smoothstep(0.99, 0.993, rd.z) * mix(1.0, ((sin(rd.y * 3.1415 * 70.0) * 0.5) + 0.5) * smoothstep(-0.08, 0.01, rd.y), step(rd.y, 0.006)));

    col *= 0.8;

    return col;
}

float marchplane(vec3 ro, vec3 rd) {
    float d = 0.0;
    for(int i = 0; i < MAX_STEPS; i++) {
        float sd = ro.y + (rd.y * d);
        d += sd;
        if(d > MAX_DIST) return MAX_DIST;
        if(abs(sd) <= MIN_DIST) return d;
    }
    return MAX_DIST;
}

void mainImage(out vec4 o, vec2 i) {
    vec2 uv = (i - (0.5 * iResolution.xy)) / min(iResolution.x, iResolution.y);

    vec3 ro = vec3(0.0, 3.0, iTime * 3.0);
    vec3 rd = normalize(vec3(uv, 1.7));

    vec3 col = vec3(0.0);

    float d = march(ro, rd);
    vec3 p = ro + (rd * d);

    if(d < MAX_DIST) {
        col = vec3(0.5, 0.2, 0.8) * 2.0;
    }

    // bloom
    float dplane = marchplane(ro, rd);
    vec3 pplane = ro + (rd * dplane);
    vec2 gridf = fract(pplane.xz - 0.5);
    col += vec3(0.5, 0.2, 0.8) * 0.8 * (smoothstep(0.2, 0.0, gridf.x) + smoothstep(0.8, 1.0, gridf.x) + smoothstep(0.4 + log(p.z - ro.z) * 0.05, 0.0, gridf.y) + smoothstep(0.6 - log(p.z - ro.z) * 0.05, 1.0, gridf.y));

    col = mix(col, sky(rd), dplane / MAX_DIST);

    o = vec4(col, 1.0);
}

float myLength(vec2 uv, float t) {
    return abs(uv.x * (1. + 0.5 * cos(t))) + abs(uv.y * (1. + 0.5 * sin(t)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = 0.3 * iTime;
    float s = 0.5 * (1. + cos(t));
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (12. + 2. * cos(0.45 * t)) *(fragCoord - 0.5 * iResolution.xy)/iResolution.y;
     float theta = atan(uv.y, uv.x);
    uv = vec2(cos(uv.x + theta + 0.9 * t), sin(uv.y + theta + 0.9 * t));
    //float theta =  atan(uv.y, uv.x);
    // Time varying pixel color
    float d = smoothstep(myLength(uv + 0.1 * vec2(cos(t),sin(t)), 4. * t),
                         0.2 * sin(1. * theta + t) * cos(1. * theta +  t), 
                         0.1);
    float r = step(16. * d * (1.-d) * d * (1.-d), 0.1);
    r *= 10. * d * (1.-d);

    d += 0.005 * cos(theta + t);

    float g = step(16. * d * (1.-d) * d * (1.-d), 0.1);
    g *= 10. * d * (1.-d);

    d -= 0.01 * cos(theta + t);

    float b = step(16. * d * (1.-d) * d * (1.-d), 0.1);
    b *= 10. * d * (1.-d);


    // Output to screen
    fragColor = vec4(g,r,b, 1.);
}

// All components are in the range [0…1], including hue.
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// https://en.wikipedia.org/wiki/Triangle_wave
float triangleWave(float t, float period) {
    return 2. * abs(t/period - floor(t/period + .5));
}

float edgeEffect(float x, float y, float scale, float iTime) {

    // Make these shader uniforms and use game logic to manipulate the effect
    float period = .07;
    float amplitude = .07;
    float border = mix(.0, .2, sin(iTime)*.5+.5);
    float wave = mix(.0, .06, sin(x*scale*15.+iTime*10.)*.5+.5);
    float shift = -iTime*.3;

    float ty = triangleWave(scale*(x+shift), period);

    return step(1.-abs(y), scale*(ty*amplitude+border+wave));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2. - 1.;
    float aspectRatio = iResolution.y/iResolution.x;

    vec3 col = vec3(0);
    vec3 rainbow = hsv2rgb(vec3(iTime*.1, .9, .9));

    // top/bottom
    col = mix(col, rainbow, edgeEffect(uv.x, uv.y, 1., iTime));
    // left/right
    col = mix(col, rainbow, edgeEffect(uv.y, uv.x, aspectRatio, iTime));

    // Gamma correction
    col = pow(max(col,0.), vec3(1./2.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

