    float R = float(fract(u) + fract(v) > 1.0);
    return vec3(u, v, R);
}


const vec4 black = vec4(0.0, 0.0, 0.0, 1.0);

uvec2 pcg(uvec2 v) {
    v = v * 1664525u + 1013904223u;

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v >> 16u);

    v.x += v.y * 1664525u;
    v.y += v.x * 1664525u;

    v = v ^ (v >> 16u);

    return v;
}

// pcg3d renamed for overload
// http://www.jcgt.org/published/0009/03/02/
uvec3 pcg(uvec3 v) {

    v = v * 1664525u + 1013904223u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    v ^= v >> 16u;

    v.x += v.y*v.z;
    v.y += v.z*v.x;
    v.z += v.x*v.y;

    return v;
}
vec3 pcgf(out uvec3 v) {
    v = pcg(v);
    return vec3(v) / float(0xffffffffu);
}

