    color.xyz = hsv2rgb_smooth_2315452051(vec3(rndf.x, s, 1.));
    //        color.xyz = vec3(rndf.x);
    color = linRgbColor(color);
    //        return rnd.x>>31 == 0u ? black:white;
    return color;
}

